import{_ as l,c as _,o as i,a as t}from"./app.618cde25.js";const m='{"title":"","description":"","frontmatter":{},"headers":[],"relativePath":"galatians/2.\u4FE1\u8036\u7A23\u662F\u5FC3\u7075\u7684\u5370\u8BB0.md"}',p={},e=t("<p>\u4FE1\u8036\u7A23\u662F\u5FC3\u7075\u7684\u70D9\u5370\uFF0C\u4E0D\u662F\u4E00\u4E2A\u51B3\u5B9A\u300A\u52A01:1-17\u300B \u5E8F\u8BBA\uFF1A\u4FE1\u5F92\u5E38\u5904\u5728\u4FE1\u5FC3\u8F6F\u5F31\u65E0\u80FD\u5F53\u4E2D\uFF0C\u751A\u81F3\u5BB9\u6613\u53D7\u5230\u7591\u60D1\u4E0E\u5C5E\u4E16\u7684\u7075\u4FB5\u5165\uFF0C\u4E3B\u8981\u6709\u4E24\u4E2A\u7406\u7531\u3002\u7B2C\u4E00\uFF1A\u56E0\u4E3A\u603B\u662F\u5C06\u57FA\u7763\u4FE1\u4EF0\u770B\u4E3A\u662F\u4E00\u79CD\u9009\u62E9\u3002\u59CB\u7EC8\u8BA4\u4E3A\u662F\u81EA\u5DF1\u826F\u597D\u7684\u51B3\u5B9A\u5E26\u6765\u4E0A\u5E1D\u7684\u63A5\u7EB3\u4E0E\u795D\u798F\u3002\u8FD9\u79CD\u4FE1\u5F92\u603B\u8BA4\u4E3A\u4FE1\u5FC3\uFF08\u4F60\u80FD\u4FE1\uFF09\u662F\u4E00\u79CD\u81EA\u5DF1\u7684\u51B3\u5B9A\u3002\u7B2C\u4E8C\uFF0C\u8BB8\u591A\u7684\u4FE1\u5F92\u603B\u8BA4\u4E3A\u4FE1\u4E86\u8036\u7A23\u4E4B\u540E\uFF0C\u63A5\u4E0B\u6765\u7684\u8868\u73B0\u3001\u884C\u5584\u3001\u4E3A\u4EBA\u90FD\u662F\u5728\u4E8E\u81EA\u5DF1\uFF0C\u800C\u4E0D\u662F\u4EE5\u542C\u4ECE\u5FC3\u7075\u7684\u4E3B\u4E3A\u51FA\u53D1\u3002\u8FD9\u4E24\u8005\u90FD\u662F\u6781\u5927\u7684\u8BEF\u89E3\uFF0C\u5BFC\u81F4\u4E00\u4E2A\u4EBA\u59CB\u7EC8\u65E0\u6CD5\u6B63\u786E\u7684\u9047\u89C1\u795E\uFF0C\u597D\u4E0E\u7942\u540C\u884C\u3002\u5F80\u5F80\uFF0C\u6211\u4EEC\u5E38\u56E0\u81EA\u5DF1\u6CA1\u6709\u4FE1\u5FC3\u611F\u5230\u81EA\u8D23\u65E0\u5948\uFF0C\u5374\u6CA1\u6709\u8BA4\u771F\u7684\u601D\u60F3\u8FC7\uFF0C\u5230\u5E95\u6211\u4EEC\u5BF9\u4E0A\u5E1D\u7684\u4FE1\u5FC3\u662F\u4ECE\u54EA\u513F\u6765\uFF1F\u4E00\u4E2A\u4EBA\u80FD\u51E1\u4E8B\u4FE1\u9760\u795E\u662F\u56E0\u4E3A\u4ED6\u8BA4\u8BC6\u795E\u4E86\uFF01\u4ED6\u603B\u80FD\u4FE1\u4EFB\u795E\u662F\u56E0\u4E3A\u4E0E\u795E\u6709\u5173\u7CFB\u4E86\uFF01\u8FD9\u662F\u8BB8\u591A\u4FE1\u5F92\u4ECE\u672A\u6E05\u695A\u8BA4\u8BC6\u7684\u3002\u5728\u63A2\u8BA8\u6574\u672C\u52A0\u62C9\u592A\u4E66\u4E4B\u524D\uFF0C\u6211\u4EEC\u5148\u8981\u660E\u767D\u201C\u4F55\u8C13\u4FE1\u8036\u7A23\uFF1F\u201D\u90A3\u5C31\u662F\u4E0A\u5E1D\u4F7F\u6211\u8BA4\u8BC6\u4E86\u7942\u5728\u57FA\u7763\u91CC\u6C38\u8FDC\u65E0\u6761\u4EF6\u7684\u7231\uFF0C\u4EE5\u81F4\u6211\u7684\u7075\u9B42\u91CD\u751F\u4E86\uFF01\u300A\u7EA61\uFF1A13\u300B\u8BF4\uFF1A\u8FD9\u7B49\u4EBA\u662F\u795E\u6240\u751F\u7684\uFF0C\u8DDF\u4ED6\u7684\u51B3\u5B9A\u4E0E\u610F\u5FD7\u65E0\u5173\u3002\u6240\u4EE5\uFF0C\u4E0A\u5E1D\u5207\u5207\u7684\u8981\u6211\u4EEC\u660E\u767D\u7942\u4ECA\u65E5\u7EDD\u5BF9\u7231\u6211\u3001\u8D50\u798F\u6211\u3001\u8F6F\u5F31\u5F53\u4E2D\u4E5F\u7EDD\u4E0D\u79BB\u5F03\u6211\uFF0C\u90A3\u5E76\u4E0D\u662F\u6765\u4E4B\u6211\u7684\u6B63\u786E\u51B3\u5B9A\uFF08\u9009\u62E9\uFF09\uFF0C\u4E43\u662F\u56E0\u6211\u662F\u7942\u501F\u5723\u7075\u6240\u751F\u7684\u3002\u6240\u4EE5\uFF0C\u5BF9\u201C\u795E\u751F\u4E86\u6211\u201D\u6709\u786E\u636E\u7684\u4EBA\uFF0C\u5728\u4FE1\u4EF0\u7684\u9053\u8DEF\u91CC\u4E0D\u662F\u8FFD\u6C42\u4EBA\u7684\u5B8C\u7F8E\uFF0C\u4E43\u662F\u8FFD\u6C42\u8981\u542C\u4ECE\u5FC3\u7075\u7684\u4E3B\u6765\u6D3B\u3002\u56E0\u4E3A\u7236\u5B50\u5173\u7CFB\u7684\u6EE1\u8DB3\u662F\u6765\u4E4B\u5B50\u542C\u4ECE\u7236\uFF0C\u4E0D\u662F\u6765\u4E4B\u5B50\u5F53\u9075\u7167\u67D0\u4E9B\u5C0A\u5219\u3001\u6761\u89C4\u3001\u6C34\u51C6\u6765\u6D3B\u3002\u5176\u5B9E\uFF0C\u6211\u4EEC\u76EE\u524D\u6240\u5904\u5728\u7684\u91CD\u91CD\u95EE\u9898\u5F53\u4E2D\uFF0C\u53EA\u8981\u4E00\u65E6\u8F6C\u5411\u8981\u542C\u4ECE\u6211\u4EEC\u5FC3\u4E2D\u7684\u4E3B\uFF0C\u5728\u6211\u4EEC\u5FC3\u7075\u7684\u6DF1\u5904\uFF0C\u4E00\u5207\u7684\u95EE\u9898\u4FBF\u6D88\u6563\u3002\u6240\u4EE5\u7B80\u5355\u8BF4\uFF0C\u4ECA\u65E5\u4FE1\u5F92\u7684\u5F0A\u75C5\u662F\u56E0\u4E3A\u5BF9\u2018\u4FE1\u8036\u7A23\u2019\u8BEF\u8BA4\u4E3A\u201C\u6211\u51B3\u5B9A\u76F8\u4FE1\uFF0C\u624D\u6709\u795D\u798F\u201D\uFF0C\u4E0D\u662F\u201C\u6211\u5FC3\u7075\u5DF2\u7ECF\u6709\u795E\u513F\u5973\u7684\u70D9\u5370\u4E86\uFF0C\u6240\u4EE5\u975E\u8499\u798F\u4E0D\u53EF\u201D\uFF1B\u6240\u4EE5\u6709\u4E00\u79CD\u4FE1\u4EF0\u662F\u603B\u5904\u5728\u201C\u6211\u8981\u505A\uFF0C\u6211\u8981\u6709\u597D\u884C\u4E3A\uFF0C\u8981\u699C\u6837\u201D\uFF08\u4EBA\u4E3A\u51FA\u53D1\uFF09\uFF0C\u53E6\u4E00\u79CD\u4E14\u662F\u201C\u4E3B\uFF0C\u4F60\u8981\u6211\u600E\u4E48\u505A\uFF0C\u600E\u4E48\u8363\u8000\u4F60\uFF1F\u201D\uFF08\u795E\u4E3A\u51FA\u53D1\uFF09\u3002\u8FD9\u4E5F\u5C31\u662F\u6211\u4EEC\u4ECA\u5929\u501F\u52A0\u62C9\u592A\u4E66\u8981\u660E\u767D\u7684\u4E3B\u8981\u771F\u7406\u3002\u4FE1\u8036\u7A23\uFF0C\u539F\u6765\u662F\u70D9\u5370\uFF0C\u4E0D\u662F\u51B3\u5B9A\u3002</p><p>\u4FE1\u8036\u7A23\u662F\u5FC3\u7075\u7684\u70D9\u5370\uFF0C\u4E0D\u662F\u4E00\u4E2A\u51B3\u5B9A\u300A1\uFF1A1-9\u300B \u5F53\u4E00\u4E2A\u4EBA\u542C\u4FE1\u4E86\u57FA\u7763\u798F\u97F3\u65F6\uFF0C\u90A3\u5982\u540C\u5FC3\u7075\u7684\u70D9\u5370\uFF0C\u65E0\u6CD5\u518D\u62B9\u6740\u6389</p><ul><li>\u300A\u5F171\uFF1A13\u300B\u4F60\u4EEC\u5373\u542C\u89C1\u771F\u7406\u7684\u9053...\u4E5F\u4FE1\u4E86\u57FA\u7763...\u5C31\u53D7\u4E86\u6240\u5E94\u8BB8\u7684\u5723\u7075\u4E3A\u5370\u8BB0\u3002\u5F53\u4E00\u4E2A\u4EBA\u542C\u4E86\u57FA\u7763\u7684\u798F\u97F3\uFF0C\u5723\u7075\u611F\u52A8\u4ED6\u4F7F\u4ED6\u771F\u4FE1\u800C\u8BA4\u8BC6\u81EA\u5DF1\u662F\u795E\u7684\u513F\u5973\uFF0C\u8FD9\u8EAB\u4EFD\u523B\u5728\u4ED6\u5FC3\u7075\u91CC\uFF0C\u518D\u65E0\u6CD5\u62B9\u6740\u3001\u5426\u8BA4\u3001\u6216\u6539\u53D8\u4E3B\u610F\u3002\u7531\u6B64\u53EF\u89C1\uFF0C\u4FE1\u8036\u7A23\u4E0D\u662F\u4E00\u4E2A\u51B3\u5B9A\u3002\u82E5\u662F\u51B3\u5B9A\uFF0C\u8FD8\u80FD\u5728\u6539\u53D8\u4E3B\u610F\u3002\u4F46\u82E5\u662F\u70D9\u5370\uFF0C\u90A3\u5C31\u662F\u5FC3\u4E2D\u5DF2\u7ECF\u660E\u767D\u7684\u4E8B\u5B9E\uFF0C\u6240\u4EE5\u4E4B\u540E\u6211\u4EEC\u5728\u51E1\u4E8B\u4E0A\u5BF9\u795E\u7684\u4FE1\u5FC3\u662F\u5EFA\u7ACB\u5728\u8FD9\u4E8B\u5B9E\u3002\uFF08\u5982\u540C\u4F60\u4FE1\u4F60\u662F\u4F60\u7236\u4EB2\u7684\u5B69\u5B50\uFF0C\u8FD9\u4E0D\u662F\u4F60\u51B3\u5B9A\u4FE1\u7684\uFF0C\u4E43\u662F\u5DF2\u7ECF\u660E\u767D\u7684\u4E8B\u5B9E\u4E86\u3002\u4ECE\u6B64\u4EE5\u540E\uFF0C\u4F60\u5BF9\u4F60\u7236\u4EB2\u7684\u4FE1\u4EFB\u4E0E\u4FE1\u9760\u662F\u5EFA\u7ACB\u5728\u8FD9\u5173\u7CFB\u4E0A\uFF09\u3002</li></ul><p>\u4ECE\u4E00\u4E2A\u4EBA\u542C\u4FE1\u4E86\u57FA\u7763\u798F\u97F3\uFF0C\u5F97\u4E86\u5FC3\u7075\u7684\u5370\u8BB0\u5F00\u59CB\uFF0C\u4ED6\u7684\u5FC3\u4E2D\u81EA\u7136\u4EA7\u751F\u4E00\u4E9B\u4E0E\u4E16\u4EBA\u4E0D\u540C\uFF08\u5206\u522B\uFF09\u7684\u611F\u52A8</p><ul><li>\u5F53\u4E00\u4E2A\u4EBA\u542C\u4FE1\u4E86\u57FA\u7763\u798F\u97F3\u800C\u5F97\u7740\u70D9\u5370\uFF0C\u5FC5\u6709\u5FC3\u7075\u7684\u8BC1\u636E\u3002\u4ECE\u90A3\u65F6\u8D77\uFF0C\u4ED6\u4F1A\u8BA4\u5F97\u4E3B\uFF08\u58F0\u97F3\uFF09\uFF0C\u4E3B\u4E5F\u8BA4\u5F97\u4ED6\uFF01\uFF08\u5982\u540C\u7236\u8BA4\u5F97\u5B69\u5B50\uFF0C\u5B69\u5B50\u4E5F\u8BA4\u5F97\u7236\uFF09\u300A\u7EA610\uFF1A14\u300B\u3002\u518D\u6765\u5C31\u662F\u4ED6\u91CC\u5934\u7684\u5723\u7075\u4E5F\u4F7F\u4ED6\u8BA4\u5F97\u4FE1\u4E3B\u4E4B\u4EBA\uFF0C\u4ED6\u7684\u5FC3\u7075\u4E5F\u81EA\u7136\u559C\u60A6\u4ED6\u4EEC\uFF0C\u4E5F\u8FEB\u5207\u4E3A\u81EA\u5DF1\u6240\u7231\u7684\u4EBA\u7684\u6551\u6069\u7977\u544A\uFF0C\u7B49\u3002\u5F53\u7136\uFF0C\u8FD9\u4EBA\u4E5F\u4F1A\u6709\u8F6F\u5F31\u7684\u65F6\u5019\uFF0C\u6240\u4EE5\u5BFC\u81F4\u8FD9\u4E9B\u611F\u52A8\u4E0D\u4F1A\u8FD9\u4E48\u5F3A\u70C8\uFF0C\u4F46\u4ECE\u5FC3\u7075\u7684\u70D9\u5370\u6240\u6765\u7684\u611F\u52A8\u603B\u662F\u65E0\u6CD5\u62B9\u6740\u3002* \u7136\u540E\uFF0C\u5FC3\u7075\u6709\u70D9\u5370\u7684\u4FE1\u5F92\u8D8A\u662F\u542C\u89C1\u4E0A\u5E1D\u8BDD\u8BED\u7684\u6559\u5BFC\uFF0C\u4ED6\u8D8A\u662F\u4E0E\u4E16\u754C\u5206\u522B\uFF0C\u4E0D\u4F1A\u7559\u5728\u4E0E\u4E16\u4EBA\u4E00\u6837\u7684\u4EF7\u503C\u89C2\uFF0C\u4EBA\u751F\u89C2\uFF0C\u4E16\u754C\u89C2...</li></ul><p>\u5728\u4FE1\u4EF0\u751F\u6D3B\u5F53\u4E2D\uFF0C\u6211\u4EEC\u5076\u5C14\u88AB\u8FC7\u72AF\u6240\u80DC\uFF0C\u4F46\u5FC3\u7075\u6709\u70D9\u5370\u4E4B\u4EBA\u65E0\u6CD5\u5F03\u7EDD\u4E3B\uFF0C\u4E5F\u5F53\u7136\u4E0D\u80FD\u88AB\u4E3B\u5F03\u7EDD</p><ul><li>\u8BB8\u591A\u4FE1\u5F92\u4E00\u65E6\u8F6F\u5F31\u6216\u5904\u5728\u6F2B\u957F\u7684\u8BD5\u70BC\u91CC\uFF0C\u5C31\u4F1A\u9677\u5165\u4E00\u79CD\u65E0\u5948\u51B7\u6DE1\u7684\u72B6\u6001\u3002\u4ED6\u4EEC\u7684\u5FC3\u6001\u662F\u4E00\u76F4\u7B49\u5019\u95EE\u9898\u89E3\u51B3\u6216\u662F\u795E\u6765\u6311\u52A8\u4ED6\u4EEC\u3002\u4F46\u5FC3\u91CC\u4E5F\u603B\u8BA4\u4E3A\u662F\u81EA\u5DF1\u4E0D\u613F\u610F\u6765\u5230\u795E\u9762\u524D\uFF0C\u6240\u4EE5\u795E\u4E5F\u4E0D\u80FD\u5E2E\u4ED6\u4EEC\u3002\u5176\u5B9E\uFF0C\u4E0A\u5E1D\u4ECE\u672A\u79BB\u5F00\u4ED6\u4EEC\u3002\u4F46\u7531\u4E8E\u4ED6\u4EEC\u5FC3\u91CC\u7684\u70D9\u5370\u4E0D\u6E05\u695A\uFF0C\u4ED6\u4EEC\u65E0\u6CD5\u4ECE\u795E\u7684\u8BDD\uFF08\u5E94\u8BB8\uFF09\u5F97\u6311\u52A8\u3002\u53EF\u662F\uFF0C\u5FC3\u7075\u7684\u70D9\u5370\u660E\u786E\u7684\u4FE1\u5F92\u5BF9\u751F\u4ED6\u7684\u4E0A\u5E1D\u603B\u4F1A\u6709\u4E00\u79CD\u201C\u6211\u5F80\u90A3\u91CC\u53BB\u8EB2\u907F\u4F60\u7684\u7075\u3002\u6211\u5F80\u90A3\u91CC\u9003\u8EB2\u907F\u4F60\u7684\u9762\u201D\u7684\u5FC3\u6001\u3002\uFF08\u5982\u540C\u513F\u5973\u975E\u542C\u5230\u7236\u7684\u80AF\u5B9A\u4E0E\u8BA4\u540C\u624D\u80FD\u5F97\u4EE5\u559C\u4E50\uFF0C\u6709\u795E\u513F\u5973\u70D9\u5370\u4E4B\u4EBA\u4E5F\u5FC5\u5F97\u542C\u4E3B\u7684\u58F0\u97F3\u624D\u80FD\u5F97\u4EE5\u81EA\u7531\uFF09\u3002</li></ul><p>\u5F97\u8499\u795E\u513F\u5973\u7684\u70D9\u5370\u4E4B\u4EBA\uFF0C\u613F\u610F\u5F97\u7684\u662F\u795E\u7684\u5FC3\u300A1\uFF1A1\uFF0C4\uFF0C10\uFF0C17\u300B</p><ul><li>\u5FC3\u7075\u6709\u70D9\u5370\u4E4B\u4EBA\uFF0C\u5FC5\u8981\u5F97\u542C\u89C1\u795E\uFF0C\u624D\u80FD\u5F97\u4EAB\u5B89\u606F\u3002\u56E0\u6B64\uFF0C\u4ED6\u8FFD\u6C42\u7684\u662F\u5F8B\u6CD5\u4EE5\u5916\u7684\u4E49\uFF0C\u5C31\u662F\u201C\u51E1\u4E8B\u7684\u542C\u4ECE\u201D\uFF0C\u4E0D\u662F\u201C\u6B7B\u884C\u201D\u3002 \u5F53\u8981\u4ECE\u300C\u786E\u4FE1\u81EA\u5DF1\u5DF2\u7ECF\u662F\u8499\u6069\u7684\u300D\u5F00\u59CB\uFF0C\u4E0D\u662F\u8981\u591A\u5F97\u6069</li><li>\u5F97\u795E\u7684\u5FC3\u7684\u9996\u8981\u662F\uFF1A\u786E\u4FE1\u90A3\u5DF2\u7ECF\u4E34\u5230\u81EA\u5DF1\u8EAB\u4E0A\u7684\u6069\u60E0\uFF0C\u4E0D\u662F\u8981\u591A\u5F97\u6069\u60E0\u3002\u8BB8\u591A\u4FE1\u5F92\u5728\u5FC3\u7075\u91CC\u65E0\u6CD5\u7ECF\u5386\u798F\u97F3\u7684\u5927\u80FD\uFF0C\u539F\u56E0\u662F\u56E0\u4E3A\u5728\u5FC3\u7075\u91CC\u8FD8\u672A\u5F97\u7740\u798F\u97F3\u6240\u8D50\u7684\u786E\u636E\u3002\u5F53\u4EBA\u7684\u5FC3\u7075\u786E\u4FE1\u5728\u57FA\u7763\u91CC\u300C\u5DF2\u7ECF\u5F97\u4E86\u4E00\u5207\u300DVs\u300C\u81EA\u5DF1\u8FD8\u662F\u60F3\u591A\u5F97\u300D\uFF0C\u8FD9\u662F\u5E26\u6765\u7EDD\u7136\u4E0D\u540C\u7684\u5723\u7075\u5DE5\u4F5C\u3002\u65E0\u8BBA\u6211\u4EEC\u770B\u4EC0\u4E48\uFF08\u4F8B\u5982\uFF1A\u770B\u81EA\u5DF1\uFF0C\u770B\u5BB6\u4EBA\uFF0C\u770B\u81EA\u5DF1\u7684\u524D\u9014\uFF0C\u770B\u81EA\u5DF1\u6240\u505A\u7684\u4E8B\u4E1A\uFF09\uFF0C\u90FD\u8981\u5E26\u7740\u4FE1\u4E3B\u7684\u8D50\u798F\u7684\u5FC3\uFF0C\u4E0D\u662F\u591A\u770B\u5C5E\u8089\u7684\u73B0\u8C61\u4E0E\u6761\u4EF6\uFF0C\u8FD9\u6837\u5C31\u662F\u5F97\u8499\u4E3B\u7684\u5FC3\u7684\u5F00\u59CB\u4E86\u3002</li></ul><p>\u5728\u51E1\u4E8B\u4E0A\u8BA4\u5B9A\u795E\uFF0C\u5F97\u7740\u4E00\u4E2A\u300C\u5355\u4E00\u542C\u4ECE\u795E\u7684\u5FC3\u300D</p><ul><li>\u4FDD\u7F57\u8BF4\uFF1A\u201C\u6211\u662F\u8981\u5F97\u4EBA\u7684\u5FC3\u5462\uFF1F\u8FD8\u662F\u8981\u5F97\u795E\u7684\u5FC3\u5462\uFF1F\u201D\u5176\u5B9E\uFF0C\u5F53\u4E00\u4E2A\u4FE1\u5F92\u7684\u5FC3\u7075\u6CA1\u6709\u70D9\u5370\u65F6\uFF0C\u4ED6\u603B\u4F1A\u56E0\u4EBA\u7684\u8BDD\u8BED\u3001\u56E0\u7D20\u3001\u6807\u51C6\u6765\u53D7\u5230\u5F71\u54CD\uFF0C\u4EE5\u81F4\u65E0\u6CD5\u5F97\u542C\u4E0A\u5E1D\u7EAF\u6B63\u7684\u58F0\u97F3\u3002\u5FC3\u7075\u6709\u70D9\u5370\u7684\u4FE1\u5F92\u5728\u5FC3\u91CC\u975E\u5E38\u6E05\u695A\uFF0C\u6BCF\u5F53\u4ED6\u4EEC\u5355\u5355\u613F\u610F\u542C\u4ECE\u795E\u65F6\uFF0C\u5723\u7075\u6240\u8D50\u7684\u80AF\u5B9A\u4E0E\u5E73\u5B89\u4F1A\u4E34\u5230\u4ED6\u4EEC\uFF0C\u4F7F\u4ED6\u4EEC\u521A\u5F3A\u58EE\u80C6\uFF0C\u800C\u4ED6\u4EEC\u6240\u9762\u5BF9\u7684\u8BB8\u591A\u56F0\u6270\u4E0E\u95EE\u9898\u4E5F\u4F1A\u5728\u90A3\u65F6\u5019\u88AB\u89E3\u51B3\u3002\u4FDD\u7F57\u80FD\u5BF9\u52A0\u62C9\u592A\u4EBA\u5982\u6B64\u5766\u8BDA\u4E0E\u76F4\u723D\u662F\u56E0\u4E3A\u4ED6\u5FC3\u4E2D\u662F\u5355\u5355\u542C\u4ECE\u795E\uFF0C\u4E0D\u662F\u8981\u5F97\u4EFB\u4F55\u4EBA\u6216\u7FA4\u4F53\u7684\u559C\u60A6\u3002</li></ul><p>\u7EE7\u7EED\u5C06\u81EA\u5DF1\u8001\u65E7\u7684\u4FE1\u5FF5\u62C6\u6BC1\uFF0C\u518D\u6B21\u900F\u8FC7\u795E\u7684\u8BDD\u8BED\u5EFA\u7ACB\u300C\u542C\u4ECE\u795E\u7684\u751F\u547D\u7CFB\u7EDF\u300D\u300A\u52A01\uFF1A17\u300B</p><ul><li>\u8BB8\u591A\u4FE1\u5F92\u8BF4\u4ED6\u8981\u542C\u4ECE\u795E\uFF0C\u5374\u6CA1\u6709\u4E00\u4E2A\u542C\u4ECE\u795E\u7684\u751F\u547D\u7CFB\u7EDF\u3002\u90A3\u4E48\uFF0C\u4ED6\u7684\u542C\u4ECE\u795E\u4FBF\u662F\u65E0\u6839\u65E0\u57FA\u7684\u3002\u56E0\u4E3A\u4EBA\u751F\u6765\u5C31\u5904\u5728\u5815\u843D\u7684\u7075\uFF0C\u6EE1\u6709\u81EA\u5DF1\u4ECE\u4E16\u754C\u7684\u6765\u7684\u795E\u89C2\uFF0C\u81EA\u6211\u89C2\uFF0C\u5386\u53F2\u89C2\uFF0C\u4E16\u754C\u89C2\uFF0C\u4EF7\u503C\u89C2\uFF0C\u4EBA\u751F\u89C2\u3002\u6240\u4EE5\uFF0C\u5F53\u4E00\u4E2A\u4EBA\u8BF4\uFF1A\u201C\u6211\u662F\u5355\u5355\u8981\u542C\u4ECE\u795E\u201D\uFF0C\u4ED6\u7B2C\u4E00\u6240\u505A\u7684\u662F\u6094\u6539\u8F6C\u5411\u795E\uFF08\u57FA\u7763\uFF09\uFF0C\u4E4B\u540E\u662F\u4EE5\u795E\u7684\u8BDD\u6765\u53D6\u4EE3\u81EA\u5DF1\u8001\u65E7\u7684\u4FE1\u5FF5\uFF0C\u5EFA\u7ACB\u5728\u57FA\u7763\u91CC\u9762\u7684\u201C\u51E1\u4E8B\u89C2\u201D\u3002\u8FD9\u5C31\u662F\u4FDD\u7F57\u8499\u53EC\u4E4B\u540E\u7ACB\u65F6\u5F80\u963F\u62C9\u4F2F\u53BB\u6240\u505A\u7684\u3002\uFF08\u867D\u7136\u7ECF\u6587\u6CA1\u8BF4\u4FDD\u7F57\u5F80\u963F\u62C9\u4F2F\u53BB\u7684\u7406\u7531\u4F55\u5728\uFF0C\u4F46\u5F88\u6709\u53EF\u80FD\u662F\u4EE5\u9053\u6210\u8089\u8EAB\uFF0C\u6B7B\u800C\u590D\u6D3B\uFF0C\u638C\u6743\u5B9D\u5EA7\u7684\u8036\u7A23\u57FA\u7763\uFF0C\u91CD\u8BFB\u6574\u672C\u65E7\u7EA6\u5723\u7ECF\uFF0C\u5EFA\u7ACB\u6B63\u786E\u7684\u751F\u547D\u7CFB\u7EDF\uFF0C\u4E5F\u540C\u65F6\u9886\u53D7\u4ECE\u795E\u800C\u6765\u6709\u5173\u57FA\u7763\u7684\u542F\u793A\uFF09\u3002\u8FD9\u5C31\u662F\u62E5\u6709\u6B63\u76F4\u6E05\u6D01\u5FC3\u7075\u7684\u4EBA\u6240\u505A\u7684\u4E86\uFF01</li></ul><p>\u798F\u97F3\u4F7F\u6211\u660E\u767D\u4E00\u5207\u90FD\u662F\u51FA\u4E8E\u795E\uFF0C\u6240\u4EE5\u4ECA\u65E5\u5F53\u8BA4\u8BC6\u7942\u7684\u4F5C\u4E3A\u800C\u8DDF\u4ECE\u300A1\uFF1A11-17\u300B \u300C\u798F\u97F3\u300D\u662F\u5168\u7136\u4ECE\u795E\u800C\u51FA\uFF0C\u5E76\u6CA1\u6709\u52A0\u4E0A\u4EBA\u7684\u4EEA\u6587</p><ul><li>\u4FDD\u7F57\u8981\u52A0\u62C9\u592A\u4EBA\u660E\u767D\u798F\u97F3\u7684\u7EDD\u5BF9\u7EAF\u6B63\u5EA6\u3002\u867D\u4ED6\u4E0E\u4F7F\u5F92\u4EEC\u90FD\u662F\u72B9\u592A\u4EBA\uFF0C\u4F46\u4ECE\u795E\u800C\u6765\u7684\u798F\u97F3\u5E76\u6CA1\u6709\u53D7\u5230\u4EFB\u4F55\u4EBA\u7684\u6559\u4E49\u3001\u9053\u5FB7\u3001\u6587\u5316\u3001\u6216\u662F\u72B9\u592A\u4EBA\u5F8B\u6CD5\u51C6\u5219\u7684\u5F71\u54CD\u3002\u4ECA\u5929\uFF0C\u8BB8\u591A\u4FE1\u5F92\u5728\u8BA4\u8BC6\u798F\u97F3\u4E0A\u5374\u4E0D\u77E5\u4E0D\u89C9\u5728\u5FC3\u91CC\u9762\u52A0\u4E0A\u81EA\u5DF1\u4E00\u5957\u4EBA\u7684\u4EEA\u6587\uFF0C\u6240\u4EE5\u4E3B\u7684\u798F\u97F3\u603B\u65E0\u6CD5\u8FDB\u5230\u4ED6\u4EEC\u751F\u547D\u7684\u4E2D\u5FC3\u70B9\u3002\u4ED6\u4EEC\u867D\u662F\u2018\u4FE1\u8036\u7A23\u2019\uFF0C\u4F46\u4E5F\u603B\u5728\u8BB8\u591A\u4E8B\u4E0A\u5E26\u6709\u4EBA\u7684\u610F\u601D\uFF0C\u4EE5\u81F4\u65E0\u6CD5\u7ECF\u5386\u51E1\u4E8B\u4E0A\u5723\u7075\u5927\u80FD\u7684\u4F5C\u4E3A\u3002</li></ul><p>\u795E\u5C06\u300C\u5E94\u8BB8\u300D\u8D50\u7ED9\u51E1\u4FE1\u57FA\u7763\u798F\u97F3\u4E4B\u4EBA\uFF0C\u4F7F\u4ED6\u4EEC\u8BA4\u8BC6\u81EA\u5DF1\u662F\u4ECE\u6BCD\u8179\u91CC\u5C31\u5206\u522B\u51FA\u6765\uFF0C\u4E3A\u8981\u5C06\u798F\u97F3\u4F20\u7ED9\u4E07\u6C11</p><ul><li>\u795E\u5C06\u7942\u7684\u5E94\u8BB8\u8D50\u4E8E\u6211\u4EEC\uFF0C\u4F7F\u6211\u4EEC\u8FD9\u7FA4\u5728\u57FA\u7763\u91CC\u7684\u4EBA\u771F\u8BA4\u8BC6\u81EA\u5DF1\u662F\u8C01\u3002\u5176\u5B9E\uFF0C\u4EBA\u600E\u4E48\u8BA4\u8BC6\u81EA\u5DF1\uFF0C\u4ED6\u4FBF\u4F1A\u8D70\u600E\u6837\u7684\u8DEF\uFF0C\u4E5F\u4E3A\u81EA\u5DF1\u5B89\u6392\u4EC0\u4E48\u3002\u4FDD\u7F57\u8FD8\u672A\u5728\u57FA\u7763\u91CC\u8BA4\u8BC6\u81EA\u5DF1\u65F6\uFF0C\u4ED6\u4E13\u8BA4\u4E3A\u81EA\u5DF1\u662F\u2018\u72B9\u592A\u795E\u6C11\u2019\u800C\u5FC5\u987B\u9760\u5F8B\u6CD5\u5F97\u8499\u795E\u559C\u60A6\u3002\u6240\u4EE5\uFF0C\u4ED6\u751F\u6765\u662F\u4E00\u4E2A\u5F8B\u6CD5\u4E3B\u4E49\u8005\uFF0C\u4E13\u9760\u2018\u5B88\u5F8B\u2019\u6765\u5F97\u795E\u7684\u4E49\u3002\u4F46\u4ED6\u4ECE\u798F\u97F3\u5F97\u7740\u70D9\u5370\u4E4B\u540E\uFF0C\u624D\u77E5\u9053\u8DDF\u81EA\u5DF1\u7684\u610F\u5FF5\u65E0\u5173\uFF0C\u795E\u5728\u521B\u7ACB\u4E16\u754C\u4EE5\u524D\u5DF2\u7ECF\u62E3\u9009\u4E86\u4ED6\uFF0C\u5C06\u4ED6\u5206\u522B\u51FA\u6765\uFF0C\u4E3A\u8981\u900F\u8FC7\u4ED6\u7684\u4E00\u751F\u4F7F\u4ED6\u8BA4\u8BC6\u57FA\u7763\uFF0C\u4F20\u626C\u57FA\u7763\uFF0C\u62EF\u6551\u540C\u6837\u795E\u7684\u5B50\u6C11\u3002\u6240\u4EE5\uFF0C\u82E5\u4E00\u4E2A\u4FE1\u5F92\u53EA\u5E26\u7740\u201C\u4FE1\u8036\u7A23\uFF0C\u5F97\u6C38\u751F\uFF0C\u4E3B\u65E5\u53BB\u6559\u4F1A\u201D\u7684\u5B97\u6559\u89C2\u5FF5\uFF0C\u4ED6\u7684\u4E00\u751F\u7EDD\u5BF9\u65E0\u6CD5\u8D70\u5728\u4E0A\u5E1D\u7684\u4E2D\u5FC3\u8BA1\u5212\u3002\u552F\u6709\u8BA4\u8BC6\u5E94\u8BB8\uFF0C\u4EE5\u5E94\u8BB8\u8BBE\u8BA1\u4EBA\u751F\u4E4B\u4EBA\u624D\u80FD\u6D3B\u51FA\u4ED6\u751F\u547D\u7684\u5C0A\u8D35\u610F\u4E49\u3002</li></ul><p>\u795E\u4E5F\u9884\u5907\u4E86\u795E\u6C11\u5728\u5730\u4E0A\u7684\u300C\u89D2\u8272\u3001\u5C97\u4F4D\u3001\u6069\u8D50\u300D\uFF0C\u597D\u4F7F\u4ED6\u4EEC\u6653\u5F97\u795E\u5C06\u5982\u4F55\u5E26\u9886\u4ED6\u4EEC\u7684\u4E00\u751F\u6210\u5C31\u795E\u6240\u5E94\u8BB8\u7684</p><ul><li>\u6211\u4EEC\u8981\u6653\u5F97\u795E\u5728\u6BCF\u4E2A\u5723\u6C11\u8EAB\u4E0A\u7684\u72EC\u7279\u8BA1\u5212\u3002\u795E\u7684\u547C\u53EC\u4E0E\u5E94\u8BB8\u7EB5\u7136\u662F\u4E00\u81F4\u7684\uFF0C\u4F46\u795E\u5982\u4F55\u4F7F\u7528\u4E00\u4E2A\u5723\u6C11\u6765\u6210\u5C31\u795E\u7684\u5E94\u8BB8\u5374\u662F\u5404\u6709\u4E0D\u540C\u3002\u8FD9\u5C31\u663E\u660E\u5728\u795E\u5DF2\u7ECF\u653E\u5728\u6211\u4EEC\u751F\u547D\u91CC\u7684\u300C\u89D2\u8272\u3001\u5C97\u4F4D\u3001\u6069\u8D50\u300D\u91CC\u3002\u6240\u4EE5\uFF0C\u6211\u4EEC\u5728\u670D\u4F8D\u795E\u5F53\u4E2D\u4E00\u5B9A\u8981\u660E\u767D\u795E\u547C\u53EC\u6211\u4EEC\u6240\u505A\u7684\uFF0C\u7AD9\u597D\u5C97\u4F4D\uFF0C\u6BCF\u65E5\u5FE0\u5FC3\u4E8E\u795E\u6240\u6258\u4ED8\u6211\u4EEC\u7684\u3002\u8FD9\u6837\u6211\u4EEC\u7684\u670D\u4F8D\u624D\u80FD\u4E13\u5FC3\u3001\u4E00\u5FC3\u3001\u5168\u5FC3\u3001\u6052\u5FC3\u3002\u603B\u5F52\u6765\u8BF4\uFF0C\u4E00\u5207\u90FD\u5904\u4E8E\u795E\uFF0C\u4E5F\u5F52\u4E8E\u795E\u3002</li></ul>",19),a=[e];function u(o,s,r,c,n,d){return i(),_("div",null,a)}var T=l(p,[["render",u]]);export{m as __pageData,T as default};
