import{_ as l,c as _,o as i,a as t}from"./app.70d86030.js";const m='{"title":"","description":"","frontmatter":{},"headers":[],"relativePath":"galatians/3.\u4E0D\u53EF\u524A\u5F31\u5341\u5B57\u67B6\u7684\u529F\u6548.md"}',p={},e=t("<p>\u4E0D\u53EF\u524A\u5F31\u5341\u5B57\u67B6\u7684\u529F\u6548\u300A\u52A01:11 - 2:14\u300B \u5E8F\u8BBA\uFF1A\u5341\u5B57\u67B6\u7684\u798F\u97F3\u662F\u4ECE\u795E\u800C\u6765\uFF0C\u4E5F\u662F\u795E\u7684\u5927\u80FD\u3002\u6574\u4E2A\u5B87\u5B99\u4E07\u6709\u3001\u4EBA\u7C7B\u5386\u53F2\u3001\u6C38\u6052\u7684\u65F6\u5149\u90FD\u662F\u4E3A\u8FD9\u798F\u97F3\u800C\u5B58\u5728\u7684\u3002\u6240\u4EE5\uFF0C\u82E5\u4E00\u4E2A\u4FE1\u5F92\u6309\u7740\u7EAF\u6B63\u7684\u798F\u97F3\u601D\u60F3\u3001\u5224\u65AD\u3001\u884C\u51E1\u4E8B\uFF0C\u795E\u7684\u5927\u80FD\u5FC5\u4F1A\u968F\u7740\u4ED6\u7684\u5FC3\u7075\u4E0E\u4ED6\u4E00\u5207\u6240\u884C\u7684\u3002\u4F46\u662F\uFF0C\u4FE1\u5F92\u65F6\u5E38\u5728\u4E24\u65B9\u9762\u524A\u5F31\u5341\u5B57\u67B6\u7684\u5927\u80FD\u3002\u7B2C\u4E00\uFF0C\u5C31\u662F\u4E0D\u6B63\u786E\u7684\u5C06\u5168\u5907\u7684\u798F\u97F3\u6559\u5BFC\uFF0C\u603B\u662F\u52A0\u51CF\u4EBA\u7684\u4EEA\u6587\uFF0C\u53EB\u542C\u9053\u4E4B\u4EBA\u603B\u7559\u5728\u201C\u56E0\u62E5\u6709\u4E86\u57FA\u7763\u8FD8\u4E0D\u591F\u201D\u7684\u5FC3\u7075\u3002\u53E6\u5916\uFF0C\u5C31\u662F\u5E38\u5728\u65E0\u610F\u8BC6\u4E2D\uFF0C\u5728\u751F\u6D3B\u4E0E\u884C\u4E3A\u91CC\u4F20\u8FBE\u201C\u975E\u798F\u97F3\u201D\u7684\u591A\u79CD\u4FE1\u5FF5\uFF0C\u4EE5\u81F4\u7ECA\u5012\u4ED6\u4EBA\u3002\u8FD9\u4E24\u8005\u90FD\u662F\u5728\u4ECA\u5929\u7684\u4FE1\u606F\u91CC\u6211\u4EEC\u5F53\u53CD\u601D\u4E0E\u68C0\u8BA8\u7684\u3002\u5176\u5B9E\uFF0C\u8FD9\u4E24\u8005\u90FD\u662F\u6765\u4E4B\u4EBA\u4E0D\u591F\u6B63\u76F4\u6E05\u6D01\u7684\u5FC3\u7075\uFF0C\u5BFC\u81F4\u6211\u4EEC\u65E0\u610F\u8BC6\u7684\u65E0\u77E5\uFF0C\u4E0D\u7136\u5C31\u662F\u56E0\u65E0\u80FD\u800C\u59A5\u534F\u4E8E\u201C\u975E\u798F\u97F3\u201D\u7684\u6289\u62E9\u548C\u505A\u6CD5\u3002\u5F53\u7136\uFF0C\u6211\u4EEC\u6CA1\u6709\u4E00\u4E2A\u4EBA\u662F\u5B8C\u6574\u65E0\u6687\u7684\u3002\u4F46\u662F\uFF0C\u795E\u6240\u8D23\u5907\u7684\u4E0D\u662F\u4EBA\u7684\u8F6F\u5F31\u4F53\u8D28\uFF0C\u4E43\u662F\u542C\u4E86\u798F\u97F3\uFF0C\u5374\u7559\u7740\u4EBA\u7684\u4EEA\u6587\uFF0C\u5E26\u7740\u300C\u4E0D\u660E\u767D\u771F\u9053 = \u4E0D\u4FE1 = \u4E0D\u613F\u610F\u6B7B\u300D\u7684\u5FC3\u7075\u3002\u603B\u800C\u6765\u8BF4\uFF0C\u8FD9\u662F\u4EBA\u7075\u91CC\u7684\u95EE\u9898\uFF08\u4FE1\u7684\u4E0D\u7EAF\u6B63\uFF09\uFF0C\u4E0D\u662F\u4E00\u4E2A\u8F6F\u5F31\u4F53\u8D28\u7684\u95EE\u9898\u3002\u56E0\u4E3A\u4FE1\u7684\u4E0D\u7EAF\u6B63\uFF0C\u6240\u4EE5\u603B\u4F1A\u53D7\u8BD5\u63A2\u3002\u6211\u4EEC\u5728\u4ECA\u5929\u7684\u7ECF\u6587\u91CC\u4F1A\u770B\u89C1\u4E09\u4EF6\u91CD\u8981\u7684\u4E8B\u53D1\uFF0C\u5C31\u662F\u5F53\u4FDD\u7F57\u5728\u5927\u9A6C\u58EB\u9769\u9047\u89C1\u4E86\u4E3B\u8036\u7A23\u4E4B\u540E\uFF0C\u4ED6\u6700\u9996\u5148\u505A\u7684\u5C31\u662F\u501F\u7740\u798F\u97F3\uFF081\uFF09\u5168\u90E8\u62C6\u6BC1\uFF0C\u91CD\u65B0\u5EFA\u9020\u4ED6\u7684\u751F\u547D\uFF1B\uFF082\uFF09\u5341\u56DB\u5E74\u540E\uFF0C\u4ED6\u5230\u8036\u8DEF\u6492\u51B7\u5BFB\u627E\u8036\u8DEF\u6492\u51B7\u540D\u671B\u7684\u4F7F\u5F92\uFF0C\u8981\u4E0E\u4ED6\u4EEC\u786E\u8BA4\u4E0E\u8BA4\u540C\u798F\u97F3\u7684\u771F\u610F\uFF1B\uFF083\uFF09\u751A\u81F3\u6709\u4E00\u6B21\u5728\u4E0D\u5F97\u5DF2\u7684\u60C5\u51B5\u4E4B\u4E0B\uFF0C\u4ED6\u4E3A\u4E86\u7EF4\u62A4\u798F\u97F3\u7684\u7EAF\u51C0\u5EA6\uFF0C\u8D23\u5907\u4E86\u5F7C\u5F97\uFF08\u77F6\u6CD5\uFF09\u7684\u5047\u5192\u4E3A\u5584\u3002\u4F46\u4ED6\u8FD9\u4E48\u505A\uFF0C\u5E76\u4E0D\u662F\u8981\u663E\u793A\u4ED6\u6BD4\u5F7C\u5F97\u66F4\u52A0\u516C\u4E49\u6216\u9AD8\u5C1A\uFF0C\u4E43\u662F\u5728\u798F\u97F3\u4F20\u5230\u5916\u90A6\u65F6\uFF0C\uFF08\u56E0\u798F\u97F3\u662F\u501F\u5341\u4E8C\u4F7F\u5F92\u4F20\u9012\uFF09\uFF0C\u82E5\u72B9\u592A\u57FA\u7763\u5F92\u7684\u9886\u8896\u5F2F\u66F2\u4E86\u798F\u97F3\u7684\u4EF7\u503C\uFF0C\u5386\u53F2\u5386\u4EE3\u4FBF\u4F1A\u5BF9\u798F\u97F3\u7684\u72EC\u4E00\u6027\u4E0E\u7EAF\u6B63\u6027\u6A21\u7CCA\u4E86\uFF01\u6240\u4EE5\uFF0C\u5728\u8FD9\u5173\u952E\u7684\u65F6\u523B\u91CC\uFF0C\u4FDD\u7F57\u5FC5\u5B9A\u5F53\u6307\u51FA\u5F7C\u5F97\u7684\u9519\u8BEF\uFF0C\u4ED6\u4E5F\u501F\u6B64\u8BA9\u52A0\u62C9\u592A\u4FE1\u5F92\u8BA4\u8BC6\u201C\u552F\u72EC\u56E0\u4FE1\u57FA\u7763\u79F0\u4E49\u201D\u7684\u7EDD\u5BF9\u6027\u3002</p><p>\u798F\u97F3\u662F\u501F\u7740\u57FA\u7763\u6240\u542F\u793A\uFF0C\u6CA1\u6709\u52A0\u4E0A\u4EFB\u4F55\u4EBA\u7684\u4EEA\u6587\u300A1\uFF1A11-24\u300B \u5F53\u4EBA\u5168\u7136\u5904\u5728\u5815\u843D\u91CC\uFF0C\u4E0A\u5E1D\u4E50\u610F\uFF08\u4E3B\u52A8\uFF09\u5411\u4EBA\u663E\u660E\u7942\u7684\u6551\u6069</p><ul><li>\u6240\u4EE5\uFF0C\u798F\u97F3\u7684\u7B2C\u4E00\u6240\u5F3A\u8C03\u7684\u662F\uFF1A\u4ECE\u4EBA\u6240\u51FA\u53D1\u7684\uFF0C\u6CA1\u6709\u4E00\u4E2A\u80FD\u8FBE\u5230\u795E\u7684\u4E49\u3002\u4ECA\u5929\uFF0C\u8BB8\u591A\u4EBA\u5728\u4FE1\u798F\u97F3\u4E0E\u4F20\u8BB2\u798F\u97F3\u4E0A\uFF0C\u7EE7\u7EED\u9AD8\u4E3E\u67D0\u4E9B\u4EBA\u7684\u9053\u5FB7\u3001\u6587\u5316\u3001\u80CC\u666F\u3001\u826F\u5584\u3001\u5B66\u95EE\u3001\u5584\u884C\uFF08\u597D\u4EBA\u683C\uFF09\u3001\u7B49\uFF0C\u7136\u540E\u5C06\u8FD9\u4E00\u5207\u4E0E\u201C\u5728\u57FA\u7763\u91CC\u8499\u798F\u201D\u4E00\u540C\u6DF7\u5408\u8D77\u6765\u3002\u8BB8\u591A\u4FE1\u5F92\u4E5F\u4E0D\u662F\u4ECE\u5426\u8BA4\u4EBA\u610F\uFF08\u4E16\u754C\uFF09\u800C\u5F00\u59CB\u8BA4\u8BC6\u795E\u7684\u8BDD\u3001\u8BA4\u8BC6\u81EA\u5DF1\u3001\u8BA4\u8BC6\u4E00\u5207\u7684\u3002\u8FD9\u5BFC\u81F4\u4E86\u4EBA\u8FC7\u540E\u5728\u5FC3\u7075\u91CC\u6240\u5F97\u7684\u5723\u7075\u5370\u8BB0\u3001\u611F\u52A8\u3001\u5145\u6EE1\u90FD\u5927\u6709\u504F\u5DEE\u3002\u5F53\u7136\uFF0C\u4EBA\u7684\u6240\u8C13\u5584\u884C\u5584\u610F\u4E5F\u90FD\u662F\u4ECE\u795E\u800C\u6765\uFF0C\u4F46\u8FD9\u4E9B\u90FD\u662F\u5904\u4E8E\u201C\u4E00\u822C\u6069\u5178\u201D\uFF0C\u5374\u6CA1\u6709\u5305\u62EC\u201C\u7279\u522B\u6069\u5178\u201D\u6240\u7ED9\u4E8E\u7684\u6551\u6069\u3002\u8FDE\u884C\u795E\u5F8B\u6CD5\u7684\u7EA6\u4F2F\u3001\u54E5\u5C3C\u6D41\u3001\u4FDD\u7F57\u3001\u7B49\uFF0C\u90FD\u662F\u8981\u501F\u7740\u56E0\u4FE1\u795E\u7684\u6551\u8D4E\u542F\u793A\uFF08\u57FA\u7763\uFF09\uFF0C\u624D\u80FD\u5F97\u8499\u79F0\u4E49\u3002\u6240\u4EE5\uFF0C\u4EBA\u82E5\u56E0\u8FD9\u201C\u4E00\u822C\u6069\u5178\u201D\u800C\u81EA\u6EE1\u81EA\u4E49\uFF0C\u800C\u4E0D\u80FD\u9886\u53D7\uFF08\u4E0D\u9700\u8981\uFF09\u4E0A\u5E1D\u7684\u201C\u7279\u522B\u6069\u5178\u201D\uFF08\u57FA\u7763\uFF09\uFF0C\u90A3\u4EBA\u5C06\u662F\u6700\u53EF\u60B2\u7684\u3002</li></ul><p>\u5728\u57FA\u7763\u91CC\u7684\u884C\u5584\u3001\u70ED\u5FC3\u3001\u8654\u8BDA\uFF0C\u5168\u7136\u662F\u53D7\u5230\u5723\u7075\u611F\u52A8\u800C\u51FA\u53D1\uFF0C\u4E0D\u53D7\u5230\u4EBA\u7684\u4EEA\u6587\u5F71\u54CD</p><ul><li>\u5723\u7075\u662F\u4E00\u5207\u5723\u6D01\u7684\u6E90\u5934\uFF01\u4EBA\u7684\u4EFB\u4F55\u5584\u884C\u90FD\u65E0\u6CD5\u6BD4\u5723\u7075\u66F4\u5723\u6D01\u3002\u5723\u7075\u6240\u8D50\u7684\u5723\u6D01\u662F\uFF1A\uFF081\uFF09\u56E0\u57FA\u7763\u79F0\u4E49\u4E4B\u5FC3\uFF0C\u597D\u53EB\u4EBA\u4E0D\u518D\u53D7\u63A7\u544A\u3002\u4E0D\u53D7\u63A7\u544A\u4E4B\u4EBA\uFF0C\u624D\u80FD\u5F00\u59CB\u884C\u5584\u3002\u56E0\u4E3A\u5728\u63A7\u544A\uFF08\u5211\u7F5A\uFF09\u4E4B\u5FC3\u6240\u51FA\u53D1\u7684\uFF0C\u4E0D\u80FD\u4ECE\u201C\u7231\u201D\u51FA\u53D1\uFF0C\u4EE5\u81F4\u5931\u53BB\u5723\u6D01\u3002\uFF082\uFF09\u5C31\u662F\uFF1A\u542C\u4ECE\u795E\u800C\u884C\u3002\u4EBA\u7684\u4E00\u5207\u5584\u884C\u90FD\u65E0\u6CD5\u80DC\u4E8E\u201C\u542C\u4ECE\u795E\u201D\u4E4B\u5584\u3002\u4E9A\u4F2F\u62C9\u7F55\u3001\u6469\u897F\u3001\u5927\u536B\u88AB\u795E\u6240\u79F0\u8BB8\u90FD\u662F\u56E0\u542C\u4ECE\u795E\u800C\u6765\u3002\u5F80\u5F80\uFF0C\u6211\u4EEC\u7684\u5FC3\u5C31\u662F\u4E0D\u80FD\u660E\u767D\u800C\u8FDB\u5230\u8FD9\u4E24\u79CD\u72B6\u6001\uFF0C\u603B\u5F98\u5F8A\u5728\u53D7\u63A7\u544A\u3001\u62C5\u5FC3\u3001\u60E7\u6015\u3001\u5FE7\u95F7\u3001\u65E0\u52A9\u65E0\u80FD\u7684\u72B6\u6001\uFF0C\u6240\u4EE5\u603B\u542C\u4ECE\u8089\u4F53\uFF0C\u65E0\u6CD5\u542C\u4ECE\u795E\u3002</li></ul><p>\u798F\u97F3\u4F7F\u4EBA\u5F97\u7740\u300C\u6B63\u76F4\u6E05\u6D01\u300D\u7684\u5FC3\u7075\uFF0C\u613F\u610F\u300C\u5168\u90E8\u62C6\u6BC1\uFF0C\u91CD\u65B0\u5EFA\u9020\u300D</p><ul><li>\u4E00\u4E2A\u4FE1\u5F92\u4FE1\u4E3B\u4E0D\u4E45\uFF0C\u6216\u662F\u4FE1\u4E3B\u540E\u8FD8\u6709\u6240\u4F53\u8D28\u4E0A\u7684\u8F6F\u5F31\u90FD\u6CA1\u5173\u7CFB\uFF0C\u56E0\u4E3A\u4ED6\u5728\u795E\u9762\u524D\u662F\u4E00\u751F\u7684\u6210\u957F\uFF0C\u4E5F\u6709\u4E0A\u5E1D\u72EC\u7279\u7684\u65F6\u95F4\u8868\u5728\u4ED6\u7684\u8EAB\u4E0A\u3002\u4F46\u662F\uFF0C\u4E00\u4E2A\u4FE1\u5F92\u6709\u6839\u6709\u57FA\u7684\u6210\u957F\u662F\u4ECE\u4ED6\u62E5\u6709\u613F\u610F\u300C\u5168\u90E8\u62C6\u6BC1\uFF0C\u91CD\u65B0\u5EFA\u9020\u300D\u7684\u51B3\u5FC3\u5F00\u59CB\u3002\u6709\u65F6\u5019\uFF0C\u4EBA\u6539\u5584\u5F97\u533B\u6CBB\u9700\u8981\u65F6\u95F4\uFF0C\u4F46\u4EBA\u5FC5\u987B\u8FDB\u5230\u201C\u80FD\u88AB\u6539\u5584\uFF0C\u80FD\u88AB\u533B\u6CBB\u201D\u7684\u5FC3\u7075\u72B6\u6001\u3002\u6240\u4EE5\uFF0C\u5728\u5224\u65AD\u4E00\u4E2A\u4FE1\u5F92\u7684\u751F\u547D\u65F6\uFF0C\u4E0D\u662F\u5148\u770B\u4ED6\u7684\u6761\u4EF6\u3001\u54C1\u683C\u3001\u5B66\u95EE\u3001\u884C\u4E3A\u3001\u70ED\u5FC3...\u4E43\u662F\u4EE5\u201C\u4E3B\u662F\u5426\u5DF2\u7ECF\u6210\u4E3A\u4ED6\u5FC3\u4E2D\u7684\u4E3B\u201D\u6765\u5224\u65AD\uFF0C\u4E5F\u4ECE\u8FD9\u91CC\u6559\u5BFC\u4ED6\u5B66\u4E49\u3002\u8FD9\u7B49\u4FE1\u5F92\u4E5F\u5C31\u662F\u4E3B\u8036\u7A23\u4ECA\u65E5\u6240\u5BFB\u627E\u7684\u95E8\u5F92\u3002</li></ul><p>\u5728\u7EF4\u62A4\u798F\u97F3\u7684\u771F\u610F\u4E0A\u603B\u4E0D\u59A5\u534F\uFF0C\u4F46\u4E5F\u70ED\u5FC3\u7684\u884C\u4E3B\u6240\u6258\u4ED8\u4E4B\u4E8B\u300A2\uFF1A1-14\u300B</p><ul><li>\u8BA4\u8BC6\u798F\u97F3\u7684\u4FDD\u7F57\u5F88\u6E05\u695A\u7684\u5F97\u7740\u5723\u7075\u7684\u611F\u52A8\uFF0C\u5728\u4ED6\u7231\u795E\u7231\u4EBA\u7684\u5FC3\u7075\u91CC\uFF0C\u660E\u786E\u7684\u5F97\u7740\u5723\u7075\u4EE5\u4E0B\u7684\u6307\u793A\uFF1A \u798F\u97F3\u7684\u7CBE\u4E49\u662F\u7231\u795E\u7231\u4EBA\uFF0C\u6240\u4EE5\u5728\u201C\u524A\u5F31\u5341\u5B57\u67B6\u7684\u529F\u6548\uFF0C\u4F7F\u4EBA\u65E0\u6CD5\u7ECF\u5386\u798F\u97F3\u5927\u80FD\u201D\u7684\u4E8B\u4E0A\uFF0C\u603B\u4E0D\u59A5\u534F\u300A2\uFF1A1-5\u300B</li><li>\u4FDD\u7F57\u6240\u884C\u7684\u90FD\u662F\u8981\u4F7F\u4EBA\u7684\u5FC3\u5355\u5355\u5D07\u9AD8\u57FA\u7763\uFF0C\u8BA4\u8BC6\u798F\u97F3\u7684\u5927\u80FD\u3002\u4FDD\u7F57\u5728\u300A\u52A02\uFF1A3\u300B\u91CC\uFF0C\u4E3A\u4E86\u7EF4\u62A4\u57FA\u7763\u6551\u6069\u7684\u7EAF\u51C0\u5EA6\uFF0C\u4ED6\u4E0D\u7ED9\u63D0\u591A\uFF08\u5E0C\u814A\u4EBA\uFF09\u884C\u5272\u793C\uFF0C\u4E43\u5355\u5355\u7ED9\u4F17\u4EBA\u770B\u4ED6\u91CC\u5934\u751F\u547D\u7684\u5370\u8BB0\u3002\u8FD9\u4E0E\u300A\u4F7F16\uFF1A3\u300B\uFF0C\u4FDD\u7F57\u7ED9\u63D0\u6469\u592A\u884C\u5272\u793C\u7684\u60C5\u51B5\u53C8\u4E0D\u4E00\u6837\u3002\u7406\u7531\u662F\u56E0\u4E3A\u63D0\u591A\u662F\u7EAF\u5916\u90A6\u4EBA\uFF0C\u5047\u5F1F\u5144\u4EEC\u7279\u4EE5\u201C\u72B9\u592A\u8EAB\u4EFD\u201D\u4E3A\u79F0\u4E49\u7684\u5173\u952E\uFF0C\u6240\u4EE5\u4E3A\u4E86\u7EF4\u62A4\u798F\u97F3\u7684\u7EAF\u51C0\u5EA6\uFF0C\u4FDD\u7F57\u7279\u610F\u4E0D\u7ED9\u63D0\u591A\u884C\u5272\u793C\u3002\u4F46\u63D0\u6469\u592A\u5DF2\u662F\u534A\u4E2A\u72B9\u592A\u4EBA\uFF08\u6BCD\u4EB2\u662F\u72B9\u592A\u4EBA\uFF09\uFF0C\u4E5F\u5728\u72B9\u592A\u6559\u4E49\u957F\u5927\u3002\u6240\u4EE5\u5BF9\u63D0\u6469\u592A\u6765\u8BB2\uFF0C\u4ED6\u53D7\u4E0D\u53D7\u5272\u793C\u4E0D\u5173\u952E\uFF0C\u4F46\u56E0\u4E3A\u4FDD\u7F57\u8981\u5E26\u7740\u63D0\u6469\u592A\u5411\u72B9\u592A\u4EBA\u4F20\u798F\u97F3\uFF0C\u4E3A\u4E86\u4E0D\u8BA9\u72B9\u592A\u4EBA\u6709\u5148\u5165\u4E3A\u4E3B\u7684\u60F3\u6CD5\u800C\u88AB\u7ECA\u5012\uFF0C\u4FDD\u7F57\u4FBF\u7ED9\u4ED6\u884C\u5272\u793C\u3002\u5728\u8FD9\u4E00\u5207\uFF0C\u4FDD\u7F57\u90FD\u662F\u4E3A\u4E86\u798F\u97F3\u7684\u76CA\u5904\u800C\u884C\u3002</li></ul><p>\u5728\u4F7F\u4F17\u4EBA\u5F97\u76CA\u5904\u7684\u4E8B\u4E0A\uFF0C\u603B\u8981\u70ED\u5FC3\u7684\u53BB\u505A\u300A2\uFF1A6-10\u300B</p><ul><li>\u5728\u9886\u53D7\u201C\u56E0\u4FE1\u79F0\u4E49\uFF0C\u56E0\u4FE1\u800C\u6D3B\u201D\u7684\u6559\u5BFC\u4E0A\uFF0C\u4E5F\u6709\u8BB8\u591A\u4FE1\u5F92\u56E0\u4E0D\u660E\u767D\u201C\u5723\u7075\u50AC\u903C\u800C\u6765\u7684\u4E3B\u52A8\u201D\uFF0C\u6240\u4EE5\u8BE5\u79EF\u6781\u7684\u4E0D\u79EF\u6781\uFF0C\u8BE5\u5FE0\u5FC3\u6301\u5B88\u7684\u53CD\u6563\u6F2B\u8FDF\u949D\u3002\u5728\u8FD9\u91CC\uFF0C\u6211\u4EEC\u770B\u89C1\u5F53\u4FDD\u7F57\u56E0\u4FE1\u800C\u6D3B\uFF0C\u4ED6\u5FC3\u91CC\u7684\u5723\u7075\u662F\u5982\u4F55\u79EF\u6781\u7684\u611F\u52A8\u4ED6\u3002\u4ED6\u5728\u81EA\u5DF1\u7684\u89D2\u8272\u3001\u5C97\u4F4D\u3001\u6069\u8D50\u3001\u4E0E\u7231\u795E\u7231\u4EBA\u7684\u804C\u8D23\u4E0A\u662F\u591A\u4E48\u6E05\u695A\u4E0E\u79EF\u6781\u4E8E\u5723\u7075\u7684\u5F15\u5BFC\u3002\u4ED6\u770B\u89C1\u795E\u5728\u5916\u90A6\u4EBA\u7684\u4F5C\u4E3A\uFF0C\u5BDF\u89C9\u72B9\u592A\u5047\u5F1F\u5144\u7684\u6781\u7AEF\uFF0C\u5C31\u7ACB\u65F6\u4E0A\u8036\u8DEF\u6492\u51B7\u627E\u4F7F\u5F92\u4EEC\u786E\u8BA4\u798F\u97F3\u7684\u771F\u610F\uFF0C\u4E4B\u540E\u5F97\u4F17\u4EBA\u7684\u795D\u798F\u5411\u5916\u90A6\u4EBA\u4F20\u9053\uFF0C\u7136\u540E\u4E5F\u4E50\u610F\u63A5\u53D7\u5E2E\u52A9\u8D2B\u82E6\u5F1F\u5144\u4EEC\u7684\u6258\u4ED8\u3002\u56E0\u6B64\uFF0C\u4ED6\u5F97\u4E86\u795E\u4E0E\u4EBA\u7684\u559C\u60A6\u3002</li></ul><p>\u5728\u7231\u4E2D\u7684\u8D23\u5907\uFF0C\u5E26\u6765\u5BF9\u795E\u7684\u56FD\u4E0E\u5BF9\u4EBA\u90FD\u6709\u76CA\u5904\u300A2\uFF1A11-14\u300B</p><ul><li>\u5728\u8FD9\u91CC\uFF0C\u4FDD\u7F57\u53D7\u5723\u7075\u611F\u52A8\u8D23\u5907\u5F7C\u5F97\u7684\u8FC7\u9519\uFF0C\u56E0\u8FD9\u8FC7\u9519\u4E0D\u4F46\u4F1A\u5E26\u6765\u5F7C\u5F97\u7684\u4E8F\u635F\uFF0C\u4E5F\u5E26\u6765\u4E86\u4F17\u591A\u72B9\u592A\u4FE1\u5F92\u88AB\u7ECA\u5012\uFF0C\u66F4\u662F\u4F1A\u5E26\u6765\u57FA\u7763\u798F\u97F3\u7684\u5EF6\u4F38\u4F1A\u9010\u6E10\u53D8\u8D28\u3002\u6240\u4EE5\uFF0C\u4FDD\u7F57\u5728\u7EF4\u62A4\u798F\u97F3\u7684\u4E8B\u4E0A\u975E\u5E38\u7684\u679C\u65AD\u3002\u4F46\u662F\u6211\u4EEC\u4E5F\u8981\u5728\u8D23\u5907\u5C5E\u7075\u9886\u8896\u7684\u4E8B\u4E0A\u8C28\u614E\u3002\u8BB8\u591A\u4FE1\u5F92\u82E5\u4E0D\u660E\u767D\u5176\u4E2D\u7684\u610F\u4E49\uFF0C\u80E1\u4E71\u6309\u7740\u201C\u81EA\u6211\u4E3B\u89C2\u6B63\u4E49\u201D\u6765\u8D23\u5907\u4EBA\u6216\u662F\u5C5E\u7075\u9886\u8896\uFF0C\u4F46\u81EA\u5DF1\u751F\u547D\u7684\u52A8\u673A\u3001\u5BA2\u89C2\u5224\u65AD\u3001\u7231\u5FC3\u5374\u4E0D\u5982\u9886\u8896\uFF0C\u4E5F\u4E0D\u662F\u5B58\u5FC3\u7231\u795E\u7231\u5168\u4F53\u6559\u4F1A\uFF0C\u4E5F\u4E0D\u6309\u7740\u795E\u7684\u7F8E\u610F\u884C\uFF0C\u90A3\u4FBF\u662F\u611A\u6627\u7684\u505A\u6CD5\uFF0C\u56E0\u4E3A\u4F1A\u5E26\u6765\u4E89\u8BBA\u7684\u7075\u505A\u5DE5\uFF0C\u4E5F\u5BFC\u81F4\u4E3B\u7684\u6559\u4F1A\u4E0E\u81EA\u5DF1\u90FD\u53D7\u5230\u4E8F\u635F\u3002</li></ul><p>\u5728\u798F\u97F3\u4E0A\u505A\u5E08\u5085\u7684\uFF0C\u603B\u8981\u8C28\u614E\uFF0C\u597D\u5728\u51E1\u4E8B\u4E0A\u603B\u53EB\u57FA\u7763\u5728\u6211\u4EEC\u8EAB\u4E0A\u7167\u5E38\u663E\u5927\u300A\u52A02\uFF1A11-13\u300B\u300A\u96C53\uFF1A1\u300B\u300A\u81531\uFF1A20\u300B \u6240\u6709\u4FE1\u5F92\u90FD\u6709\u4E49\u52A1\u4FDD\u62A4\u5F7C\u6B64\u7684\u4FE1\u5FC3</p><ul><li>\u5F53\u6211\u4EEC\u770B\u89C1\u81EA\u5DF1\u7684\u80A2\u4F53\u8FDC\u79BB\u4E3B\u9053\u6216\u662F\u8F7B\u89C6\u67D0\u4E9B\u5723\u6D01\u7684\u771F\u7406\uFF0C\u800C\u968F\u4ECE\u60C5\u6B32\u7684\u4E8B\u65F6\uFF0C\u6211\u4EEC\u6709\u4E49\u52A1\u201C\u4FDD\u62A4\u4ED6\u201D\uFF0C\u4EE5\u300A\u592A18\uFF1A15-20\u300B\u7684\u539F\u7406\u529D\u89E3\u4ED6\u3002\u4F46\u6211\u4EEC\u5FC5\u8981\u61C2\u5F97\u9760\u5723\u7075\u800C\u884C\u3002\u56E0\u6709\u4E9B\u8F6F\u5F31\u662F\u4EBA\u7684\u4F53\u8D28\u5E26\u6765\u7684\uFF08\u7075\u91CC\u7684\u8425\u5792\uFF09\uFF0C\u4E0D\u662F\u4EBA\u77E5\u9053\u540E\u9A6C\u4E0A\u80FD\u6539\u7684\uFF0C\u4F46\u6211\u4EEC\u5728\u5E2E\u52A9\u80A2\u4F53\u65F6\uFF0C\u603B\u8981\u5728\u7231\u4E2D\u884C\uFF0C\u7406\u89E3\u4EBA\u7684\u5904\u5883\uFF0C\u4E5F\u4E3B\u8981\u5E2E\u52A9\u4ED6\u5EFA\u7ACB\u5728\u57FA\u7763\u91CC\u6B63\u786E\u7684\u201C\u51E1\u4E8B\u89C2\u201D\uFF0C\u4E0D\u662F\u4E13\u6CE8\u4EBA\u884C\u4E3A\u7684\u5BF9\u9519\u3002</li></ul><p>\u5728\u4E3B\u91CC\u505A\u5E08\u5085\u7684\uFF08\u6559\u5BFC\u7267\u517B\u7684\uFF09\uFF0C\u90FD\u56E0\u4E3A\u53D7\u4E86\u201C\u5582\u517B\u7167\u987E\u4E3B\u7684\u7F8A\u7684\u6258\u4ED8\u201C\uFF0C\u6240\u4EE5\u5728\u884C\u4E8B\u4E3A\u4EBA\u4E0A\u90FD\u5F53\u52A0\u500D\u8C28\u614E</p><ul><li>\u505A\u5E08\u5085\u7684\u5728\u4E3B\u91CC\u6709\u66F4\u5927\u7684\u6258\u4ED8\uFF0C\u867D\u662F\u81EA\u7531\uFF08\u51E1\u4E8B\u90FD\u53EF\u884C\uFF09\uFF0C\u4F46\u603B\u662F\u5728\u8A00\u884C\u3001\u4E3E\u6B62\u3001\u4E3A\u4EBA\u3001\u4F5C\u4E3A\u65B9\u9762\u66F4\u52A0\u4E25\u8C28\u3002\u6240\u4EE5\u5728\u51E1\u4E8B\u4E0A\uFF1A \uFF081\uFF09\u5148\u8981\u4FDD\u62A4\u798F\u97F3\u7684\u7EAF\u51C0\u5EA6\uFF0C\u6240\u4EE5\u5728\u6559\u5BFC\u4E0E\u4E3A\u4EBA\u4E0A\uFF0C\u603B\u662F\u9AD8\u4E3E\u57FA\u7763\u7684\u798F\u97F3\uFF08\u795E\u7684\u5E94\u8BB8\uFF09\uFF0C\u4E0D\u9AD8\u4E3E\u4EBA\u7684\u6761\u4EF6\u3001\u54C1\u683C\u3001\u884C\u4E3A\u3001\u7B49\u3002 \uFF082\uFF09\u5728\u884C\u4E8B\u4E3A\u4EBA\u65B9\u9762\uFF0C\u5FC3\u4E2D\u603B\u5E26\u7740\uFF1A\u201C\u51E1\u4E8B\u90FD\u53EF\u884C\uFF0C\u4F46\u4E0D\u90FD\u6709\u76CA\u5904\u201D\u7684\u539F\u5219\uFF0C\u597D\u53EB\u6211\u4EEC\u80FD\u4FDD\u62A4\u4FE1\u5F92\u7684\u4FE1\u5FC3\uFF0C\u4E5F\u4ECE\u4E2D\u6307\u70B9\u4FE1\u5F92\u8D70\u8499\u6069\u7684\u9053\u8DEF\u3002\u5176\u5B9E\uFF0C\u505A\u6559\u5BFC\u7267\u517B\u7684\u4EBA\u65E0\u8BBA\u505A\u4EC0\u4E48\uFF0C\u90FD\u5411\u4E3B\u505A\uFF0C\u4E5F\u4E3A\u4E86\u5168\u4F53\u4FE1\u5F92\u80FD\u591A\u660E\u767D\u771F\u9053\u3001\u5F97\u9020\u5C31\u3001\u8D70\u795E\u559C\u60A6\u7684\u8DEF\u4E3A\u76EE\u6807\u3002</li></ul><p>\u4F46\u5728\u4E3B\u91CC\uFF0C\u6CA1\u6709\u4E00\u4E2A\u4EBA\u662F\u5B8C\u5168\u65E0\u8FC7\u5931\u7684\uFF0C\u6240\u4EE5\u603B\u8981\u7528\u7231\u5FC3\u5F7C\u6B64\u5BF9\u5F85\uFF0C\u4E0D\u80FD\u8D85\u51FA\u5723\u7075\u7684\u4F5C\u4E3A\u6765\u8981\u6C42\u6216\u8D23\u5907\u4EBA \u2460 \u8981\u5224\u65AD\u4EBA\u7684\u8F6F\u5F31\u8FC7\u72AF\u91CC\u5934\u7684\u52A8\u673A\u662F\u654C\u5BF9\u771F\u7406\u8FD8\u662F\u4EBA\u7684\u65E0\u77E5\u65E0\u80FD\u5E26\u6765\u3002\u82E5\u662F\u540E\u8005\uFF0C\u603B\u662F\u9976\u6055\u3001\u529D\u52C9\u3001\u7B49\u5019\uFF0C\u4E0D\u8981\u592A\u4E25\u5389\u8D23\u5907\u3002 \u2461 \u5728\u5224\u65AD\u53EF\u5426\u8D23\u5907\uFF0C\u5148\u770B\u90A3\u662F\u5426\u4F1A\u5E26\u7ED9\u8F6F\u5F31\u8005\u6781\u5927\u7684\u5BB3\u5904\u3002\u6240\u4EE5\uFF0C\u7231\u5FC3\u51FA\u53D1\u7684\u8D23\u5907\u662F\u5148\u4EE5\u8F6F\u5F31\u8005\u5F97\u76CA\u5904\u51FA\u53D1\uFF0C\u4E0D\u662F\u539F\u5219\u51FA\u53D1\u3002 \u2462 \u7136\u540E\u5728\u770B\u662F\u5426\u4F1A\u5E26\u7ED9\u5168\u4F53\u906D\u53D7\u635F\u5BB3\u3002\u82E5\u662F\u4F1A\uFF0C\u9002\u5F53\u7684\u63D0\u9192\u603B\u662F\u9700\u8981\u3002 \u2463 \u8D23\u5907\u4EBA\u4E4B\u540E\u603B\u8981\u65E0\u6761\u4EF6\u7684\u9976\u6055\u4E0E\u9057\u5FD8\u4ED6\u7684\u8F6F\u5F31\uFF0C\u4E0D\u8981\u5E26\u7740\u4E25\u8C28\u76D1\u89C6\u7684\u773C\u5149\u3002\u603B\u800C\u8BF4\uFF0C\u8FD9\u90FD\u662F\u8981\u5BF9\u6148\u7231\u4E0E\u516C\u4E49\u4E4B\u5FC3\u7075\u7684\u638C\u63E1\u3002</p>",18),a=[e];function o(u,s,r,c,n,d){return i(),_("div",null,a)}var T=l(p,[["render",o]]);export{m as __pageData,T as default};
