import{_,c as l,o as i,a as t}from"./app.618cde25.js";const m='{"title":"","description":"","frontmatter":{},"headers":[],"relativePath":"galatians/4.\u5F8B\u6CD5\u4F7F\u4EBA\u77E5\u7F6A\uFF0C\u798F\u97F3\u4F7F\u4EBA\u56E0\u4FE1\u795E\u7684\u513F\u5B50\u800C\u6D3B.md"}',p={},e=t("<p>\u5F8B\u6CD5\u4F7F\u4EBA\u77E5\u7F6A\uFF0C\u798F\u97F3\u4F7F\u4EBA\u56E0\u4FE1\u795E\u7684\u513F\u5B50\u800C\u6D3B\u300A\u52A02:15 \u2013 3:14\u300B \u5E8F\u8BBA\uFF1A\u5F53\u4FDD\u7F57\u8BF4\uFF1A\u201C\u6211\u5DF2\u7ECF\u4E0E\u57FA\u7763\u540C\u9489\u5341\u5B57\u67B6\u3002\u73B0\u5728\u6D3B\u7740\u7684\uFF0C\u4E0D\u518D\u662F\u6211\uFF0C\u4E43\u662F\u57FA\u7763\u5728\u6211\u91CC\u9762\u6D3B\u7740\u3002\u201D\u8FD9\u53E5\u8BDD\u5E76\u4E0D\u662F\u5F3A\u8C03\u4ED6\u4FE1\u8036\u7A23\u4E4B\u540E\uFF0C\u52AA\u529B\u820D\u5DF1\u59D4\u8EAB\uFF0C\u8981\u4E3A\u4E3B\u6487\u5F03\u4E00\u5207\u7684\u7CBE\u795E\u3002\u4E43\u662F\u4ED6\u53D1\u73B0\u4E86\u4E00\u4E2A\u7EDD\u5BF9\u4E8B\u5B9E\uFF0C\u5C31\u662F\u5F53\u4ED6\u4FE1\u4E86\u8036\u7A23\u57FA\u7763\uFF0C\u4ED6\u5DF2\u7ECF\u518D\u4E5F\u65E0\u6CD5\u7528\u4E16\u754C\u7684\u6807\u51C6\u6216\u4EBA\u7684\u662F\u975E\u4E4B\u5FC3\u6765\u5224\u65AD\u81EA\u5DF1\u4E86\u3002\u56E0\u4E3A\u4ED6\u7684\u751F\u547D\u5DF2\u7ECF\u4E0D\u5C5E\u4E8E\u81EA\u5DF1\uFF0C\u4E43\u5C5E\u4E8E\u4E3B\uFF0C\u662F\u5DF2\u7ECF\u62E5\u6709\u57FA\u7763\u7684\u751F\u547D\u4E0E\u5E94\u8BB8\u5728\u4ED6\u8EAB\u4E0A\u4E86\u3002\u6240\u4EE5\uFF0C\u4ED6\u4ECA\u5929\u6D3B\u7740\uFF0C\u662F\u56E0\u4FE1\u57FA\u7763\u795E\u7684\u513F\u5B50\uFF08\u4E49\uFF0C\u7231\uFF0C\u540C\u5728\uFF0C\u5F15\u5BFC\uFF0C\u6210\u5C31\u2026\uFF09\u800C\u6D3B\uFF0C\u4E0D\u662F\u4EE5\u4EFB\u4F55\u4EBA\u7684\u6807\u51C6\u3001\u5F8B\u6CD5\u7684\u8981\u6C42\u3001\u6216\u662F\u81EA\u5DF1\u7684\u826F\u77E5\u3002\u7EB5\u7136\u6709\u65F6\u88AB\u8FC7\u72AF\u6240\u80DC\u6216\u662F\u884C\u7684\u4E0D\u5408\u5B9C\uFF0C\u4ED6\u4E5F\u53D1\u73B0\u4ED6\u4E0D\u80FD\u63A7\u544A\u81EA\u5DF1\uFF0C\u82E5\u662F\u884C\u5F97\u7F8E\u5584\u4E5F\u4E0D\u56E0\u81EA\u5DF1\u6240\u884C\u7684\u611F\u5230\u516C\u4E49\u81EA\u8C6A\uFF0C\u56E0\u4E3A\u4ED6\u7684\u751F\u547D\u5DF2\u4E0D\u5C5E\u4E8E\u81EA\u5DF1\u4E86\uFF01\u4ED6\u51E1\u4E8B\u552F\u6709\u8981\u56DE\u590D\u7684\u662F\u7231\u4E3B\u542C\u4ECE\u4E3B\u7684\u5FC3\uFF08\u795E\u513F\u5B50\u7684\u5FC3\uFF09\u5C31\u597D\u4E86\u3002\u8FD9\u4E5F\u5C31\u662F\u56E0\u4FE1\u79F0\u4E49\u7684\u5965\u79D8\u4E86\uFF01\u4ECA\u5929\u6709\u8BB8\u591A\u4FE1\u5F92\u4F3C\u4E4E\u77E5\u9053\u56E0\u4FE1\u79F0\u4E49\u7684\u9053\u7406\uFF0C\u4F46\u5728\u5FC3\u7075\u91CC\u5E76\u4E0D\u8BA4\u8BC6\u4E0E\u4F9D\u9760\u2018\u56E0\u4FE1\u79F0\u4E49\u2019\u7684\u751F\u547D\u3002\u4EBA\u603B\u662F\u5904\u5728\u4E00\u4E0B\u81EA\u5351\u53C8\u4E00\u4E0B\u9A84\u50B2\u6216\u4E00\u4E0B\u7070\u5FC3\u53C8\u4E00\u4E0B\u5174\u594B\u7684\u5149\u666F\uFF0C\u5FC3\u91CC\u53D7\u5230\u5404\u79CD\u4E16\u754C\u6216\u826F\u77E5\u7684\u6279\u5224\u3002\u8FD9\u662F\u56E0\u4E3A\u8FD8\u662F\u81EA\u5DF1\u5728\u6D3B\uFF08\u81EA\u5DF1\u5728\u8D1F\u8D23\u81EA\u5DF1\u7684\u751F\u547D\u4E0E\u6240\u505A\u7684\u4E00\u5207\uFF09\uFF0C\u7531\u59CB\u81F3\u7EC8\u6CA1\u6709\u53D1\u73B0\u81EA\u5DF1\u662F\u5DF2\u7ECF\u4E0E\u57FA\u7763\u540C\u9489\u5341\u5B57\u67B6\u4E86\u3002\u5176\u5B9E\uFF0C\u8FD9\u662F\u5FC3\u7075\u2018\u4E0D\u8BA4\u8BC6\u2019\u7684\u95EE\u9898\uFF0C\u5E76\u4E0D\u662F\u2018\u4E0D\u52AA\u529B\u2019\u7684\u95EE\u9898\u3002\u8FD9\u5982\u540C\u4E00\u4E2A\u5B69\u5B50\u4E0D\u8BA4\u8BC6\u81EA\u5DF1\u7684\u8EAB\u4EFD\u5C31\u662F\u5BB6\u91CC\u7684\u5C0F\u4E3B\u4EBA\uFF08\u6CA1\u6709\u5C0F\u4E3B\u4EBA\u7684\u5FC3\u7075\uFF09\uFF0C\u4F46\u4ED6\u5374\u52AA\u529B\u8981\u6D3B\u51FA\u90A3\u6837\u5B50\uFF0C\u52AA\u529B\u8981\u8BA8\u7236\u6BCD\u6B22\u6B23\uFF0C\u4F46\u8FD9\u662F\u77DB\u76FE\u865A\u5047\u7684\u3002\u4FDD\u7F57\u5728\u4ECA\u5929\u7684\u7ECF\u6587\u91CC\u5F3A\u8C03\u56E0\u4FE1\u79F0\u4E49\uFF08\u4E0E\u4E3B\u540C\u6B7B\u540C\u590D\u6D3B\uFF09\u7684\u5965\u79D8\uFF0C\u4EE5\u6B64\u63A8\u7FFB\u4E86\u72B9\u592A\u5F8B\u6CD5\u4E3B\u4E49\u8005\u3002\u4ED6\u4E5F\u8BC1\u660E\u2018\u4EE5\u4FE1\u800C\u6D3B\u2019\u672C\u662F\u4E0A\u5E1D\u8D77\u521D\u547C\u53EC\u4EBA\u7684\u7F8E\u610F\uFF1B\u82E5\u4E0D\u662F\u501F\u7740\u2018\u4FE1\u2019\uFF0C\u65E0\u6CD5\u5F97\u77E5\u795E\u7684\u6069\uFF1B\u82E5\u4E0D\u662F\u2018\u4FE1\u800C\u6765\u7684\u4E49\u2019\uFF0C\u5FC3\u7075\u8FD8\u662F\u5B58\u7559\u5728\u5B9A\u7F6A\u3001\u8BC5\u5492\u3001\u5211\u7F5A\u3001\u60E7\u6015\u7684\u72B6\u6001\uFF1B\u4E5F\u552F\u6709\u501F\u7740\u2018\u4FE1\u800C\u79F0\u4E49\u2019\u624D\u80FD\u770B\u89C1\u5723\u7075\u5927\u80FD\u7684\u4F5C\u4E3A\u3002</p><p>\u795E\u7684\u672C\u610F\u539F\u662F\u53EB\u4EBA\u4EE5\u300C\u4FE1\u800C\u542C\u4ECE\u795E\u300D\u6765\u6D3B\uFF0C\u4E5F\u4EE5\u6B64\u539F\u7406\u6765\u521B\u9020\u4EBA\u7684\u751F\u547D \u4FE1\u800C\u542C\u4ECE\u795E\u7684\u751F\u547D\u624D\u662F\u539F\u672C\u4E0A\u5E1D\u521B\u9020\u4EBA\u7684\u6837\u5B50\uFF0C\u4E5F\u4EE5\u6B64\u7BA1\u7406\u6CBB\u7406\u4E00\u5207</p><ul><li>\u4E0A\u5E1D\u8D77\u521D\u9020\u4E9A\u5F53\u590F\u5A03\uFF0C\u7528\u610F\u7EDD\u4E0D\u662F\u653E\u5728\u53EB\u4ED6\u4EEC\u9075\u5B88\u4E0D\u5403\u5584\u6076\u679C\u7684\u547D\u4EE4\uFF0C\u4E43\u662F\u8D50\u4ED6\u4EEC\u300C\u795E\u7684\u8BDD\u4E0E\u5723\u7075\u300D\uFF0C\u53EB\u4ED6\u4EEC\u76F8\u4FE1\u800C\u542C\u4ECE\u795E\u3002\u4E9A\u5F53\u4E0E\u590F\u5A03\u4EE5\u6B64\u663E\u51FA\u4E0A\u5E1D\u4E00\u5207\u7684\u8363\u8000\u3001\u5C0A\u8363\u3001\u80FD\u529B\u3001\u6743\u67C4\uFF1B\u540C\u6837\u7684\uFF0C\u4E0A\u5E1D\u547C\u53EC\u4E9A\u4F2F\u62C9\u7F55\uFF0C\u4ED6\u56E0\u6B64\u4FE1\u800C\u542C\u4ECE\u4E86\u3002\u795E\u5E76\u6CA1\u6709\u518D\u7ED9\u4ED6\u4EFB\u4F55\u7684\u8BEB\u547D\u5F8B\u6CD5\u6216\u8981\u8FBE\u5230\u7684\u6C34\u51C6\u3002\u6240\u4EE5\uFF0C\u4E9A\u4F2F\u62C9\u7F55\u7684\u4E00\u751F\u662F\u7EE7\u7EED\u4E3A\u5411\u4ED6\u663E\u660E\u7684\u8036\u548C\u534E\u7B51\u575B\uFF0C\u547C\u6C42\u7942\u7684\u540D\uFF0C\u8FD9\u5C31\u8BC1\u660E\u4ED6\u56E0\u4FE1\u79F0\u4E49\u4E86\uFF0C\u4ED6\u4FE1\u800C\u542C\u4ECE\u795E\u7684\u751F\u547D\u4E5F\u9884\u8868\u4E86\u5C06\u6765\u8981\u6765\u7684\u57FA\u7763\u3002\u6240\u4EE5\uFF0C\u5BF9\u6211\u4EEC\u8FD9\u5728\u57FA\u7763\u91CC\u7684\uFF0C\u795E\u8981\u56DE\u590D\u5728\u6211\u4EEC\u751F\u547D\u91CC\u7684\u662F\u4E00\u4E2A\u4FE1\u800C\u542C\u4ECE\u7684\u751F\u547D\u3002\u4E8B\u5B9E\u4E0A\uFF0C\u5C0A\u5B88\u5F8B\u6CD5\u51C6\u5219\u7684\u751F\u547D\u7EDD\u65E0\u6CD5\u6A21\u4EFF\u4FE1\u800C\u542C\u4ECE\u7684\u751F\u547D\u3002\u5F53\u4EBA\u56E0\u4FE1\u795E\u7684\u513F\u5B50\u800C\u6D3B\uFF0C\u4ED6\u6D3B\u51FA\u6765\u7684\u5C31\u662F\u57FA\u7763\u7684\u8363\u5F62\uFF0C\u662F\u5927\u80FD\u5927\u529B\u3002</li></ul><p>\u53EA\u6709\u201C\u4EE5\u4FE1\u4E3A\u672C\u201D\u7684\u4EBA\u624D\u80FD\u5728\u5FC3\u7075\u91CC\u4EAB\u53D7\u5168\u7136\u7684\u81EA\u7531\uFF08\u79F0\u4E49\uFF09\u300A2:15-16\uFF1B3:6-10\u300B</p><ul><li>\u798F\u97F3\u5C06\u4EBA\u751F\u547D\u91CC\u7684\u4E00\u4E2A\u4E8B\u5B9E\u663E\u660E\uFF1A\u300C\u7EAF\u7CB9\u662F\u501F\u7740\u4FE1\uFF0C\u4F7F\u4EBA\u5F97\u795E\u7EAF\u6B63\u7684\u4E49\u300D\u3002\u4E9A\u5F53\u590F\u5A03\u662F\u7EAF\u7CB9\u5F97\u4E86\u795E\u7684\u8BDD\u4E0E\u7075\u5728\u91CC\u5934\u800C\u5FC3\u4E2D\u65E0\u7455\u75B5\uFF1B\u4E9A\u4F2F\u62C9\u7F55\u662F\u56E0\u4FE1\u4E0A\u5E1D\uFF0C\u6240\u4EE5\u5F97\u5168\u7136\u79F0\u4E49\u7684\u5FC3\u7075\uFF1B\u6469\u897F\u662F\u4FE1\u4E86\uFF08\u9047\u89C1\uFF09\u4EE5\u8272\u5217\u7684\u4E0A\u5E1D\uFF0C\u4ECE\u6B64\u5BF9\u7942\u5766\u7136\u65E0\u60E7\u3002\u8FD9\u4E9B\u4EBA\u5FC3\u7075\u91CC\u5168\u7136\u7684\u79F0\u4E49\u90FD\u662F\u6765\u4E4B\u4FE1\uFF0C\u4E0E\u8BEB\u547D\u3001\u5F8B\u6CD5\u3001\u5178\u7AE0\u3001\u51C6\u5219\u3001\u6761\u4EF6\u3001\u80CC\u666F\u65E0\u5173\u3002\u6240\u4EE5\uFF0C\u8FD9\u662F\u539F\u672C\u795E\u501F\u4FE1\u5FC3\uFF08\u8BDD\u4E0E\u7075\uFF09\u4F4F\u5728\u4EBA\u5FC3\u4E2D\u7684\u5965\u79D8\uFF0C\u4F46\u4EBA\u5815\u843D\u4E4B\u540E\uFF0C\u65E0\u6CD5\u7406\u89E3\u8FD9\u751F\u547D\u7684\u5965\u79D8\uFF0C\u4EE5\u81F4\u9760\u5B97\u6559\uFF08\u4EBA\u627E\u795E\u7684\u5404\u79CD\u65B9\u5F0F\uFF09\u4E3A\u672C\u3002\u4F46\u6CA1\u6709\u4E00\u4E2A\u5B97\u6559\u7684\u6C34\u51C6\u80FD\u4F7F\u4EBA\u7684\u5FC3\u7075\u5168\u7136\u79F0\u4E49\u3002</li></ul><p>\u4EE5\u201C\u884C\u5F8B\u6CD5\u4E3A\u672C\u201D\u7684\u5FC3\u7075\u662F\u53D7\u8BC5\u5492\u7684\uFF0C\u4ED6\u4ECD\u5904\u4E8E\u5974\u4EC6\u7684\u5FC3\uFF0C\u6D3B\u5728\u663C\u591C\u88AB\u63A7\u544A\u4E5F\u63A7\u544A\u4EBA\u7684\u72B6\u6001\u300A3:11\u300B</p><ul><li>\u4E8B\u5B9E\u4E0A\uFF0C\u82E5\u4E00\u4E2A\u4EBA\u8FD8\u672A\u4E0E\u57FA\u7763\u540C\u6B7B\u540C\u6D3B\uFF0C\u4ED6\u7684\u5FC3\u7075\u662F\u81EA\u52A8\u7559\u5728\u201C\u884C\u5F8B\u6CD5\u4E3A\u672C\u201D\u7684\u72B6\u6001\u3002\u65E0\u8BBA\u4ED6\u591A\u5584\u826F\u6216\u6709\u9053\u5FB7\uFF0C\u4ED6\u91CC\u5934\u7684\u662F\u975E\u4E4B\u5FC3\u662F\u4E00\u76F4\u63A7\u544A\u4ED6\uFF0C\u4E5F\u65AD\u5B9A\u4ED6\u7684\u4E00\u4E3E\u4E00\u52A8\u3002\u56E0\u4E3A\u4EBA\u7684\u662F\u975E\u4E4B\u826F\u5FC3\u65E0\u6CD5\u53EB\u4ED6\u770B\u89C1\u795E\uFF0C\u6240\u4EE5\u4ED6\u7684\u7075\u9B42\u8FD8\u5728\u9B54\u9B3C\u7684\u7BA1\u8F96\u4E4B\u4E0B\u3002\u6240\u4EE5\uFF0C\u5018\u82E5\u4E00\u4E2A\u4EBA\u4E0D\u8BA4\u8BC6\u201C\u4FE1\u800C\u542C\u4ECE\u795E\u201D\u4E4B\u5965\u79D8\uFF0C\u5374\u4E13\u4EE5\u5F8B\u6CD5\uFF08\u662F\u975E\uFF09\u7684\u6784\u601D\u6765\u751F\u6D3B\u3001\u884C\u4E8B\u4E3A\u4EBA\u3001\u5904\u4E8B\u5F85\u4EBA\uFF0C\u5F88\u5FEB\u4F1A\u770B\u89C1\u4ED6\u7684\u8EAB\u5FC3\u7075\u3001\u751F\u6D3B\u3001\u4EBA\u9645\u90FD\u4F1A\u53D7\u5230\u8BC5\u5492\u3002\u4ED6\u4F1A\u9996\u5148\u8D8A\u8FDC\u79BB\u771F\u795E\uFF0C\u4E4B\u540E\u5FC3\u5FF5\u6000\u610F\u5931\u53BB\u559C\u4E50\uFF08\u751A\u81F3\u5FE7\u90C1\uFF09\uFF0C\u7136\u540E\u4ED6\u5468\u56F4\u7684\u4EBA\u4E5F\u53D7\u4ED6\u538B\u8FEB\u6346\u7ED1\uFF0C\u7B49\u3002</li></ul><p>\u300C\u5F8B\u6CD5\u7684\u529F\u7528\u300D\u662F\u628A\u4EBA\u6307\u5411\u57FA\u7763\u7684\u798F\u97F3\uFF0C\u4F7F\u4EBA\u80FD\u5355\u5355\u900F\u8FC7\u2018\u56E0\u4FE1\u57FA\u7763\u2019\u6D3B\u51FA\u795E\u7684\u5927\u80FD \u5F8B\u6CD5\u7684\u7B2C\u4E00\u4E2A\u529F\u7528\u662F\u53EB\u4EBA\u300C\u77E5\u7F6A \u300D\uFF0C\u4EE5\u81F4\u624D\u80FD\u8BA4\u8BC6\u795E\u65E0\u9650\u7684\u300C\u6069\u5178\u300D</p><ul><li>\u9996\u5148\u6211\u4EEC\u8981\u8BA4\u8BC6\uFF1A\u4E0A\u5E1D\u7684\u5FC3\u610F\u662F\u201C\u6069\u5178\u201D\uFF0C\u5E76\u4E0D\u662F\u201C\u5F8B\u6CD5\u201D\u3002\u6240\u4EE5\uFF0C\u5723\u7075\u6574\u4E2A\u7684\u505A\u5DE5\u662F\u5728\u4E8E\u4EBA\u7684\u5FC3\u7075\u6709\u5426\u6069\u5178\uFF0C\u5E76\u4E0D\u662F\u56E0\u4EBA\u7684\u5FC3\u7075\u6709\u5426\u5F8B\u6CD5\u3002\u5018\u82E5\u4EBA\u7684\u5FC3\u4E0D\u80FD\u56E0\u57FA\u7763\u7684\u727A\u7272\u800C\u88AB\u6069\u611F\uFF0C\u5723\u7075\u65E0\u6CD5\u523B\u5370\u3001\u611F\u52A8\u3001\u5145\u6EE1\u4ED6\u3002\u90A3\u4E48\uFF0C\u4EBA\u65E0\u6CD5\u8BA4\u8BC6\u795E\u7684\u6069\u5178\u7684\u4E3B\u8981\u7406\u7531\u662F\u56E0\u4E3A\u4E0D\u77E5\u7F6A\uFF08\u4E0D\u80FD\u770B\u89C1\u81EA\u5DF1\u7684\u8BC5\u5492\u800C\u7EDD\u671B\uFF09\u3002\u6240\u4EE5\uFF0C\u795E\u4E25\u8C28\u7684\u8BEB\u547D\u5F8B\u6CD5\u662F\u4E3A\u4E86\u53EB\u4EBA\u8BA4\u77E5\u81EA\u5DF1\u7684\u7F6A\u4E0E\u56F0\u82E6\u6D41\u79BB\u7684\u5149\u666F\u3002\u8FD9\u5C31\u662F\u300A\u7F577\uFF1A13\u300B\uFF1A\u4F46\u7F6A\u501F\u7740\u90A3\u826F\u5584\u7684\uFF08\u5F8B\u6CD5\uFF09\u53EB\u6211\u6B7B\uFF0C\u5C31\u663E\u51FA\uFF08\u6211\u6240\u884C\u7684\u4E00\u5207\uFF09\u771F\u662F\u7F6A\u3002\u53EB\u7F6A\u56E0\u7740\u8BEB\u547D\uFF08\u5F8B\u6CD5\uFF09\u66F4\u663E\u51FA\u662F\u6076\u6781\u4E86\u3002</li></ul><p>\u56E0\u5F8B\u6CD5\u77E5\u7F6A\u7684\u4EBA\u6DF1\u611F\u57FA\u7763\u7684\u6069\u5178\uFF0C\u4F7F\u4ED6\u51E1\u4E8B\u80FD\u9886\u53D7\u7231\uFF0C\u4E5F\u4EE5\u7231\u51FA\u53D1</p><ul><li>\u8BB8\u591A\u4FE1\u5F92\u662F\u5728\u9053\u7406\u4E0A\u77E5\u9053\u795E\u7684\u6069\u5178\uFF0C\u4F46\u5FC3\u7075\u6CA1\u6709\u6DF1\u611F\u4E3B\u7684\u6069\u5178\uFF0C\u6240\u4EE5\u5723\u7075\u7684\u80FD\u529B\u5728\u4ED6\u7684\u8EAB\u4E0A\u4E0D\u4EA7\u751F\u3002\u5F88\u591A\u4FE1\u5F92\u662F\u4E00\u72AF\u9519\u65F6\uFF0C\u4ED6\u5FC3\u7075\u7684\u53CD\u5E94\u662F\u201C\u54CE\u5440\uFF0C\u6211\u53C8\u72AF\u9519\u4E86\uFF01\u201D\uFF0C\u7136\u540E\u81EA\u8D23\u5185\u759A\u3002\u90A3\u662F\u56E0\u4E3A\u4ED6\u8FD8\u6CA1\u6709\u4E0E\u8036\u7A23\u57FA\u7763\u540C\u9489\u5728\u5341\u5B57\u67B6\uFF0C\u6240\u4EE5\u8FD8\u5728\u6309\u662F\u975E\u4E4B\u5FC3\u770B\u81EA\u5DF1\u3002\u4F46\u5DF2\u7ECF\u5168\u7136\u77E5\u7F6A\u7684\u4EBA\u867D\u53C8\u4F1A\u72AF\u9519\uFF0C\u4F46\u4ED6\u7684\u5FC3\u7075\u662F\uFF1A\u201C\u4E3B\uFF0C\u6211\u611F\u8C22\u4F60\uFF0C\u56E0\u4E3A\u53C8\u660E\u767D\u4E86\u4F60\u7684\u6069\u5178\u4F55\u7B49\u6D69\u5927\u201D\uFF0C\u4EE5\u81F4\u5FC3\u91CC\u66F4\u7231\u795E\u7231\u4EBA\u3002\u6240\u4EE5\uFF0C\u524D\u8005\u662F\u8FD8\u6709\u9A84\u50B2\u4E4B\u5FC3\uFF08\u9760\u81EA\u5DF1\u7684\u4E49\uFF09\uFF0C\u540E\u8005\u662F\u5168\u7136\u77E5\u7F6A\u800C\u5351\u5FAE\uFF08\u9760\u57FA\u7763\u7684\u4E49\uFF09\uFF0C\u6240\u4EE5\u6069\u5178\u4E0E\u7231\u4F1A\u5728\u4ED6\u91CC\u5934\u7EE7\u7EED\u589E\u957F\u3002</li></ul><p>\u5FC3\u4E2D\u4EAB\u6709\u57FA\u7763\u7684\u7231\u7684\u4EBA\u624D\u80FD\u8BA4\u8BC6\u5F8B\u6CD5\u7684\u7CBE\u4E49\uFF0C\u5728\u4ED6\u5FC3\u4E2D\u6240\u5224\u65AD\u7684\u90FD\u5E26\u6709\u300C\u4E0A\u5E1D\u7684\u516C\u4E49\u4E0E\u6148\u7231\u300D</p><ul><li>\u795E\u7684\u5F8B\u6CD5\u662F\u5723\u6D01\u7684\u3001\u516C\u4E49\u7684\u3001\u826F\u5584\u7684\u300A\u7F577\uFF1A12\u300B\uFF0C\u4F46\u662F\u7EDD\u65E0\u6CD5\u5411\u4EBA\u63CF\u8FF0\u795E\u7684\u672C\u5FC3\u610F\u3002\u552F\u6709\u4EBA\u5F97\u7740\u4E86\u795E\u513F\u5973\u7684\u5FC3\uFF0C\u624D\u80FD\u660E\u767D\u5F8B\u6CD5\u4E0E\u8BEB\u547D\u7684\u7CBE\u4E49\uFF0C\u6D3B\u51FA\u795E\u7684\u516C\u4E49\u4E0E\u6148\u7231\u3002\u6240\u4EE5\uFF0C\u4E00\u4E2A\u4FE1\u5F92\u8FD8\u672A\u5F97\u7740\u513F\u5973\u7684\u5FC3\u65F6\uFF0C\u795E\u7684\u5F8B\u6CD5\u65E0\u6CD5\u6210\u4E3A\u4ED6\u5FC3\u4E2D\u5224\u65AD\u51E1\u4E8B\u7684\u667A\u6167\uFF0C\u53CD\u800C\u7528\u4E8E\u63A7\u544A\u81EA\u5DF1\u4E0E\u4ED6\u4EBA\u3002\u5982\u540C\uFF0C\u4E00\u4E2A\u597D\u7684\u6CD5\u5B98\u662F\u4ECE\u4ED6\u201C\u5FC3\u91CC\u7684\u5224\u51B3\u201D\u770B\u51FA\u6765\uFF0C\u5E76\u4E0D\u662F\u4ECE\u4ED6\u6B7B\u677F\u6309\u7740\u201C\u5BAA\u6CD5\u7684\u5224\u51B3\u201D\u3002\u518D\u5C31\u662F\u6211\u4EEC\u4E5F\u8981\u660E\u767D\uFF1A\u795E\u7684\u7231\u662F\u542B\u6709\u7942\u7684\u667A\u6167\u4E0E\u516C\u4E49\uFF0C\u6240\u4EE5\u6211\u4EEC\u4E5F\u4E0D\u80FD\u4E3A\u4E86\u5F3A\u8C03\u6069\u5178\u800C\u8BB2\u6069\u5178\uFF0C\u4E43\u662F\u660E\u767D\u795E\u770B\u51E1\u4E8B\u7684\u5FC3\u800C\u5E26\u51FA\u6069\u5178\u4E0E\u7231\u3002</li></ul><p>\u65E2\u7136\u9760\u5723\u7075\u5165\u95E8\uFF0C\u5C31\u5F53\u9760\u5723\u7075\u6210\u5168</p><ul><li>\u4EBA\u5BF9\u5723\u7075\u6B63\u786E\u7684\u8BA4\u8BC6\u662F\u6765\u4E4B\u5BF9\u57FA\u7763\u798F\u97F3\u6B63\u786E\u7684\u8BA4\u8BC6\uFF0C\u6240\u4EE5\u5728\u8BA4\u8BC6\u798F\u97F3\u4E0A\u300C\u7EAF\u6B63\u300D\u4E0E\u300C\u6709\u6839\u6709\u57FA\u300D\u662F\u8BA4\u8BC6\u4E0E\u4F9D\u9760\u5723\u7075\u7684\u5173\u952E\u3002 \u5723\u7075\u4F7F\u4EBA\u56E0\u542C\u4FE1\u57FA\u7763\u7684\u798F\u97F3\u800C\u5F97\u7740\u300C\u5B8C\u6574\u7684\u81EA\u7531\u300D</li><li>\u4EBA\u56E0\u5FC3\u91CC\u5F97\u7740\u4E86\u57FA\u7763\u7684\u4E49\uFF0C\u4E0D\u518D\u53D7\u2018\u884C\u4E0D\u884C\u5F97\u5230\u5F8B\u6CD5\u2019\u7684\u8F96\u5236\uFF0C\u6240\u4EE5\u5FC3\u91CC\u6709\u7EDD\u7136\u7684\u81EA\u7531\u3002\u8FD9\u7B49\u81EA\u7531\u5C31\u662F\u57FA\u7763\u4FE1\u4EF0\u7684\u80FD\u529B\uFF0C\u4E0D\u662F\u6765\u4E4B\u81EA\u5DF1\u505A\u5F97\u5BF9\u3001\u505A\u5F97\u597D\u3001\u5F97\u4EBA\u8BA4\u540C\u559C\u60A6\u3001\u7B49\uFF0C\u4E43\u662F\u4ECE\u57FA\u7763\u800C\u6765\u7684\u2018\u4F55\u65F6\u3001\u4F55\u5904\u3001\u4F55\u5149\u666F\u90FD\u80FD\u9762\u5BF9\u795E\u7684\u5FC3\u2019\u3002\u518D\u8005\uFF0C\u4EBA\u7684\u5FC3\u4E2D\u518D\u4E0D\u542B\u7740\u201C\u6015\u4F1A\u505A\u9519\u6216\u662F\u6015\u540E\u679C\u201D\u7684\u610F\u5FF5\uFF0C\u4E43\u662F\u6309\u7740\u51E1\u4E8B\u5BFB\u6C42\u795E\u7684\u7F8E\u610F\u884C\u3002\u4E00\u4E2A\u4EBA\u82E5\u8981\u7ECF\u5386\u5723\u7075\u7684\u80FD\u529B\uFF0C\u5FC5\u8981\u8BA4\u8BC6\u8FD9\u7B49\u5FC3\u7075\u3002</li></ul><p>\u5723\u7075\u4F7F\u4EBA\u542C\u89C1\u800C\u968F\u4ECE\u795E\u7684\u58F0\u97F3\uFF0C\u4E5F\u7528\u795E\u8FF9\u5F02\u80FD\u6765\u8BC1\u5B9E\u6240\u4FE1\u4ECE\u7684\u9053</p><ul><li>\u5F88\u591A\u65F6\u5019\uFF0C\u6211\u4EEC\u7684\u4FE1\u4EF0\u4E00\u76F4\u5904\u5728\u4E00\u79CD\u2018\u89C2\u5FF5\u6027\u2019\u6216\u2018\u77E5\u9053\u5374\u505A\u4E0D\u5230\u2019\u7684\u72B6\u6001\u3002\u4E3B\u8981\u7684\u7406\u7531\u662F\u56E0\u4E3A\u6211\u4EEC\u6B63\u5728\u88AB\u5404\u79CD\u4E16\u754C\u6216\u4EBA\u7684\u610F\u5FF5\u5E72\u6270\uFF0C\u6240\u4EE5\u6CA1\u6709\u80FD\u529B\u968F\u4ECE\u4E3B\u3002\u5018\u82E5\u4E00\u4E2A\u4EBA\u542C\u89C1\u795E\u7684\u58F0\u97F3\uFF0C\u4E0A\u5934\u7684\u529B\u91CF\u9A6C\u4E0A\u4E34\u5230\u4ED6\uFF0C\u4F7F\u4ED6\u6709\u80FD\u529B\u987A\u670D\uFF0C\u4E5F\u5FC5\u770B\u89C1\u4E3B\u7684\u6210\u5C31\u3002 \u2460 \u5723\u7075\u4F7F\u4EBA\u6709\u5FC3\u8981\u5168\u90E8\u62C6\u6BC1\uFF0C\u91CD\u65B0\u5EFA\u9020\uFF0C\u53EB\u81EA\u5DF1\u5FC3\u4E2D\u7684\u5404\u610F\u5FF5\u964D\u670D\u4E8E\u795E\u7684\u8BDD\u3002 \u2461 \u5723\u7075\u4F7F\u4EBA\u56E0\u770B\u89C1\u795E\u6240\u8981\u6210\u5C31\u4E4B\u4E8B\uFF0C\u6240\u4EE5\u521A\u5F3A\u58EE\u80C6\u7684\u968F\u4ECE/\u987A\u670D\u3002 \u2462 \u5723\u7075\u53EB\u4EBA\u56E0\u542C\u4ECE\u795E\u800C\u5F97\u7740\u786E\u5B9A\u7684\u5FC3\uFF0C\u6240\u4EE5\u51E1\u4E8B\u5411\u795E\u5766\u7136\u65E0\u60E7\uFF0C\u65E0\u8BBA\u6C42\u4EC0\u4E48\uFF0C\u5C31\u5FC5\u5F97\u7740\u3002 \u2463 \u5723\u7075\u7528\u795E\u8FF9\u5F02\u80FD\u6765\u8BC1\u5B9E\u6240\u4FE1\u4ECE\u7684\u9053\u3002</li></ul><p>\u6240\u4EE5\uFF0C\u4FE1\u5F92\u4E00\u751F\u7684\u52AA\u529B\u662F\u4E0D\u65AD\u8981\u5F03\u7EDD\u4EBA\u672C\uFF08\u4EBA\u7684\u6837\u5F0F\uFF09\uFF0C\u800C\u53D7\u5723\u7075\u5F15\u5BFC\uFF08\u57FA\u7763\u7684\u6837\u5F0F\uFF09</p><ul><li>\u7267\u5E08\u4E2A\u4EBA\u7684\u7075\u91CC\u7ECF\u5386\uFF1A\u6211\u662F\u5E38\u5E26\u7740\u4E00\u4E2A\u773C\u5149\uFF0C\u5C31\u662F\u201C\u4E0A\u5E1D\u662F\u51E1\u4E8B\u4E0E\u6211\u540C\u5728\uFF0C\u7942\u662F\u7231\u6211\uFF0C\u4E3A\u6211\u820D\u5DF1\uFF0C\u800C\u7942\u5728\u6211\u91CC\u5934\u6B7B\u800C\u590D\u6D3B\u7684\u5927\u80FD\u4F7F\u6211\u80FD\u5F97\u80DC\u4E00\u5207\u3002\u201D\u6240\u4EE5\uFF0C\u82E5\u6211\u5728\u4EC0\u4E48\u4E8B\u4E0A\u6211\u53D7\u63A7\u544A\u3001\u62C5\u5FE7\u60E7\u6015\u3001\u6216\u662F\u65E0\u80FD\u65E0\u529B\u3001\u6211\u5FC3\u91CC\u7EDD\u5BF9\u7684\u8BA4\u8BC6\u662F\uFF1A\u5728\u90A3\u65B9\u9762\u6211\u8FD8\u6CA1\u6709\u987A\u7740\u5723\u7075\u800C\u884C\uFF08\u770B\u4E0D\u89C1\u7942\u91CC\u5934\u7684\u81EA\u7531\u4E0E\u6709\u80FD\u529B\u7684\u5E26\u9886\uFF09\uFF0C\u53CD\u800C\u662F\u591A\u987A\u7740\u81EA\u5DF1\u7684\u610F\u601D\u800C\u884C\u3002\u6240\u4EE5\uFF0C\u6BCF\u5F53\u6211\u5982\u6B64\u5BDF\u89C9\u65F6\uFF0C\u5C31\u660E\u767D\u5F53\u5426\u8BA4\u81EA\u5DF1\uFF0C\u601D\u60F3\u57FA\u7763\u73B0\u5728\u8981\u600E\u6837\u900F\u8FC7\u6211\u6D3B\uFF08\u884C\u4E8B\uFF09\uFF0C\u5C31\u5FC5\u201C\u60F3\u8D77\u7942\u7684\u8BDD\uFF0C\u660E\u767D\u7942\u7684\u7F8E\u610F\uFF0C\u5FC3\u91CC\u6709\u529B\u91CF\u8DDF\u4ECE\u3002\u201D\u300A\u7EA614\uFF1A26-27\u300B</li></ul>",19),a=[e];function u(o,s,r,c,n,d){return i(),l("div",null,a)}var T=_(p,[["render",u]]);export{m as __pageData,T as default};
