import{_ as l,c as _,o as i,a as p}from"./app.618cde25.js";const V='{"title":"","description":"","frontmatter":{},"headers":[],"relativePath":"galatians/5.\u795E\u7684\u672C\u610F\u539F\u662F\u8981\u9AD8\u4E3E\u57FA\u7763\u4E0E\u513F\u5B50\u4E4B\u5FC3\u7075.md"}',t={},e=p("<p>\u795E\u7684\u672C\u610F\u539F\u662F\u8981\u9AD8\u4E3E\u57FA\u7763\u4E0E\u513F\u5B50\u4E4B\u5FC3\u7075\u300A\u52A03:15 \u2013 4:7\u300B \u5E8F\u8BBA\uFF1A\u5176\u5B9E\uFF0C\u4E0A\u5E1D\u7531\u59CB\u81F3\u7EC8\u4E0D\u662F\u8981\u5B9A\u4EBA\u7684\u7F6A\uFF0C\u4E43\u662F\u8981\u4EBA\u63A5\u53D7\u57FA\u7763\u7684\u6069\u5178\u3002\u795E\u7684\u5FC3\u610F\u4E0D\u5728\u4E8E\u5341\u8BEB\u547D\uFF0C\u4E43\u662F\u501F\u7740\u5341\u8BEB\u547D\u5C06\u4EBA\u7275\u5F15\u5230\u795E\u7684\u6069\u5178\u4E0E\u7231\u9762\u524D\u3002\u6240\u4EE5\uFF0C\u4E00\u4E2A\u4EBA\u4E0D\u72AF\u7F6A\u5E76\u4E0D\u80FD\u5F97\u795E\u7684\u559C\u60A6\uFF0C\u4ED6\u552F\u6709\u56E0\u4FE1\u800C\u6D3B\u624D\u80FD\u8499\u795E\u559C\u60A6\u3002\u795E\u900F\u8FC7\u6211\u4EEC\u8EAB\u4E0A\u8981\u663E\u660E\u7684\u8363\u8000\uFF0C\u5E76\u4E0D\u662F\u201C\u6837\u6837\u90FD\u505A\u5F97\u5BF9\u201D\u7684\u8363\u8000\uFF0C\u4E43\u662F\u201C\u51E1\u4E8B\u542C\u4ECE\u5723\u7075\u201D\u7684\u8363\u8000\u3002\u56E0\u6B64\uFF0C\u795E\u5FC5\u987B\u501F\u5F8B\u6CD5\u4F7F\u4EBA\u77E5\u7F6A\u3002\u77E5\u7F6A\u4E4B\u4EBA\u624D\u80FD\u9886\u53D7\u6069\u5178\uFF0C\u4EE5\u7231\u795E\u7231\u4EBA\u7684\u5FC3\uFF08\u5723\u7075\uFF09\u6765\u6D3B\u3002\u8FD9\u5C31\u662F\u56E0\u4FE1\u79F0\u4E49\u7684\u798F\u97F3\u4E86\u3002\u6240\u4EE5\uFF0C\u4E0D\u8BA4\u8BC6\u8FD9\u798F\u97F3\u7684\u4EBA\uFF0C\u4ED6\u4EEC\u81EA\u7136\u5904\u5728\u5F8B\u6CD5\u4E4B\u4E0B\uFF0C\u6309\u7740\u662F\u975E\u4E4B\u5FC3\u6765\u6D3B\uFF08\u6216\u662F\u4EE5\u4EBA\u6240\u5236\u9020\u7684\u5B97\u6559\u4E3A\u6807\u51C6\uFF09\uFF0C\u8FD9\u5C31\u8BC1\u660E\u4ED6\u4EEC\u662F\u5904\u4E8E\u5974\u4EC6\u7684\u5149\u666F\uFF0C\u5728\u65E0\u77E5\u65E0\u80FD\u91CC\u751F\u6D3B\u3002\u4F46\u4ECA\u5929\uFF0C\u8BB8\u591A\u4FE1\u4E86\u4E3B\u7684\u4EBA\uFF0C\u5BF9\u201C\u56E0\u4FE1\u79F0\u4E49\u201D\u7684\u7CBE\u4E49\u4E0D\u7406\u89E3\uFF0C\u867D\u63A5\u53D7\u4E86\u57FA\u7763\u4E3A\u4E2A\u4EBA\u7684\u6551\u4E3B\uFF0C\u4F46\u5FC3\u4E2D\u7684\u5404\u610F\u5FF5\u5E76\u4E0D\u662F\u6765\u4E4B\u6216\u51FA\u4E8E\u798F\u97F3\uFF0C\u4E43\u662F\u4EBA\u7684\u662F\u975E\u4E4B\u5FC3\u3002\u8FD9\u6837\u7684\u4FE1\u5F92\u7247\u9762\u7684\u7406\u89E3\u4E86\u798F\u97F3\uFF0C\u4EE5\u81F4\u65E0\u6CD5\u6D3B\u5728\u56E0\u4FE1\u79F0\u4E49\u7684\u5FC3\u7075\uFF0C\u53CD\u5012\u5974\u4EC6\u7684\u5FC3\u7075\u3002\u8BD5\u95EE\uFF1A\u4E3A\u4F55\u4E00\u4E2A\u4EBA\u771F\u5B9E\u7684\u300C\u77E5\u7F6A\u300D\u3001\u63A5\u53D7\u57FA\u7763\u300C\u6069\u5178\u300D\u3001\u9886\u4F1A\u300C\u7231\u795E\u7231\u4EBA\u4E4B\u5FC3\u300D\u3001\u300C\u987A\u7740\u5723\u7075\u800C\u6D3B\u300D\u662F\u8FD9\u4E48\u91CD\u8981\uFF1F\u8FD9\u798F\u97F3\u4E3A\u4F55\u975E\u8981\u8FDB\u5165\u4EBA\u7684\u4E2D\u5FC3\uFF0C\u904D\u6EE1\u4ED6\u7684\u5FC3\u6000\u610F\u5FF5\uFF0C\u5374\u4E0D\u80FD\u9A6C\u864E\u8349\u7387\uFF1F\u8FD9\u662F\u56E0\u4E3A\u6574\u4E2A\u5B87\u5B99\u4E07\u6709\u7684\u4E2D\u5FC3\u4E0E\u6574\u4E2A\u7075\u754C\u7684\u8FD0\u884C\u90FD\u662F\u4E3A\u4E86\u9AD8\u4E3E\u8FD9\u798F\u97F3\u3002\u6240\u4EE5\uFF0C\u5F53\u4E00\u4E2A\u4EBA\u7684\u5FC3\u7075\u8BA4\u8BC6\u8FD9\u798F\u97F3\uFF0C\u800C\u6309\u7740\u8FD9\u798F\u97F3\u6765\u601D\u60F3\u3001\u5224\u65AD\u3001\u751F\u6D3B\uFF0C\u4ED6\u7684\u5BB6\u5EAD\u3001\u5730\u533A\u3001\u65F6\u4EE3\u662F\u4EE5\u4ED6\u4E3A\u4E2D\u5FC3\u6765\u8FD0\u884C\u3002\u6240\u4EE5\uFF0C\u50CF\u4FDD\u7F57\u8FD9\u6837\u7684\u4EBA\uFF0C\u4ED6\u662F\u6CA1\u529E\u6CD5\u88AB\u9690\u85CF\uFF1B\u4ED6\u7684\u7977\u544A\u662F\u6CA1\u529E\u6CD5\u4E0D\u8499\u5E94\u5141\uFF1B\u4ED6\u6240\u4F20\u7684\u798F\u97F3\u662F\u6CA1\u529E\u6CD5\u88AB\u62B9\u6740\u3002\u54EA\u6015\u72B9\u592A\u4EBA\u3001\u5916\u90A6\u4EBA\u3001\u5F02\u6559\u5F92\u3001\u653F\u6CBB\u4EBA\u5458\u3001\u751A\u81F3\u8FDE\u5927\u81EA\u7136\u7684\u98CE\u6D6A\u4E5F\u8D77\u6765\u654C\u6321\u4ED6\uFF0C\u8FD9\u662F\u53CD\u800C\u53EB\u798F\u97F3\u66F4\u5FEB\u66F4\u6709\u80FD\u529B\u7684\u88AB\u4F20\u626C\uFF0C\u4F7F\u66F4\u591A\u7684\u4EBA\u542C\u89C1\u798F\u97F3\uFF0C\u66F4\u591A\u7684\u95E8\u5F92\u88AB\u5174\u8D77\u3002\u5404\u4F4D\uFF0C\u8FD9\u5C31\u662F\u798F\u97F3\u7684\u5927\u80FD\u4E86\uFF01\u4ECA\u5929\uFF0C\u6211\u4EEC\u552F\u6709\u5728\u5FC3\u7075\u4EAB\u53D7\u8FD9\u798F\u97F3\u7684\u5927\u80FD\uFF0C\u624D\u80FD\u770B\u89C1\u6240\u6709\u7684\u95EE\u9898\u8F6C\u4E3A\u795D\u798F\u3002</p><p>\u795E\u7684\u672C\u610F\u539F\u662F\u8981\u5728\u4EBA\u7684\u5FC3\u7075\u91CC\u9AD8\u4E3E\u57FA\u7763 \u867D\u7136\u4EBA\u7684\u72AF\u7F6A\u52A0\u589E\uFF0C\u5F8B\u6CD5\u4E0E\u4EBA\u672C\u4E3B\u4E49\u4E5F\u8DDF\u7740\u52A0\u589E\uFF0C\u4F46\u4ECE\u795E\u800C\u6765\u7684\u6551\u6069\u662F\u4ECE\u6765\u6CA1\u6709\u6539\u53D8</p><ul><li>\u4EBA\u662F\u90FD\u88AB\u4E16\u754C\u3001\u73AF\u5883\u3001\u60C5\u7EEA\u7275\u5F15\u3002\u4E00\u4E2A\u4EBA\u8FD8\u672A\u8BA4\u8BC6\u57FA\u7763\u4E4B\u524D\uFF0C\u8FD9\u4E16\u754C\u8981\u600E\u6837\u5851\u9020\u4ED6\uFF0C\u4ED6\u5C31\u6210\u4E3A\u600E\u6837\u7684\u4EBA\u3002\u82E5\u751F\u6765\u662F\u72B9\u592A\u4EBA\uFF0C\u5C31\u88AB\u72B9\u592A\u4EBA\u7684\u6212\u5F8B\u5851\u9020\uFF1B\u82E5\u751F\u957F\u4E3A\u534E\u4EBA\uFF0C\u5C31\u88AB\u534E\u4EBA\u7684\u98CE\u4FD7\u6587\u5316\u5851\u9020\uFF1B\u82E5\u751F\u957F\u5F53\u4E2D\u88AB\u67D0\u4E9B\u601D\u60F3\u4FE1\u5FF5\u611F\u67D3\uFF0C\u5C31\u6E10\u6E10\u5982\u6B64\u7684\u88AB\u5851\u9020\uFF1B\u82E5\u662F\u53D7\u5230\u6709\u4E9B\u4EBA\u6216\u4E8B\u60C5\u7684\u51B2\u51FB\uFF0C\u4E5F\u4F1A\u5728\u90A3\u65B9\u9762\u53D7\u5230\u6D88\u6781\u7684\u5851\u9020\u3002\u6240\u4EE5\uFF0C\u4EBA\u7684\u98CE\u4FD7\u3001\u6587\u5316\u3001\u5B66\u95EE\u3001\u7ECF\u5386\u3001\u9700\u6C42\u3001\u5F8B\u6CD5\u662F\u7EE7\u7EED\u8981\u52A0\u589E\u3002\u4F46\u867D\u662F\u5982\u6B64\uFF0C\u4E0A\u5E1D\u9020\u4EBA\u7684\u539F\u7406\u6CA1\u6709\u6539\u53D8\uFF0C\u4E0A\u5E1D\u7684\u6551\u6069\u4E5F\u6CA1\u6709\u6539\u53D8\u3002\u4EBA\u552F\u6709\u770B\u89C1\u795E\u540C\u5728\u624D\u80FD\u5E78\u798F\u7F8E\u6EE1\uFF0C\u4E5F\u552F\u6709\u56E0\u4FE1\u795E\u513F\u5B50\u624D\u80FD\u79F0\u4E49\u3002\u5728\u52A0\u62C9\u592A\u4E66\u8BF4\u5230\uFF1A\u795E\u867D\u7ED9\u4E86\u4EE5\u8272\u5217\u4EBA\u5F8B\u6CD5\uFF08\u4E3A\u4E86\u4FDD\u62A4\u6574\u4E2A\u7FA4\u4F53\uFF09\uFF0C\u4F46430\u5E74\u524D\u7ED9\u4E9A\u4F2F\u62C9\u7F55\u7684\u5E94\u8BB8\u7EC8\u7A76\u662F\u5982\u6B64\uFF0C\u4E00\u76F4\u5230\u4ECA\u65E5\u4E0D\u53D8\u3002</li></ul><p>\u57FA\u7763\u4EE3\u8868\u56DB\u6837\u5173\u952E\u7684\u771F\u7406 \u2460 \u57FA\u7763\u4EE3\u8868\u8BC5\u5492\u4E0E\u795D\u798F\u7684\u7EDD\u5BF9\u6807\u51C6\u3002\u9664\u4E86\u900F\u8FC7\u57FA\u7763\u4EE5\u5916\uFF0C\u6CA1\u6709\u4EBA\u80FD\u8FDB\u5230\u6C38\u751F\u4E0E\u795D\u798F\u3002 \u2461 \u57FA\u7763\u4EE3\u8868\u4ECE\u795E\u800C\u6765\u7684\u6551\u6069\uFF08\u79F0\u4E49\uFF09\u3002\u8FD9\u6551\u6069\u7EDD\u5BF9\u6CA1\u6709\u52A0\u51CF\u4EFB\u4F55\u4EBA\u7684\u4EEA\u6587\uFF0C\u4E5F\u6CA1\u6709\u53C2\u6742\u65E7\u7EA6\u7684\u5F8B\u6CD5\u3002 \u2462 \u57FA\u7763\u4EE3\u8868\u4EBA\u552F\u6709\u56E0\u4FE1\u624D\u80FD\u79F0\u4E49\u3002\u6CA1\u6709\u901A\u8FC7\u4FE1\uFF0C\u7EDD\u5BF9\u6CA1\u6709\u79F0\u4E49\u3002\u4EBA\u7EDD\u5BF9\u4E0D\u80FD\u4EE5\u5584\u884C\u3001\u8654\u8BDA\u3001\u5C0A\u656C\u57FA\u7763\u6559\u6765\u53D6\u4EE3\u4FE1\u800C\u6765\u7684\u6551\u6069\u3002 \u2463 \u57FA\u7763\u4EE3\u8868\u4E86\u795E\u513F\u5B50\u7684\u5370\u8BB0\u3002\u8FD9\u5C31\u662F\u90A3\u5728\u6240\u6709\u4FE1\u57FA\u7763\u4E4B\u4EBA\u5FC3\u91CC\u7684\u5723\u7075\u3002\u8FD9\u5723\u7075\u7EDD\u4E0D\u79BB\u5F00\u5DF2\u4FE1\u4E4B\u4EBA\uFF0C\u4E5F\u5728\u51E1\u4E8B\u4E0A\u5FC5\u4F1A\u6307\u793A\u4ED6\u3002</p><p>\u5FC3\u7075\u4E00\u65E6\u9AD8\u4E3E\u57FA\u7763\uFF0C\u5C31\u4F1A\u770B\u89C1\u798F\u97F3\u7684\u5927\u80FD\u900F\u8FC7\u6211\u4EEC\u7684\u751F\u547D\u5F70\u663E</p><ul><li>\u5F88\u591A\u4FE1\u5F92\u5929\u5929\u7528\u53E3\u820C\u8BF4\uFF1A\u201C\u8981\u9AD8\u4E3E\u57FA\u7763\u201D\uFF0C\u4F46\u5176\u5B9E\u6839\u672C\u4E0D\u660E\u767D\u201C\u4F55\u8C13\u9AD8\u4E3E\uFF1F\u201D\u3002\u4E0A\u5E1D\u662F\u300C\u5728\u4FE1\u5F92\u7684\u5FC3\u7075\u300D\u91CC\u88AB\u9AD8\u4E3E\u7684\u3002\u6240\u4EE5\uFF0C\u798F\u97F3\u7684\u5927\u80FD\u662F\u5FC5\u5B9A\u8981\u501F\u7740\u4EBA\u7684\u5FC3\u7075\u9AD8\u4E3E\u57FA\u7763\u624D\u80FD\u663E\u660E\u7684\u3002\u5018\u82E5\u4E00\u4E2A\u4FE1\u5F92\u7684\u5FC3\u7075\u8BA4\u8BC6\u4E0E\u51E1\u4E8B\u8BA4\u5B9A\u3001\u4EF0\u671B\u3001\u4F9D\u9760\u4EE5\u4E0A2\uFF09\u7684\u771F\u7406\uFF0C\u4E0A\u5E1D\u7684\u5DE5\u4F5C\u81EA\u7136\u4F1A\u900F\u8FC7\u4ED6\u7684\u751F\u547D\u5927\u5927\u7684\u663E\u660E\uFF0C\u4F7F\u4ED6\u5468\u56F4\u7684\u4EBA\u5F97\u89C1\u795E\u7684\u8363\u5149\u3002\u6240\u4EE5\uFF0C\u8FD9\u4E16\u754C\u662F\u4E00\u76F4\u7528\u5404\u79CD\u7684\u98CE\u4FD7\u3001\u6587\u5316\u3001\u5B66\u95EE\u3001\u4E8B\u4EF6\u6765\u53EB\u6211\u4EEC\u8F6C\u79FB\u5982\u6B64\u7684\u5FC3\u7075\u3002\u8FD9\u662F\u56E0\u4E3A\u5F53\u4EBA\u4E00\u5931\u53BB\u8FD9\u6837\u7684\u5FC3\u7075\uFF0C\u9A6C\u4E0A\u4F1A\u8FDB\u5230\u88AB\u63A7\u544A\u7684\u72B6\u6001\u3001\u53D7\u4E16\u754C/\u8089\u4F53\u6346\u7ED1\u3001\u5931\u53BB\u81EA\u7531\u4E0E\u80FD\u529B\u3002</li></ul><p>\u4F46\u4EBA\u751F\u547D\u7684\u6784\u9020\u662F\uFF1A\u82E5\u4E0D\u8BA4\u8BC6\u81EA\u5DF1\u7684\u7F6A\uFF0C\u65E0\u6CD5\u6765\u5230\u57FA\u7763\u9762\u524D \u5F8B\u6CD5\u662F\u5C06\u4EBA\u5708\u5728\u7F6A\u91CC\uFF0C\u4F7F\u4EBA\u88AB\u7275\u5F15\u5230\u57FA\u7763\u7684\u9762\u524D\uFF0C\u5F97\u4EE5\u56E0\u4FE1\u79F0\u4E49</p><ul><li>\u5728\u4F20\u798F\u97F3\u7684\u65E5\u5B50\u91CC\uFF0C\u53D1\u73B0\u4FE1\u8036\u7A23\u7684\u4EBA\u6709\u4E09\u79CD\uFF1A \u2460 \u56E0\u4E3A\u770B\u89C1\u8036\u7A23\u7684\u826F\u597D\u800C\u4FE1\u3002\u8FD9\u7B49\u4FE1\u5F92\u8BA4\u5B9A\u57FA\u7763\u80FD\u8D50\u4EBA\u5E73\u5B89\u3001\u559C\u4E50\u3001\u5B89\u5EB7\uFF0C\u4F46\u4ED6\u4EEC\u65E0\u6CD5\u8FA8\u522B\u5B97\u6559\u4E0E\u798F\u97F3\uFF0C\u6216\u662F\u4EBA\u610F\u4E0E\u795E\u4E49\u3002 \u2461 \u56E0\u4E3A\u81EA\u5DF1\u8EAB\u4E0A\u6709\u95EE\u9898\u800C\u4FE1\u3002\u5927\u90E8\u5206\u4EBA\u90FD\u662F\u5728\u8FD9\u6837\u7684\u5149\u666F\u5F53\u4E2D\uFF0C\u795E\u5411\u4ED6\u663E\u660E\u3002\u4F46\u662F\u5F80\u5F80\u95EE\u9898\u6D88\u6563\u540E\uFF0C\u9700\u8981\u57FA\u7763\u7684\u5FC3\u4E5F\u51CF\u5C11\u3002 \u2462 \u56E0\u4E3A\u5FC3\u7075\u77E5\u7F6A\u800C\u4FE1\u3002\u8FD9\u5C31\u662F\u624E\u5FC3\u6240\u5E26\u6765\u7684\u91CD\u751F\u6094\u6539\u3002\u8FD9\u6837\u7684\u4EBA\u662F\u4E00\u751F\u4F1A\u7EE7\u7EED\u5426\u8BA4\u81EA\u5DF1\uFF0C\u4EE5\u4FE1\u57FA\u7763\u7684\u4E49\u800C\u6D3B\u3002</li><li>\u4F20\u798F\u97F3\u8005\u7684\u8D23\u4EFB\u662F\u8981\u5C06\u6240\u6709\u6765\u5230\u4E3B\u524D\u7684\u4EBA\u5E26\u5165\u201C\u77E5\u7F6A\u7684\u5FC3\u7075\u201D\uFF0C\u4F7F\u4ED6\u8BA4\u8BC6\u57FA\u7763\u91CC\u5934\u6D69\u5927\u7684\u6069\u5178\uFF0C\u4EE5\u81F4\u4E00\u751F\u9760\u57FA\u7763\u7684\u4E49\u800C\u6D3B\u3002</li></ul><p>\u5F8B\u6CD5\u662F\u516C\u4E49\u3001\u826F\u5584\u3001\u5723\u6D01\u7684\uFF0C\u4F46\u5374\u662F\u4E3A\u8FC7\u72AF\u6DFB\u4E0A\u7684\uFF08\u5411\u4EBA\u663E\u660E\u4ED6\u4EEC\u7684\u7F6A\uFF09\uFF0C\u6CA1\u6709\u62EF\u6551\u4EBA\u7684\u529F\u7528</p><ul><li>\u5982\u540C\u78C1\u5171\u632F\u6210(MRI)\u88AB\u4F7F\u7528\u6765\u663E\u793A\u4EBA\u8EAB\u4F53\u91CC\u7684\u80BF\u7624\u6216\u9AA8\u6298\uFF08\u751A\u81F3\u660E\u663E\u7684\u663E\u793A\uFF09\uFF0C\u5374\u4E0D\u80FD\u62EF\u6551\u533B\u6CBB\u4EBA\u3002\u540C\u6837\u7684\uFF0C\u5F8B\u6CD5\u660E\u663E\u7684\u663E\u793A\u4EBA\u7684\u7F6A\u4E0E\u8BC5\u5492\uFF0C\u5374\u4E0D\u80FD\u4ECE\u90A3\u91CC\u5C06\u4EBA\u62EF\u6551\u51FA\u6765\u3002\u6240\u4EE5\uFF0C\u4E00\u4E2A\u8499\u795E\u62E3\u9009\u7684\u4EBA\uFF0C\u82E5\u4ED6\u6D3B\u5728\u4E16\u754C\u91CC\uFF08\u5F8B\u6CD5\u4E4B\u4E0B\uFF09\uFF0C\u4ED6\u5E94 \u8BE5\u8981\u75DB\u82E6\u7684\u3002\u4ED6\u82E5\u75DB\u82E6\uFF0C\u8BC1\u660E\u4ED6\u7684\u8499\u6069\u3002\u82E5\u4ED6\u4E0D\u75DB\u82E6\uFF0C\u8BC1\u660E\u4ED6\u7684\u65E0\u77E5\u6216\u662F\u8499\u6069\u7684\u65F6\u95F4\u8868\u8FD8\u6CA1\u5230\u3002\u6240\u4EE5\uFF0C\u5728\u4F20\u798F\u97F3\u5F53\u4E2D\uFF0C\u771F\u7684\u8981\u5BFB\u627E\u516B\u798F\u4EBA\uFF08\u751F\u547D\u56E0\u81EA\u5DF1\u7684\u7F6A\u6216\u95EE\u9898\u800C\u6765\u5230\u5C3D\u5934\uFF09\u3002\u5DF2\u7ECF\u4FE1\u4E86\u4E3B\u7684\u4EBA\u4E5F\u5F53\u56E0\u8FD9\u826F\u5584\u7684\u5F8B\u6CD5\u77E5\u9053\u81EA\u5DF1\u7684\u8FC7\u72AF\uFF0C\u6240\u4EE5\u624D\u80FD\u4E0D\u65AD\u9760\u6069\u5178\u6765\u6D3B\uFF0C\u5728\u7231\u91CC\u6210\u5723\u3002</li></ul><p>\u6240\u4EE5\uFF0C\u56E0\u4FE1\u79F0\u4E49\u4E4B\u540E\uFF0C\u4EBA\u7684\u5FC3\u7075\u8981\u8F6C\u5411\u6069\u5178\u800C\u6D3B\uFF0C\u4E0D\u80FD\u5728\u7559\u5728\u5F8B\u6CD5\u7684\u8981\u6C42\uFF08\u4EEA\u6587\u7684\u65E7\u6837\uFF09\u300A\u7F576:14; 7:4-11; 8\uFF1A1\u300B</p><ul><li>\u9B54\u9B3C\u662F\u663C\u591C\u63A7\u544A\u5F1F\u5144\u4E4B\u7075\u3002\u4F46\u662F\uFF0C\u6211\u4EEC\u8981\u77E5\u9053\u5B83\u8BD5\u63A2\u7684\u7126\u70B9\u4E0E\u63A7\u544A\u7684\u80FD\u529B\u662F\u900F\u8FC7\u5F8B\u6CD5\u3002\u82E5\u4E00\u4E2A\u4EBA\u4FE1\u4E3B\u540E\u53C8\u9760\u5F8B\u6CD5\u7684\u51C6\u5219\u6D3B\uFF0C\u7F6A\u610F\u662F\u5F88\u5FEB\u53C8\u4F1A\u5728\u4ED6\u91CC\u5934\u53D1\u52A8\uFF0C\u4E5F\u5728\u90A3\u5730\u65B9\u4F1A\u56E0\u4ED6\u7684\u8F6F\u5F31\u63A7\u544A\u4ED6\u3002\u6240\u4EE5\u300A\u7F577\u300B\u8BF4\uFF1A\u6211\u4EEC\u8FD9\u4E0E\u57FA\u7763\u540C\u6B7B\u540C\u590D\u6D3B\u7684\u4EBA\uFF0C\u5728\u5F8B\u6CD5\u4E0A\u5DF2\u7ECF\u6B7B\u4E86...\u82E5\u6CA1\u6709\u5F8B\u6CD5\uFF0C\u7F6A\u662F\u6B7B\u7684\u3002\u4F46\u662F\uFF0C\u82E5\u4E00\u4E2A\u4EBA\u4F9D\u9760\u5F8B\u6CD5\u6D3B\uFF0C\u5728\u4ED6\u8981\u5B88\u5F8B\u6CD5\u7684\u5FC3\u6001\u91CC\uFF0C\u9B54\u9B3C\u4FBF\u4F1A\u53D1\u52A8\u4ED6\u72AF\u7F6A\u4E4B\u5FC3\uFF0C\u7136\u540E\u63A7\u544A\u4ED6\uFF0C\u4F7F\u4ED6\u65E0\u6CD5\u9762\u5BF9\u795E\u3002\u4FE1\u5F92\u662F\u6309\u7740\u5FC3\u7075\u7684\u65B0\u6837\u6D3B\uFF0C\u5C31\u662F\u4EE5\u300C\u613F\u610F\u7231\u795E\uFF0C\u542C\u4ECE\u795E\u300D\u7684\u5FC3\u800C\u6D3B\uFF0C\u53EA\u6709\u5982\u6B64\u80FD\u6D88\u6563\u90A3\u6697\u793A\u63A7\u544A\u4EBA\u7684\u7075\u3002</li></ul><p>\u4ECE\u4ECA\u4EE5\u540E\uFF0C\u4EE5\u795E\u513F\u5B50\u7684\u5FC3\u7075\u6765\u6D3B\uFF0C\u7136\u540E\u300C\u5728\u5FC3\u7075\u91CC\u786E\u8BA4\u300D\u90A3\u798F\u97F3\u7684\u5927\u80FD\u662F\u5982\u4F55\u4E34\u5230\u81EA\u5DF1\u8EAB\u4E0A \u5728\u5FC3\u7075\u91CC\u786E\u8BA4\uFF1A\u77E5\u7F6A\u7684\u5FC3\u7075 Vs \u53D7\u63A7\u544A\u7684\u5FC3\u7075</p><ul><li>\u5728\u786E\u8BA4\u4E2D\u4F1A\u53D1\u73B0\u5F53\u6211\u4EEC\u8F6F\u5F31\u88AB\u8FC7\u72AF\u6240\u80DC\u65F6\uFF0C\u90A3\u77E5\u7F6A\u7684\u5FC3\u7075\u4F7F\u6211\u4EEC\u4E0D\u518D\u9760\u81EA\u5DF1\u7684\u4E49\uFF0C\u53CD\u800C\u6D3B\u5728\u57FA\u7763\u7684\u4E49\uFF0C\u66F4\u5766\u7136\u65E0\u60E7\u7684\u9762\u5BF9\u795E\u3002\u4F46\u53D7\u63A7\u544A\u7684\u5FC3\u7075\u662F\u53EB\u6211\u4EEC\u4E00\u4E0B\u5B50\u5185\u759A\u81EA\u5351\uFF0C\u4E00\u4E0B\u5B50\u53C8\u81EA\u4EE5\u4E3A\u4E49\u3002\u4ECE\u8FD9\u91CC\uFF0C\u6211\u4EEC\u4F1A\u9886\u4F1A\u5230\u77E5\u7F6A\u662F\u4E00\u4E2A\u8C26\u5351\uFF0C\u5374\u5927\u6709\u80FD\u529B\u7684\u5FC3\u7075\u3002</li></ul><p>\u5728\u5FC3\u7075\u91CC\u786E\u8BA4\uFF1A\u6D3B\u5728\u6069\u5178\u4E4B\u4E0B\u7684\u81EA\u7531 Vs \u6D3B\u5728\u5F8B\u6CD5\u4E4B\u4E0B\u7684\u8981\u6C42</p><ul><li>\u5728\u786E\u8BA4\u5F53\u4E2D\u4F1A\u53D1\u73B0\u6069\u5178\u4E4B\u4E0B\u7684\u5FC3\u7075\u53EB\u7F6A\u6076\u8BD5\u63A2\u65E0\u6CD5\u9760\u8FD1\u6211\u4EEC\uFF0C\u4E5F\u6CA1\u6709\u6015\u72AF\u9519\u7684\u5FC3\u4E0E\u72AF\u9519\u540E\u7684\u5211\u7F5A\u611F\u3002\u751A\u81F3\u88AB\u7BA1\u6559\u4E4B\u540E\u4ECD\u7136\u80FD\u671F\u5F85\u800C\u770B\u89C1\u4E0B\u9762\u66F4\u5927\u7684\u6069\u5178\u3002\u56E0\u6B64\u7231\u795E\u4E0E\u7231\u4EBA\u7684\u5FC3\u52A0\u589E\u3002\u5468\u56F4\u7684\u4EBA\u4E5F\u559C\u6B22\u4E0E\u6211\u4EEC\u5728\u4E00\u8D77\u3002\u4F46\u6D3B\u5728\u5F8B\u6CD5\uFF08\u662F\u975E\u4E4B\u5FC3\uFF09\u4E4B\u4E0B\u7684\u4EBA\u5E38\u5E38\u5BB3\u6015\u72AF\u9519\uFF0C\u5BB3\u6015\u540E\u679C\uFF0C\u4E5F\u5E38\u88AB\u4EBA\u5BF9\u4ED6\u7684\u770B\u6CD5\u53D7\u5F71\u54CD\u3002\u4ED6\u91CC\u5934\u7231\u795E\u7231\u4EBA\u7684\u5FC3\u4E0D\u589E\u957F\u3002\u5468\u56F4\u7684\u4EBA\u4E5F\u56E0\u4ED6\u7684\u751F\u547D\u627F\u53D7\u8BB8\u591A\u538B\u529B\u4E0E\u81EA\u8D23\u3002</li></ul><p>\u5728\u5FC3\u7075\u91CC\u786E\u8BA4\uFF1A\u542C\u4ECE\u795E\u7684\u667A\u6167 Vs \u4F9D\u9760\u4EBA\u672C\u7684\u806A\u660E</p><ul><li>\u5728\u786E\u8BA4\u5F53\u4E2D\u4F1A\u53D1\u73B0\u4E0A\u5E1D\u795D\u798F\u4E00\u4E2A\u4EBA\u7684\u656C\u754F\u4E4B\u5FC3\uFF0C\u4E0D\u662F\u795D\u798F\u4EBA\u7684\u806A\u660E\u3002\u6240\u4EE5\uFF0C\u5F53\u4E00\u4E2A\u4EBA\u6709\u5FC3\u8981\u542C\u4ECE\u795E\u65F6\uFF0C\u4ED6\u9996\u5148\u4F1A\u5F97\u7740\u5E73\u5B89\u7684\u5FC3\uFF0C\u7136\u540E\u5728\u6240\u884C\u7684\u4E8B\u4E0A\u5F97\u786E\u5B9A\u7684\u5FC3\uFF0C\u4E0D\u56E0\u4EBA\u6447\u6446\uFF0C\u4ED6\u6240\u4F5C\u7684\u4E5F\u7ECF\u5F97\u8D77\u65F6\u95F4\u7684\u8003\u9A8C\uFF0C\u5728\u957F\u8FDC\u7684\u65F6\u5149\u4F1A\u770B\u89C1\u660E\u663E\u7684\u8BC1\u636E\u3002\u4F46\u4F9D\u9760\u4EBA\u672C\u7684\u4EBA\u603B\u662F\u56E0\u6210\u679C\u7684\u597D\u574F\u800C\u6447\u52A8\uFF0C\u6CA1\u6709\u7ACB\u8DB3\u70B9\u3002\u8FD9\u6837\u7684\u4EBA\u505A\u7684\u4E5F\u7ECF\u4E0D\u8D77\u65F6\u95F4\u4E0E\u95EE\u9898\u7684\u8003\u9A8C\u3002</li></ul><p>\u5728\u5FC3\u7075\u91CC\u786E\u8BA4\uFF1A\u4E3A\u5929\u56FD\u800C\u6D3B\u7684\u559C\u4E50\u6EE1\u8DB3 Vs \u8FFD\u6C42\u4E16\u754C\u7684\u77ED\u6682\u5F00\u5FC3</p><ul><li>\u5728\u786E\u8BA4\u5F53\u4E2D\u4F1A\u53D1\u73B0\u4E3A\u5929\u56FD\u800C\u6D3B\u5E76\u975E\u662F\u538B\u529B\uFF0C\u4E43\u662F\u4E00\u4E2A\u627F\u53D7\u5C5E\u7075\u4EA7\u4E1A\u4E4B\u4EBA\u975E\u4F5C\u4E0D\u53EF\u7684\u4E8B\u3002\u8FD9\u6837\u7684\u4EBA\u4E3A\u5929\u56FD\u6765\u884C\u51E1\u4E8B\u5C31\u662F\u4ED6\u7684\u559C\u4E50\u4E0E\u6EE1\u8DB3\uFF0C\u4E16\u754C\u7684\u6210\u529F\u4E5F\u65E0\u6CD5\u76F8\u6BD4\u3002\u4F46\u5728\u5730\u4E0A\u5F97\u7740\u7684\u94B1\u8D22\u3001\u540D\u8A89\u3001\u5730\u4F4D\u3001\u6210\u5C31\u662F\u5F88\u5FEB\u6D88\u6563\uFF0C\u4E5F\u5F97\u4E86\u4E4B\u540E\u542B\u6709\u7A7A\u865A\u4E0E\u5BB3\u6015\u4F1A\u5931\u53BB\u7684\u611F\u89C9\u3002\u51E1\u4E0D\u4E3A\u5929\u56FD\u6240\u5F97\u7684\u4EA8\u901A\u987A\u5229\u90FD\u5E26\u6765\u5FC3\u7075\u6709\u5982\u6B64\u7684\u611F\u53D7\u3002\u6240\u4EE5\uFF0C\u4ECE\u8FD9\u91CC\uFF0C\u6211\u4EEC\u4F1A\u6653\u5F97\u771F\u6B63\u7684\u4EF7\u503C\u4E0E\u6709\u80FD\u529B\u7684\u4EBA\u751F\u662F\u671D\u5411\u54EA\u91CC\u3002</li></ul>",20),a=[e];function s(u,o,r,c,n,d){return i(),_("div",null,a)}var m=l(t,[["render",s]]);export{V as __pageData,m as default};
