import{_ as l,c as _,o as e,a}from"./app.cc1c08f6.js";const g='{"title":"\u798F\u97F3\u8D50\u4EBA\u771F\u81EA\u7531\uFF0C\u4E4B\u540E\u4F7F\u4ED6\u51E1\u4E8B\u4EE5\u7231\u51FA\u53D1","description":"","frontmatter":{},"headers":[{"level":3,"title":"\u798F\u97F3\u8D50\u4EBA\u771F\u81EA\u7531\uFF0C\u4E4B\u540E\u4F7F\u4ED6\u51E1\u4E8B\u4EE5\u7231\u51FA\u53D1","slug":"\u798F\u97F3\u8D50\u4EBA\u771F\u81EA\u7531\uFF0C\u4E4B\u540E\u4F7F\u4ED6\u51E1\u4E8B\u4EE5\u7231\u51FA\u53D1"}],"relativePath":"galatians/8.\u798F\u97F3\u8D50\u4EBA\u771F\u81EA\u7531\uFF0C\u4E4B\u540E\u4F7F\u4ED6\u51E1\u4E8B\u4EE5\u7231\u51FA\u53D1.md"}',i={},t=a('<h3 id="\u798F\u97F3\u8D50\u4EBA\u771F\u81EA\u7531\uFF0C\u4E4B\u540E\u4F7F\u4ED6\u51E1\u4E8B\u4EE5\u7231\u51FA\u53D1" tabindex="-1">\u798F\u97F3\u8D50\u4EBA\u771F\u81EA\u7531\uFF0C\u4E4B\u540E\u4F7F\u4ED6\u51E1\u4E8B\u4EE5\u7231\u51FA\u53D1 <a class="header-anchor" href="#\u798F\u97F3\u8D50\u4EBA\u771F\u81EA\u7531\uFF0C\u4E4B\u540E\u4F7F\u4ED6\u51E1\u4E8B\u4EE5\u7231\u51FA\u53D1" aria-hidden="true">#</a></h3><p>\u300A\u52A05:1-15\u300B \u5E8F\u8BBA\uFF1A\u798F\u97F3\u5C06\u300C\u771F\u81EA\u7531\u300D\u8D50\u7ED9\u4EBA\uFF0C\u4F7F\u300C\u7231\u300D\u6210\u4E3A\u4EBA\u5FC3\u91CC\u7684\u4E00\u5207\u52A8\u673A\uFF0C\u5728\u8FD9\u91CC\u5934\u5F97\u7740\u5723\u7075\u51E1\u4E8B\u4E0A\u7684\u611F\u52A8\u800C\u884C\u3002\u8FD9\u5C31\u662F\u6700\u8F7B\u7701\u3001\u5BB9\u6613\u3001\u6709\u80FD\u529B\u7684\u65B9\u6CD5\u4E86\u3002\u56E0\u4E3A\u5728\u7231\u91CC\u9762\u6CA1\u6709\u60E7\u6015\uFF08\u4E0D\u6015\u505A\u9519\u3001\u53D7\u5B9A\u7F6A\u6216\u4E0D\u597D\u7684\u7ED3\u679C\uFF09\u3002\u4EBA\u5728\u7231\u91CC\u884C\u4E8B\u4E3A\u4EBA\uFF0C\u5FC3\u91CC\u4E0D\u53D7\u8C34\u8D23\uFF0C\u53CD\u5012\u8D8A\u6765\u8D8A\u6E05\u695A\u795E\u51E1\u4E8B\u7684\u7F8E\u610F\u3002\u8FD9\u5C31\u662F\u4E0A\u5E1D\u613F\u610F\u7942\u513F\u5973\u4EEC\u6240\u4EAB\u53D7\u7684\u4E30\u76DB\u751F\u547D\u4E86\u3002\u6240\u4EE5\uFF0C\u5F53\u6211\u4EEC\u6765\u5230\u52A0\u62C9\u592A\u4E66\u7684\u5C3E\u7AEF\uFF0C\u6211\u4EEC\u8981\u601D\u60F3\u798F\u97F3\u6700\u7EC8\u8981\u5E26\u5165\u6211\u4EEC\u5FC3\u7075\u7684\u81EA\u7531\u4E0E\u6148\u7231\u662F\u4F55\u8C13\u4E8B\uFF1F\u5F53\u6211\u4EEC\u8BF4\u201C\u81EA\u7531\u201D\uFF0C\u8FD9\u4E0D\u662F\u4E16\u4EBA\u6240\u8BA4\u4E3A\u7684\u201C\u81EA\u6211\u4E3B\u6743\u201D\uFF0C\u56E0\u4E3A\u4EBA\u662F\u751F\u6765\u7075\u91CC\u5C31\u88AB\u6346\u7ED1\uFF0C\u601D\u60F3\u3001\u884C\u4E8B\u3001\u4E3A\u4EBA\u90FD\u968F\u4ECE\u4ECA\u4E16\u98CE\u4FD7\u4E0E\u90AA\u7075\u7684\u9996\u9886\u3002\u4F46\u662F\uFF0C\u5F53\u57FA\u7763\u91CA\u653E\u4EBA\u7684\u5FC3\u7075\u65F6\uFF0C\u4EBA\u662F\u4ECE\u4E00\u5207\u542C\u547D\u4E8E\u4EBA\u6027\u800C\u6765\u7684\u52A8\u673A\u3001\u76EE\u7684\u3001\u610F\u5FF5\u3001\u826F\u77E5\u3001\u4EEA\u6587\u3001\u7B49\u90FD\u5F97\u91CA\u653E\uFF0C\u552F\u72EC\u5355\u5355\u542C\u4ECE\u547C\u53EC\u4ED6\u7684\u795E\u3002\u4EBA\u4FBF\u4ECE\u4E00\u4E2A\u8981\u542C\u4ECE\u201C\u8BB8\u591A\u4E3B\u4EBA\u201D\u7684\u5FC3\u7075\uFF0C\u56DE\u590D\u4E86\u5355\u5355\u542C\u4ECE\u90A3\u201C\u552F\u4E00\u7684\u4E3B\u4EBA\u201D\uFF08\u57FA\u7763\uFF09\uFF0C\u8FD9\u5C31\u662F\u771F\u81EA\u7531\u4E86\u3002\u5F53\u4EBA\u7684\u5FC3\u7075\u9886\u4F1A\u4E86\u8FD9\u201C\u542C\u4ECE\u795E\u201D\u7684\u81EA\u7531\uFF0C\u4ED6\u5185\u5FC3\u7684\u52A8\u673A\u662F\u53EA\u6709\u4E00\u4E2A\u4E86\uFF0C\u5C31\u662F\u201C\u7231\u201D\u3002\u8FD9\u201C\u7231\u201D\u5728\u4ED6\u7684\u5FC3\u4E2D\u672C\u4E0D\u662F\u5F8B\u6CD5\uFF0C\u4E5F\u4E0D\u662F\u4E00\u4E2A\u8981\u6C42\uFF0C\u4E43\u662F\u751F\u547D\u7684\u6D41\u9732\u4E0E\u80FD\u529B\u3002\u56E0\u6B64\uFF0C\u5728\u57FA\u7763\u91CC\u9886\u53D7\u4E86\u81EA\u7531\u4E4B\u5FC3\u7075\u7684\u4EBA\u540C\u65F6\u4F1A\u9886\u53D7\u201C\u7231\u4EBA\u5982\u5DF1\u201D\u4E4B\u5FC3\u3002\u8FD9\u7B49\u7231\u672C\u662F\u4ECE\u798F\u97F3\u7684\u7CBE\u4E49\u800C\u6765\uFF0C\u662F\u201C\u5982\u6B7B\u4E4B\u575A\u5F3A\u201D\uFF0C\u4E0D\u80FD\u88AB\u4EBA\u7684\u6761\u4EF6\u3001\u60C5\u51B5\u3001\u8F6F\u5F31\u6765\u6DF9\u6CA1\u3002\u6211\u4EEC\u91CD\u751F\u540E\u4E5F\u5F53\u5728\u8FD9\u7B49\u7231\u7EE7\u7EED\u6210\u957F\uFF0C\u4EE5\u81F4\u80FD\u7231\u5F97\u6709\u57FA\u7763\u7684\u667A\u6167\u3001\u516C\u4E49\u4E0E\u80FD\u529B\u3002\u4ECA\u5929\uFF0C\u6709\u8BB8\u591A\u4FE1\u5F92\u4E0D\u660E\u767D\u8FD9\u7B49\u7231\u662F\u600E\u4E48\u4E34\u5230\uFF0C\u4ED6\u4EEC\u4E00\u76F4\u5C3D\u91CF\u3001\u5C1D\u8BD5\u3001\u52AA\u529B\u8981\u53BB\u7231\u4EBA\uFF0C\u5374\u5FC3\u4E2D\u4E00\u76F4\u6CA1\u6709\u80FD\u529B\u7231\u3002\u8FD9\u662F\u56E0\u4E3A\u4ED6\u4EEC\u4E0D\u660E\u767D\u771F\u7231\u662F\u501F\u5723\u7075\u6240\u8D50\u7684\u771F\u81EA\u7531\u800C\u6765\uFF0C\u4E0D\u662F\u9760\u4EBA\u7684\u52AA\u529B\u800C\u6765\u3002\u5982\u5723\u7ECF\u6240\u8BF4\uFF1A\u795E\u5C31\u662F\u7231\uFF0C\u6211\u4EEC\u7231\uFF0C\u56E0\u4E3A\uFF08\u660E\u767D\u4E86\uFF09\u795E\u5148\u7231\u6211\u4EEC\u3002</p><p>\u5341\u5B57\u67B6\u8BA8\u538C\u7684\u5730\u65B9\u5C31\u662F\u798F\u97F3\u7684\u7CBE\u4E49\u6240\u5728\u4E4B\u5904 \u5341\u5B57\u67B6\u662F\u4E0D\u4EE5\u4EBA\u7684\u6761\u4EF6\u4E0E\u5584\u884C\u4E3A\u672C\uFF0C\u4E43\u663E\u660E\u795E\u662F\u6309\u7740\u7942\u7684\u62E3\u9009\u611F\u52A8\u4EBA\u63A5\u53D7\u6551\u6069\uFF0C\u4EE5\u81F4\u767D\u767D\u7684\u79F0\u4E49</p><ul><li>\u4E0A\u5E1D\u6240\u663E\u660E\u7684\u6551\u6069\uFF0C\u4E0E\u4EBA\u7684\u5584\u610F\u5168\u7136\u65E0\u5173\uFF0C\u4E43\u662F\u7942\u8981\u6069\u5F85\u8C01\u5C31\u6069\u5F85\u8C01\uFF0C\u7942\u8981\u601C\u60AF\u8C01\u5C31\u601C\u60AF\u8C01\u3002\u8FD9\u672C\u8EAB\u662F\u4EBA\u7406\u4E0E\u4EBA\u7684\u4EF7\u503C\u89C2\u65E0\u6CD5\u7406\u89E3\u4E0E\u63A5\u53D7\u4E4B\u4E8B\u3002\u6240\u4EE5\uFF0C\u5018\u82E5\u4EBA\u5E26\u7740\u4EFB\u4F55\u7684\u826F\u77E5\u3001\u54F2\u5B66\u3001\u9053\u7406\u3001\u6587\u5316\u80CC\u666F\u3001\u5B97\u6559\u51C6\u5219\u3001\u6765\u6279\u5224\u5341\u5B57\u67B6\u7684\u6551\u6069\uFF0C\u7EDD\u5B9A\u65E0\u6CD5\u8BA4\u540C\uFF0C\u4E5F\u65E0\u6CD5\u63A5\u53D7\u3002\u56E0\u4E3A\u5168\u5584\u5168\u7231\u4E4B\u4E0A\u5E1D\u7684\u4E3B\u6743\u80DC\u4E8E\u5815\u843D\u4E4B\u4EBA\u4E00\u5207\u7684\u609F\u6027\u4E0E\u7075\u6027\uFF0C\u4F46\u5728\u90A3\u5F97\u6551\u4E4B\u4EBA\uFF0C\u8FD9\u672C\u4E3A\u795E\u7684\u6069\u5178\u3002\u4E5F\u4E3A\u795E\u7684\u5927\u80FD\u3002</li></ul><p>\u5341\u5B57\u67B6\u4E0E\u4E16\u754C\u7684\u539F\u7406\u76F8\u53CD\uFF0C\u5B8C\u5168\u524A\u5F31\u4EBA\u7684\u6761\u4EF6\uFF0C\u9AD8\u4E3E\u57FA\u7763\uFF0C\u4EE5\u81F4\u552F\u6709\u5728\u57FA\u7763\u6240\u62E5\u6709\u7684\u624D\u662F\u771F\u6B63\u8499\u6069\u8499\u798F</p><ul><li>\u300A\u6797\u524D1:22-23\u300B:\u201C\u72B9\u592A\u4EBA\u662F\u8981\u795E\u8FF9\uFF0C\u5E0C\u814A\u4EBA\u662F\u6C42\u667A\u6167\uFF0C\u6211\u4EEC\u5374\u662F\u4F20\u9489\u5341\u5B57\u67B6\u7684\u57FA\u7763...\u201D\u5176\u5B9E\uFF0C\u65E0\u8BBA\u662F\u72B9\u592A\u65CF\u6216\u5916\u90A6\u65CF\uFF0C\u4ED6\u4EEC\u6574\u4E2A\u773C\u5149\u603B\u662F\u653E\u5728\u4EBA\u7684\u6761\u4EF6\uFF0C\u6240\u4EE5\u8FD9\u5BFC\u81F4\u4EBA\u4FE1\u4E86\u4E3B\u4E4B\u540E\uFF0C\u4ECD\u7136\u504F\u5411\u5F8B\u6CD5\u4E3B\u4E49\uFF0C\u4EBA\u672C\u4E3B\u4E49\uFF0C\u795E\u79D8\u4E3B\u4E49\uFF0C\u7948\u798F\u4E3B\u4E49\u5F0F\u7684\u201C\u57FA\u7763\u4FE1\u4EF0\u201D\u3002\u4F46\u5728\u8FD9\u4E00\u5207\u91CC\uFF0C\u4EBA\u59CB\u7EC8\u6CA1\u6709\u6B7B\u53BB\u81EA\u5DF1\uFF0C\u5C06\u81EA\u5DF1\u751F\u547D\u7684\u4E3B\u6743\u4E0E\u5FC3\u4E2D\u7684\u610F\u5FF5\u90FD\u964D\u670D\u4E8E\u57FA\u7763\u3002\u4F46\u552F\u6709\u5341\u5B57\u67B6\u7684\u9053\u7406\u662F\u5C06\u5404\u6837\u7684\u8BA1\u8C0B\uFF0C\u5404\u6837\u62E6\u963B\u4EBA\u8BA4\u8BC6\u795E\u7684\u90A3\u4E9B\u81EA\u9AD8\u4E4B\u4E8B\uFF0C\u4E00\u6982\u653B\u7834\u4E86\uFF0C\u53C8\u5C06\u4EBA\u6240\u6709\u7684\u5FC3\u610F\u593A\u56DE\uFF0C\u4F7F\u4ED6\u90FD\u987A\u670D\u57FA\u7763\u3002\u300A\u6797\u540E10\uFF1A5\u300B</li></ul><p>\u5341\u5B57\u67B6\u663E\u660E\u5B8C\u5168\u7684\u987A\u670D\uFF0C\u6240\u4EE5\u4EBA\u91CC\u5934\u8089\u4F53\u7684\u60C5\u6B32\u3001\u773C\u76EE\u7684\u60C5\u6B32\u3001\u4ECA\u751F\u7684\u5938\u8000\u662F\u7EE7\u7EED\u4F1A\u654C\u6321\u987A\u670D\u4E4B\u7075</p><ul><li>\u5723\u7075\u7684\u4E8B\u4E0E\u8089\u4F53\u7684\u4E8B\u5F7C\u6B64\u4E3A\u654C\u3002\u4E0D\u8BA4\u8BC6\u795E\u7684\u4EBA\u65E0\u6CD5\u660E\u767D\u5723\u7075\u4E4B\u4E8B\uFF08\u795E\u7684\u7F8E\u610F\uFF09\uFF0C\u5C5E\u8089\u7684\u4FE1\u5F92\u4E5F\u65F6\u5E38\u65E0\u6CD5\u53D7\u5723\u7075\u5F15\u5BFC\uFF08\u9075\u884C\u795E\u65E8\u610F\uFF09\u3002\u56E0\u6B64\uFF0C\u4E0D\u8BA4\u8BC6\u5723\u7075\u4E0E\u4E0D\u987A\u670D\u5723\u7075\u4E4B\u4EBA\u90FD\u4F1A\u5728\u5FC3\u4E2D\u8BA8\u538C\u8FD9\u5341\u5B57\u67B6\uFF08\u987A\u670D\uFF09\u7684\u9053\u7406\u3002\u4F46\u4E8B\u5B9E\u4E0A\uFF0C\u5341\u5B57\u67B6\u662F\u6C38\u8FDC\u5F97\u80DC\u7684\uFF0C\u6CA1\u6709\u4E00\u4E2A\u76F8\u4FE1\u4E0E\u9075\u884C\u795E\u65E8\u610F\u7684\u4EBA\u8499\u7F9E\u6127\u300A\u81531\uFF1A20\u300B\uFF0C\u613F\u610F\u964D\u5351\u7684\u5FC5\u5347\u4E3A\u9AD8\uFF0C\u4E3A\u4E3B\u800C\u5931\u53BB\u7684\u6700\u7EC8\u5FC5\u5F97\u7740\u3002\u6240\u4EE5\uFF0C\u4EBA\u987A\u670D\u4E4B\u524D\u603B\u4F1A\u7591\u60D1\u3002</li></ul><p>\u6211\u4EEC\u8499\u53EC\u662F\u8981\u5F97\u81EA\u7531 \u8499\u795E\u53EC\u4E4B\u540E\u518D\u300C\u6CA1\u6709\u5B9A\u7F6A\u300D\uFF0C\u53EA\u5728\u4E8E\u4FE1\u5FC3\u540C\u7740\u7231\u5FC3\u7684\u6210\u957F</p><ul><li>\u4E0D\u5B9A\u7F6A\uFF0C\u662F\u57FA\u7763\u4FE1\u4EF0\u7684\u5F00\u59CB\u3001\u8FC7\u7A0B\u3001\u7ED3\u675F\u300A\u7F578\uFF1A1\uFF0C15\uFF0C38-39\u300B\u3002\u4FE1\u5F92\u5728\u5FC3\u7075\u91CC\u4EAB\u53D7\u7EDD\u5BF9\u4E0D\u88AB\u5B9A\u7F6A\u7684\u786E\u636E\uFF0C\u624D\u80FD\u5728\u7A7A\u4E2D\u638C\u6743\u8005\u9996\u9886\u7684\u4E16\u754C\u91CC\u5E38\u5E38\u4E00\u5239\u90A3\u4ECE\u63A7\u544A\u3001\u60E7\u6015\u3001\u65E0\u80FD\u51FA\u6765\uFF0C\u5F97\u7740\u80FD\u529B\u8DDF\u4ECE\u795E\u3002\u6240\u4EE5\uFF0C\u771F\u81EA\u7531\u7684\u4FE1\u5F92\u4E0D\u662F\u6015\u72AF\u9519\uFF0C\u4E43\u662F\u5728\u51E1\u4E8B\u4E0A\uFF0C\u7EE7\u7EED\u751F\u53D1\u4EC1\u7231\u7684\u4FE1\u5FC3\u3002\u6362\u53E5\u8BDD\uFF0C\u4FE1\u5F92\u7684\u8FFD\u6C42\u5DF2\u4E0D\u518D\u662F\u4E0D\u72AF\u9519\uFF0C\u4E43\u662F\u5728\u4FE1\u3001\u671B\u3001\u7231\u7684\u589E\u957F\uFF0C\u8D8A\u6765\u8D8A\u6EE1\u6709\u57FA\u7763\u7684\u8363\u5F62\u3002</li></ul><p>\u8499\u795E\u53EC\u662F\u53EA\u6709\u300C\u542C\u4ECE\u4E00\u4E2A\u4E3B\u4EBA\u300D\uFF0C\u5C31\u662F\u521B\u9020\u5B87\u5B99\u4E07\u6709\u7684\u4E3B</p><ul><li>\u4EBA\u6D3B\u7740\u4E0D\u662F\u5355\u9760\u98DF\u7269\uFF0C\u4E43\u662F\u9760\u795E\u53E3\u91CC\u6240\u51FA\u7684\u4E00\u5207\u8BDD\u300A\u592A4\uFF1A4\u300B\u3002\u4E9A\u4F2F\u62C9\u7F55\u51E1\u4E8B\u4E0A\u7684\u8499\u798F\u662F\u56E0\u542C\u4ECE\u795E\u800C\u6765\uFF1B\u6469\u897F\u7684\u6743\u67C4\u662F\u9075\u7167\u8036\u548C\u534E\u7684\u5429\u5490\u800C\u6765\uFF1B\u4FDD\u7F57\u5F97\u80DC\u6240\u6709\u82E6\u96BE\u662F\u56E0\u4E3A\u5728\u82E6\u96BE\u5F53\u4E2D\u4E0D\u65AD\u4E0E\u795E\u4EA4\u901A\u3002\u5929\u3001\u5730\u3001\u4E07\u6709\u90FD\u662F\u8FD9\u4F4D\u4E3B\u6240\u9020\u7684\u3002\u7942\u5E26\u7740\u7942\u4E00\u5207\u7684\u8363\u8000\u3001\u6743\u80FD\u3001\u6069\u60E0\u6765\u4E0E\u4EBA\u4EA4\u901A\u3002\u6CA1\u6709\u4E00\u4E2A\u4EBA\u5728\u7942\u7684\u540C\u5728\u9762\u524D\u5931\u8D25\uFF0C\u4E5F\u6CA1\u6709\u4E00\u4E2A\u9075\u7167\u7942\u7684\u5429\u5490\u4E4B\u4EBA\u770B\u89C1\u7F3A\u4E4F\u3002\u4E0A\u5E1D\u4E00\u5207\u7684\u80FD\u529B\u5C31\u5728\u4E0E\u7942\u7684\u5429\u5490\uFF08\u8BDD\u8BED\uFF09\u672C\u8EAB\u4E86\u3002\u6240\u4EE5\uFF0C\u4E00\u4E2A\u4FE1\u5F92\u82E5\u80FD\u4EE5\u5BFB\u6C42\u542C\u4ECE\u4E3B\u4EBA\u7684\u58F0\u97F3\u4E3A\u6EE1\u8DB3\u548C\u6307\u793A\uFF0C\u6CA1\u6709\u4E00\u4E2A\u95EE\u9898\u6216\u82E6\u96BE\u80FD\u80DC\u8FC7\u4ED6\u3002\u300A\u6492\u4E0A15\uFF1A22\u300B\uFF1A\u201C\u542C\u547D\u80DC\u4E8E\u732E\u796D\uFF0C\u987A\u4ECE\u80DC\u4E8E\u516C\u7F8A\u7684\u8102\u6CB9\u201D\u3002\u56E0\u4E3A\u542C\u547D\u4E0E\u987A\u4ECE\u662F\u8BB2\u5230\u4EBA\u7684\u5185\u5FC3\u964D\u670D\u4E0E\u795E\uFF0C\u5728\u90A3\u91CC\u6709\u4E00\u5207\u7684\u81EA\u7531\u4E0E\u80FD\u529B\u3002</li></ul><p>\u8499\u795E\u53EC\u4E4B\u540E\u6240\u5F97\u300C\u5723\u7075\u7684\u5149\u7167\u4E0E\u50AC\u903C\u300D\u80DC\u4E8E\u4E00\u5207\u4EBA\u7684\u662F\u975E\u4E0E\u51C6\u5219</p><ul><li>\u4FE1\u5F92\u5728\u987A\u670D\u4E0A\u5E1D\u7684\u4E8B\u4E0A\uFF0C\u6709\u5723\u7075\u5728\u5FC3\u4E2D\u7684\u5149\u7167\u4E0E\u50AC\u903C\uFF0C\u4F7F\u4ED6\u975E\u987A\u670D\u4E0D\u53EF\u3002\u5723\u7075\u4FDD\u5B88\u4E00\u4E2A\u4EBA\u4E0D\u504F\u79BB\u4E3B\u7684\u5FC3\u610F\u800C\u884C\uFF0C\u6240\u4EE5\u5728\u635F\u4F24\u4ED6\u751F\u547D\u4E4B\u4E8B\u4E0A\uFF0C\u5723\u7075\u4F1A\u5F3A\u70C8\u7684\u6307\u793A\u4ED6\u7684\u826F\u5FC3\uFF0C\u4F7F\u4ED6\u5F97\u8499\u4FDD\u5B88\uFF0C\u4E0D\u81F3\u4E8E\u884C\u51FA\u4E0D\u8363\u8000\u4E0A\u5E1D\u4E4B\u4E8B\u3002\u751A\u81F3\u5F53\u8499\u7231\u4E4B\u4EBA\u5076\u5C14\u88AB\u8FC7\u72AF\u6240\u80DC\uFF0C\u5723\u7075\u5728\u4EBA\u826F\u77E5\u91CC\u7684\u8C34\u8D23\u4F1A\u4F7F\u4ED6\u6094\u6539\uFF0C\u4EE5\u81F4\u4EE5\u540E\u52A0\u500D\u8C28\u614E\u8FDC\u79BB\u7F6A\u3002\u6240\u4EE5\uFF0C\u5723\u7075\u662F\u8499\u53EC\u4E4B\u4EBA\u5FC3\u4E2D\u7684\u201C\u4FDD\u60E0\u5E08\u201D\uFF0C\u4FDD\u62A4\u4E00\u5207\u8499\u53EC\u4E4B\u4EBA\u80FD\u884C\u5728\u6069\u60E0\u91CC\u3002\u5F53\u4EBA\u8D8A\u662F\u8BA4\u8BC6\u795E\u7684\u771F\u7406\uFF0C\u8D8A\u662F\u4F1A\u5728\u51E1\u4E8B\u4E0A\u8BA4\u5B9A\u3001\u6B22\u8FCE\u3001\u4F9D\u9760\u8FD9\u4FDD\u60E0\u5E08\u800C\u6D3B\u3002\u4F46\u662F\uFF0C\u4EBA\u4E00\u65E6\u4EE5\u662F\u975E\u51C6\u5219\u4E3A\u672C\uFF0C\u5C31\u81EA\u7136\u4E0D\u8BA4\u5B9A\u4FDD\u60E0\u5E08\u3002\u6240\u4EE5\uFF0C\u4FDD\u7F57\u8BF4\uFF1A\u201C\u4F60\u4EEC\u4E0D\u53EF\u5C06\u4F60\u4EEC\uFF08\u5C0A\u8D35\uFF09\u7684\u81EA\u7531\u5F53\u4F5C\u653E\u7EB5\u60C5\u6B32\u7684\u673A\u4F1A\uFF0C\u603B\u8981\uFF08\u542C\u4ECE\u5723\u7075\uFF09\u7528\u7231\u5FC3\u4E92\u76F8\u670D\u4E8B\u3002\u201D</li></ul><p>\u6700\u7EC8\uFF0C\u8499\u53EC\u4E4B\u4EBA\u4F1A\u53D1\u73B0\u81EA\u7531\u6240\u5E26\u6765\u7684\u662F\u300C\u80FD\u529B\u300D\uFF0C\u4E0D\u662F\u653E\u7EB5</p><ul><li>\u5F53\u4EBA\u8499\u53D7\u4E86\u57FA\u7763\u91CC\u771F\u6B63\u7684\u81EA\u7531\uFF08\u513F\u5973\uFF09\u4E4B\u5FC3\uFF0C\u5FC3\u91CC\u4E0D\u518D\u662F\u2018\u4E0D\u53EF\u4EE5\u72AF\u7F6A\u2019\uFF0C\u4E43\u662F\u2018\u4E0D\u60F3\u72AF\u7F6A\u2019\uFF1B\u81EA\u7531\uFF08\u8499\u6069\u60E0\uFF09\u7684\u5FC3\u5E26\u7ED9\u4EBA\u7684\u4E0D\u662F\u2018\u8981\u7231\u4EBA\u2019\uFF0C\u4E43\u662F\u2018\u6EE1\u6709\u7231\u2019\u4E4B\u5FC3\u80A0\uFF1B\u6240\u4EE5\u5728\u5404\u6837\u5584\u884C\u5E76\u4E0D\u662F\u51FA\u81EA\u4E8E\u81EA\u6211\u5584\u610F\uFF0C\u4E43\u501F\u7740\u5723\u7075\u7684\u611F\u52A8\u611F\u5316\u800C\u884C\u3002\u6240\u4EE5\uFF0C\u771F\u6B63\u5728\u57FA\u7763\u91CC\u81EA\u7531\u7684\u4EBA\u662F\u552F\u6709\u5FC3\u4E2D\u6B20\u4E86\u201C\u5F7C\u6B64\u76F8\u7231\u201D\u7684\u503A\u300A\u7F5713\uFF1A8\u300B\uFF0C\u56E0\u4E3A\u770B\u89C1\u5728\u7231\u91CC\u624D\u5F97\u4EE5\u81EA\u7531\u4E0E\u559C\u4E50\uFF0C\u5C06\u8363\u8000\u5F52\u7ED9\u4E0A\u5E1D\u3002</li></ul><p>\u5168\u5F8B\u6CD5\u90FD\u5305\u5728\u300C\u7231\u4EBA\u5982\u5DF1\u300D\u8FD9\u4E00\u53E5\u8BDD\u300A5\uFF1A13-15\u300B \u5E0C\u814A\u6587\u4EE5\u56DB\u79CD\u8BCD\u6C47\u6765\u5F62\u5BB9\u201C\u7231\u201D \u2460 Storge\uFF08\u4EB2\u60C5\uFF09: \u5BB6\u4EBA\u4E4B\u95F4\u7684\u7231\u3002\u56E0\u62E5\u6709\u8840\u7F18\u5173\u7CFB\uFF0C\u6240\u4EE5\u6309\u4EBA\u7684\u5929\u6027\u81EA\u7136\u6709\u60C5\u4EB2\u4E4B\u7231\u3002\u4F8B\u5982\uFF1A\u7236\u6BCD\u4E0E\u513F\u5973\u4E4B\u95F4\u7684\u7231\u3002 \u2461 Phileo\uFF08\u53CB\u60C5\uFF09: \u670B\u53CB\u4E4B\u95F4\u7684\u7231\u3002\u542B\u6709\u670B\u53CB\u4E4B\u95F4\u771F\u5FC3\u7684\u5C0A\u656C\u3001\u8BA4\u540C\u3001\u8BDA\u6073\u3002\u8FD9\u7B49\u7231\u6765\u4E4B\u4EBA\u7684\u4E49\u6C14\uFF0C\u6CA1\u6709\u8840\u7F18\u5173\u7CFB\u7684\u56E0\u7D20\u3002 \u2462 Eros\uFF08\u7231\u60C5\uFF09: \u7537\u5973\u4E4B\u95F4\u604B\u60C5\u7684\u7231\u3002\u8FD9\u662F\u6765\u4E4B\u8089\u8EAB\u7684\u60C5\u611F\u4E0E\u604B\u6155\u3002\u8FD9\u79CD\u7231\u5728\u60C5\u611F\u4E0A\u975E\u5E38\u70ED\u70C8\uFF0C\u6700\u5FEB\u5F97\u6EE1\u8DB3\uFF0C\u4E5F\u6700\u5FEB\u5931\u53BB\u3002 \u2463 Agape\uFF08\u65E0\u6761\u4EF6\u7684\u7231\uFF09: \u5F62\u5BB9\u795E\u5BF9\u4EBA\u65E0\u6761\u4EF6\u7684\u7231\uFF0C\u4E5F\u8BF4\u662F\u727A\u7272\u7684\u5927\u7231\uFF0C\u4E13\u5BFB\u6C42\u4ED6\u4EBA\u7684\u76CA\u5904\u800C\u51FA\u53D1\u3002\u8FD9\u7B49\u7231\u662F\u5B8C\u5168\u51FA\u81EA\u4E0E\u5723\u7075\u3002</p><p>\u4E3B\u8036\u7A23\u8BF4\uFF1A\u201C\u4F60\u4EEC\u8981\u5F7C\u6B64\u76F8\u7231\uFF0C\u50CF\u6211\u7231\u4F60\u4EEC\u4E00\u6837...\u201D\u300A\u7EA615\uFF1A12\u300B\u300A\u7EA621\uFF1A15-19\u300B</p><ul><li>\u5F53\u6211\u4EEC\u4E3B\u8036\u7A23\u8BB2\u5230\u5723\u7075\u65F6\uFF0C\u7942\u7279\u522B\u5C06\u8FD9\u6837\u7684\u201C\u7231(Agape)\u201D\u4F20\u9012\u4E8E\u95E8\u5F92\uFF0C\u4F7F\u4ED6\u4EEC\u660E\u767D\u4E0A\u5E1D\u7231\u6211\u4EEC\u7684\u5FC3\u80A0\u662F\u5982\u4F55\u3002\u4F46\u662F\u4EBA\u5728\u5815\u843D\u7684\u5FC3\u7075\u65E0\u6CD5\u660E\u767D\u8FD9\u7B49\u7231\u3002\u4E4B\u540E\u4E3B\u8036\u7A23\u5728\u300A\u7EA621\uFF1A15-19\u300B\u95EE\u5F7C\u5F97\u8BF4\uFF0C\u201C\u4F60\u7231\uFF08Agape\uFF09\u6211\u5417\uFF1F\u201D\u5F7C\u5F97\u4FBF\u56DE\u7B54\u8BF4\uFF1A\u201C\u4E3B\u554A\uFF0C\u662F\u7684\uFF0C\u4F60\u77E5\u9053\u6211\u7231\uFF08Phileo\uFF09\u4F60\u3002\u201D\u8036\u7A23\u95EE\u4ED6\u4E09\u6B21\uFF0C\u5728\u5C06\u5F7C\u5F97\u672A\u6765\u7684\u4E8B\u544A\u8BC9\u4ED6\uFF0C\u8868\u793A\u5F7C\u5F97\u5C06\u6765\u4F1A\u660E\u767D\u8FD9Agape\u7231\uFF0C\u800C\u8363\u8000\u7684\u4E3A\u4E3B\u6B7B\u3002</li></ul><p>\u5C3D\u5FC3\u3001\u5C3D\u6027\u3001\u5C3D\u529B\u4E0D\u8BA9\u81EA\u5DF1\u5931\u53BB\u5FC3\u4E2D\u7684\u7231 \u2460 \u9996\u5148\uFF0C\u5728\u7231\u4E2D\u7EE7\u7EED\u589E\u957F\uFF0C\u5C06\u5BF9\u4EBA\u7684Storge\uFF08\u4EB2\u60C5\uFF09\uFF0CPhileo\uFF08\u53CB\u60C5\uFF09\uFF0CEros\uFF08\u7231\u60C5\uFF09\u8F6C\u4E3A\u4ECE\u4E3B\u9886\u4F1A\u7684\u65E0\u6761\u4EF6\u4E4B\u7231\u3002\u5728\u5BF9\u5F85\u5BB6\u4EBA\uFF0C\u591A\u56DE\u590D\u4E3B\u7231\u4ED6\u4EEC\u7684\u5FC3\u6765\u6D17\u4ED6\u4EEC\u7684\u811A\uFF1B\u5728\u4EBA\u7684\u53CB\u60C5\u91CC\u56DE\u590D\u798F\u97F3\u7684\u771F\u610F\uFF1B\u5728\u592B\u5987\u4E4B\u7231\u56DE\u590D\u4E3A\u5F7C\u6B64\u727A\u7272\u7684\u7231\uFF0C\u5982\u540C\u7231\u81EA\u5DF1\u7684\u8EAB\u4F53\u3002 \u2461 \u603B\u8981\u5F97\u4E3B\u7684\u5FC3\u770B\u5F85\u4E3B\u5185\u80A2\u4F53\u3002\u5F53\u660E\u767D\u4EBA\u662F\u8F6F\u5F31\uFF0C\u4E0D\u80FD\u4EE5\u81EA\u5DF1\u4E00\u5957\u7684\u51C6\u5219\u3001\u6807\u51C6\u3001\u9053\u5FB7\u5957\u5728\u80A2\u4F53\u8EAB\u4E0A\u3002\u603B\u8981\u601C\u60AF\uFF0C\u5F7C\u6B64\u6D17\u811A\u3002 \u2462 \u7231\u4EC7\u654C\u6216\u4F24\u5BB3\u8FC7\u81EA\u5DF1\u7684\u4EBA\u3002\u4E0D\u53EF\u5411\u4EBA\u542B\u7740\u6028\u6068\u6216\u51A4\u5C48\uFF0C\u56E0\u4E3A\u5728\u201C\u4E0D\u9976\u6055\u4E4B\u5FC3\u201D\uFF0C\u6CA1\u6709\u5723\u7075\u7684\u5DE5\u4F5C\u3002\u6CA1\u9519\uFF0C\u9976\u6055\u4EC7\u654C\u9700\u8981\u80FD\u529B\uFF0C\u6240\u4EE5\u6211\u4EEC\u66F4\u662F\u8981\u53CD\u601D\u4E3B\u4E00\u6B21\u53C8\u4E00\u6B21\u7684\u601C\u60AF\u3002\u5982\u540C\u6211\u4EEC\u7684\u4E3B\u8BA8\u538C\u7F6A\uFF0C\u5374\u7231\u7F6A\u4EBA\u3002\u6211\u4EEC\u867D\u4E0D\u9700\u7231\u4EBA\u7684\u8F6F\u5F31\uFF0C\u4F46\u5374\u5F53\u7231\u4EBA\u7684\u751F\u547D\u3002</p>',20),p=[t];function o(r,s,u,c,n,d){return e(),_("div",null,p)}var f=l(i,[["render",o]]);export{g as __pageData,f as default};
