import{_ as l,c as i,o as e,a as r}from"./app.70d86030.js";const m='{"title":"","description":"","frontmatter":{},"headers":[],"relativePath":"genesis/\u9884\u8868.md"}',a={},t=r('<p>\u5965\u53E4\u65AF\u4E01(St. Augustine)\u66FE\u8868\u793A: \u201C\u65E7\u7EA6\u9690\u85CF\u7740\u65B0\u7EA6, \u65B0\u7EA6\u63ED\u793A\u4E86\u65E7\u7EA6\u201D.[<a href="http://malaccagospelhall.org.my/2016/03/8399/#_ftn1" target="_blank" rel="noopener noreferrer">1]</a> \u8FD9\u53E5\u8BDD\u53EF\u4ECE\u5723\u7ECF\u4E2D\u7684\u9884\u8868\u52A0\u4EE5\u8BC1\u5B9E.[<a href="http://malaccagospelhall.org.my/2016/03/8399/#_ftn2" target="_blank" rel="noopener noreferrer">2]</a> \u82CF\u514B(Roy B. Zuck)\u6307\u51FA: \u201C\u8BB8\u591A\u7814\u8BFB\u5723\u7ECF\u7684\u4EBA\u90FD\u5BDF\u89C9\u5230\u65E7\u7EA6\u4E2D\u5305\u542B\u4E0D\u5C11\u9884\u8868, \u8FD9\u4E9B\u9884\u8868\u5728\u65B0\u7EA6\u4EE5\u67D0\u79CD\u65B9\u5F0F\u9610\u660E\u51FA\u6765. \u65E7\u7EA6\u4E0E\u65B0\u7EA6\u7684\u5173\u7CFB\u662F\u9884\u8868(types)\u4E0E\u672C\u4F53(antitypes), \u6216\u5F71\u513F(shadows)\u4E0E\u5B9E\u4F53(fulfillments)\u7684\u5173\u7CFB. \u4ECE\u65B0\u7EA6\u7684\u89D2\u5EA6\u56DE\u770B\u65E7\u7EA6\u4E2D\u7684\u4E00\u4E9B\u4EBA\u3001\u4E8B\u3001\u7269, \u4E0D\u96BE\u53D1\u73B0\u5B83\u4EEC\u662F\u5C06\u6765\u65B0\u7EA6\u7684\u4EBA\u3001\u4E8B\u3001\u7269\u4E4B\u9884\u8868\u6216\u5F71\u5B50.</p><p>\u4E3B\u8036\u7A23\u5728\u590D\u6D3B\u4E4B\u540E, \u66FE\u5411\u524D\u5F80\u4EE5\u9A6C\u5FE4\u65AF\u7684\u4E24\u4E2A\u95E8\u5F92\u663E\u73B0, \u8FD8\u4E3A\u4ED6\u4EEC\u8BB2\u89E3\u65E7\u7EA6\u5723\u7ECF, \u5305\u62EC\u5176\u4E2D\u7684\u9884\u8868, \u201C\u4E8E\u662F\u4ECE\u6469\u897F\u548C\u4F17\u5148\u77E5\u8D77, \u51E1\u7ECF\u4E0A\u6240\u6307\u7740\u81EA\u5DF1\u7684\u8BDD, \u90FD\u7ED9\u4ED6\u4EEC\u8BB2\u89E3\u660E\u767D\u4E86\u201D(\u8DEF24:27). \u6839\u636E\u6B64\u4E8B, \u6211\u4EEC\u6709\u7406\u7531\u76F8\u4FE1\u65E7\u7EA6\u5723\u7ECF\u7684\u8BB8\u591A\u4EBA\u3001\u4E8B\u3001\u7269\u5177\u6709\u66F4\u6DF1\u4E00\u5C42\u7684\u610F\u4E49 \u2014 \u9884\u8868\u90A3\u4F4D\u5C06\u8981\u9053\u6210\u8089\u8EAB\u7684\u4E3B\u8036\u7A23\u57FA\u7763(\u676510:7; \u8BD740:7). \u56E0\u6B64, \u6211\u4EEC\u7814\u8BFB\u65E7\u7EA6\u5723\u7ECF\u65F6, \u5176\u4E2D\u4E00\u4EF6\u6700\u7F8E\u7684\u4E8B, \u5C31\u662F\u5BFB\u6C42\u7ECF\u6587\u4E2D\u6240\u8981\u9884\u8868\u7684\u4E3B\u8036\u7A23\u57FA\u7763.</p><p>\u4E03\u6B21\u9965\u8352 \u8FEB\u4F7F\u72B9\u592A\u4EBA\u79BB\u5F00\u8FE6\u5357\u5730\uFF0C\u5F52\u5411\u7EA6\u745F</p><p>\u4E03\u707E \u8FEB\u4F7F\u771F\u6B63\u5728\u6551\u6069\u4E2D\u89C9\u9192\u7684\u4EBA\uFF0C\u5F52\u5411\u8036\u7A23</p><ol><li>\u201C\u7EA6\u745F\u201D\u8FD9\u540D\u7684\u610F\u601D\u662F\u201C\u589E\u6DFB\u201D(\u521B30:24);[<a href="http://malaccagospelhall.org.my/2016/03/8399/#_ftn5" target="_blank" rel="noopener noreferrer">5]</a> \u4E3B\u8036\u7A23\u4F5C\u5B69\u7AE5\u65F6, \u7942\u7684\u667A\u6167\u4EE5\u53CA\u201C\u795E\u548C\u4EBA\u559C\u7231\u7942\u7684\u5FC3\u201D\u4E0D\u65AD\u589E\u6DFB(\u8DEF2:52). \u7942\u5347\u5929\u590D\u6D3B\u540E\u6240\u5F97\u7684\u5C0A\u8D35\u548C\u8363\u8000\u66F4\u662F\u4E0D\u65AD\u589E\u6DFB(\u542F5:12-13).</li><li>\u7EA6\u745F\u7267\u517B\u7FA4\u7F8A(\u521B37:2); \u800C\u4E3B\u8036\u7A23\u662F\u597D\u7267\u8005(\u7EA610:11,14).</li><li>\u7EA6\u745F\u62A5\u544A\u54E5\u54E5\u4EEC\u7684\u6076\u884C(\u521B37:2); \u4E3B\u8036\u7A23\u4E5F\u7167\u660E\u4EBA\u7684\u6076\u884C(\u7EA63:19,20).</li><li>\u7EA6\u745F\u8499\u7236\u6240\u7231(\u521B37:3); \u4E3B\u8036\u7A23\u88AB\u7236\u79F0\u4E3A\u201C\u6211\u7684\u7231\u5B50\u201D(\u592A3:17).</li><li>\u7EA6\u745F\u88AB\u5144\u5F1F\u4EEC(\u54E5\u54E5\u4EEC)\u6240\u618E\u6076(\u521B37:4-5); \u4E3B\u8036\u7A23\u88AB\u4EBA\u65E0\u6545\u5730\u6068\u6076(\u7EA615:25).</li><li>\u7EA6\u745F\u4E0D\u88AB\u5144\u5F1F\u4EEC\u6240\u4FE1\u4EFB(\u521B37:5); \u4E3B\u8036\u7A23\u7684\u5F1F\u5144\u4EEC\u4E5F\u4E0D\u4FE1\u7942(\u7EA67:5).</li><li>\u7EA6\u745F\u5F97\u5230\u9AD8\u4E3E(\u521B37:7,9); \u4E3B\u8036\u7A23\u5728\u51E1\u4E8B\u4E0A\u9AD8\u5C45\u9996\u4F4D(\u897F1:18).</li><li>\u7EA6\u745F\u88AB\u5144\u5F1F\u4EEC\u62D2\u7EDD(\u521B37:8); \u4E3B\u8036\u7A23\u4E5F\u88AB\u81EA\u5DF1\u7684\u4EBA\u62D2\u7EDD(\u8DEF19:14).</li><li>\u7EA6\u745F\u88AB\u5144\u5F1F\u4EEC\u6240\u5AC9\u5992(\u521B37:11); \u4E3B\u8036\u7A23\u4EA6\u88AB\u4EBA\u6240\u5AC9\u5992(\u53EF15:10).</li><li>\u7EA6\u745F\u7684\u7236\u4EB2\u628A\u7EA6\u745F\u7684\u8BDD\u5B58\u5728\u5FC3\u91CC(\u521B37:11); \u4E3B\u8036\u7A23\u7684\u6BCD\u4EB2\u5219\u628A\u4E00\u5207\u4E8B\u90FD\u5B58\u5728\u5FC3\u91CC(\u8DEF2:51).</li><li>\u7EA6\u745F\u88AB\u7236\u4EB2\u5DEE\u9063\u5230\u4ED6\u5F1F\u5144\u4EEC\u90A3\u91CC(\u521B37:13); \u4E3B\u8036\u7A23\u4E5F\u88AB\u7236\u795E\u5DEE\u9063, \u53BB\u5230\u7942\u7684\u5F1F\u5144\u4EEC(\u72B9\u592A\u4EBA)\u5F53\u4E2D(\u8DEF20:13;[<a href="http://malaccagospelhall.org.my/2016/03/8399/#_ftn6" target="_blank" rel="noopener noreferrer">6]</a> \u53C2 \u7EA63:16).</li><li>\u7EA6\u745F(\u9884\u5907\u597D\u4E8B\u5949\u795E)\u56DE\u7B54\u8BF4: \u201C\u6211\u5728\u8FD9\u91CC\u201D(\u521B37:13); \u4E3B\u8036\u7A23\u4E5F\u9884\u5907\u597D\u732E\u4E0A\u81EA\u5DF1, \u7942\u8BF4: \u201C\u770B\u54EA, \u6211\u6765\u4E86\u201D(\u8BD740:7-8).</li><li>\u7EA6\u745F\u51FA\u5E0C\u4F2F\u4ED1\u8C37(\u5E0C\u4F2F\u4ED1\u610F\u5373\u201C\u4EA4\u901A\u3001\u60C5\u8C0A\u201D), \u5F80\u793A\u5251\u53BB(\u521B37:14); \u4E3B\u8036\u7A23\u79BB\u5F00\u5929\u5802, \u653E\u4E0B\u4E0E\u7236\u795E\u540C\u4EAB\u7684\u8363\u8000\u4E0E\u76F8\u4EA4(\u7EA617:5,24), \u53BB\u5230\u6492\u739B\u5229\u4E9A\u7684\u4E00\u5EA7\u57CE, \u540D\u53EB\u53D9\u52A0(Sychar, \u6709\u8005\u8BA4\u4E3A\u6B64\u5904\u7684\u201C\u53D9\u52A0\u201D\u5373\u662F\u793A\u5251(Shechem).</li><li>\u7EA6\u745F\u5BFB\u627E\u4ED6\u7684\u5144\u5F1F\u4EEC(\u521B37:16); \u4E3B\u8036\u7A23\u5BFB\u627E\u548C\u62EF\u6551\u5931\u4E27\u7684\u4EBA(\u8DEF19:10).</li><li>\u7EA6\u745F\u5BFB\u627E\u5144\u5F1F\u4EEC\u76F4\u5230\u627E\u7740\u4ED6\u4EEC(\u521B37:17); \u4E3B\u8036\u7A23\u5BFB\u627E\u5931\u4E27\u7684\u4EBA\u76F4\u5230\u627E\u7740\u4E86(\u8DEF15:4).</li><li>\u4ED6\u4EEC\u540C\u8C0B\u9677\u5BB3\u7EA6\u745F(\u521B37:18); \u4EBA\u4EEC\u5546\u8BAE\u9677\u5BB3\u4E3B\u8036\u7A23(\u592A27:1; \u7EA611:53).</li><li>\u4ED6\u4EEC\u5265\u4E86\u7EA6\u745F\u7684\u5916\u8863(\u521B37:23); \u4EBA\u4EEC\u5265\u4E86\u4E3B\u8036\u7A23\u7684\u5916\u8863(\u592A27:28,35).</li><li>\u4ED6\u4EEC\u5728\u90A3\u91CC\u5750\u4E0B(\u521B37:25); \u7F57\u9A6C\u5175\u4E01\u5750\u5728\u90A3\u91CC\u770B\u5B88\u4E3B\u8036\u7A23(\u592A27:36).</li><li>\u7EA6\u745F\u88AB\u4EBA\u752820\u4E2A\u94F6\u5B50\u5356\u4E86(\u521B37:28; KJV: twenty pieces of silver); \u4E3B\u8036\u7A23\u88AB\u4EBA\u752830\u4E2A\u94F6\u5B50\u51FA\u5356\u4E86(\u539F\u6587\u76F4\u8BD1, \u592A26:15; KJV: thirty pieces of silver).</li><li>\u7EA6\u745F\u4F5C\u62A4\u536B\u957F\u6CE2\u63D0\u4E4F\u7684\u4EC6\u4EBA(\u521B39:1); \u4E3B\u8036\u7A23\u53D6\u4E86\u5974\u4EC6\u7684\u5F62\u50CF(\u81532:7), \u7518\u4F5C\u4EC6\u4EBA\u670D\u4E8B\u4EBA(\u53EF10:45; \u8DEF22:27).[<a href="http://malaccagospelhall.org.my/2016/03/8399/#_ftn7" target="_blank" rel="noopener noreferrer">7]</a></li><li>\u8036\u548C\u534E\u795E\u4E0E\u7EA6\u745F\u540C\u5728(\u521B39:2,21,23); \u7236\u795E\u4E0E\u4E3B\u8036\u7A23\u540C\u5728(\u7EA616:32).</li><li>\u8036\u548C\u534E\u4F7F\u7EA6\u745F\u624B\u91CC\u6240\u529E\u7684\u5C3D\u90FD\u987A\u5229(\u521B39:3); \u8036\u548C\u534E\u6240\u559C\u60A6\u7684\u4E8B, \u5FC5\u5728\u4E3B\u8036\u7A23\u624B\u4E2D\u4EA8\u901A(\u8D5B53:10).</li><li>\u4E00\u5207\u6240\u6709\u7684\u90FD\u4EA4\u5728\u7EA6\u745F\u624B\u91CC(\u521B39:4); \u7236\u795E\u5DF2\u5C06\u4E07\u6709(\u4E00\u5207)\u4EA4\u5728\u4E3B\u8036\u7A23\u624B\u91CC(\u7EA63:35).</li><li>\u4EBA\u56E0\u7EA6\u745F\u7684\u7F18\u6545\u4ECE\u795E\u5F97\u798F(\u521B39:5); \u4EBA\u56E0\u4E3B\u8036\u7A23\u7684\u7F18\u6545\u4ECE\u795E\u5F97\u798F(\u5F171:3).</li><li>\u7EA6\u745F\u539F\u6765\u79C0\u96C5\u4FCA\u7F8E, \u6108\u6765\u6108\u5F97\u4EBA\u7684\u559C\u7231(\u521B39:6); \u4E3B\u8036\u7A23\u5728\u5C5E\u7075\u54C1\u683C\u65B9\u9762\u53EF\u8BF4\u662F\u201C\u5168\u7136\u7F8E\u4E3D\u201D(\u6B4C5:16), \u53EB\u795E\u548C\u4EBA\u5BF9\u4E3B\u8036\u7A23\u7684\u559C\u7231\u65E5\u76CA\u52A0\u589E(\u8DEF2:52).</li><li>\u7EA6\u745F\u53D7\u8BD5\u63A2, \u88AB\u4E3B\u4EBA\u7684\u59BB\u5B50\u5F15\u8BF1(\u521B39:7-12); \u4E3B\u8036\u7A23\u53D7\u8BD5\u63A2, \u88AB\u9B54\u9B3C\u5F15\u8BF1(\u592A4:1-10; \u67654:15).</li><li>\u7EA6\u745F\u5728\u8BD5\u63A2\u4E2D\u5E76\u4E0D\u72AF\u7F6A(\u521B39:9: \u201C\u6211\u600E\u80FD\u4F5C\u8FD9\u5927\u6076, \u5F97\u7F6A\u795E\u5462?\u201D); \u4E3B\u8036\u7A23\u4E5F\u51E1\u4E8B\u53D7\u8FC7\u8BD5\u63A2, \u53EA\u662F\u7942\u6CA1\u6709\u72AF\u7F6A(\u67654:15; \u5F7C\u524D2:22).</li><li>\u7EA6\u745F\u6CA1\u6709\u72AF\u7F6A, \u5374\u88AB\u5B9A\u7F6A\u800C\u542B\u51A4\u53D7\u82E6(\u521B40:15); \u4E3B\u8036\u7A23\u4E5F\u6CA1\u72AF\u7F6A, \u5374\u88AB\u5B9A\u7F6A\u800C\u542B\u51A4\u53D7\u82E6(\u7EA68:46; 19:4,6).</li><li>\u7EA6\u745F\u7684\u811A\u53D7\u5230\u521B\u4F24(\u8BD760:18: \u201C\u4EBA\u7528\u811A\u9563\u4F24\u4ED6\u7684\u811A\u201D); \u4E3B\u8036\u7A23\u7684\u811A\u4E5F\u53D7\u5230\u521B\u4F24(\u8BD7\u7BC722:16: \u201C\u4ED6\u4EEC\u624E\u4E86\u6211\u7684\u624B, \u6211\u7684\u811A\u201D).</li><li>\u7EA6\u745F\u5728\u76D1\u72F1\u65F6\u6709\u4E24\u4E2A\u72AF\u4EBA\u4E0E\u4ED6\u540C\u56DA(\u5373\u6CD5\u8001\u7684\u4E24\u4E2A\u5927\u81E3, \u521B40:2-3); \u4E3B\u8036\u7A23\u88AB\u9489\u5341\u67B6\u65F6\u5DE6\u53F3\u4E24\u65C1\u4E5F\u6709\u4E24\u4E2A\u72AF\u4EBA(\u8DEF23:32).</li><li>\u7EA6\u745F\u4F3A\u5019\u4ED6\u4EEC(\u521B40:4); \u4E3B\u8036\u7A23\u5728\u95E8\u5F92\u4E2D\u95F4\u5982\u540C\u670D\u4E8B\u4EBA\u7684(\u8DEF22:27).</li><li>\u4E0E\u7EA6\u745F\u540C\u56DA\u7684\u5176\u4E2D\u4E00\u4E2A\u72AF\u4EBA(\u9152\u653F)\u5F97\u7740\u597D\u6D88\u606F \u2014 \u751F\u547D\u5F97\u4EE5\u4FDD\u5168(\u521B40:13; \u53E6\u4E00\u4EBA\u5219\u6B7B); \u4E3B\u8036\u7A23\u5341\u67B6\u65C1\u7684\u5176\u4E2D\u4E00\u4E2A\u72AF\u4EBA\u4E5F\u5F97\u7740\u597D\u6D88\u606F \u2014 \u5C5E\u7075\u751F\u547D\u5F97\u4EE5\u4FDD\u5168(\u6307\u7075\u9B42\u5F97\u6551, \u8DEF23:43).</li><li>\u7EA6\u745F\u8981\u9152\u653F\u5F97\u597D\u5904\u65F6\u8BB0\u5FF5\u4ED6(\u521B40:14); \u4E3B\u8036\u7A23\u8981\u4EBA\u8499\u6069\u5F97\u6551\u540E\u8BB0\u5FF5\u7942(\u6797\u524D11:24-25).</li><li>\u9152\u653F\u8499\u6069\u590D\u804C\u540E\u7ADF\u5FD8\u8BB0\u7EA6\u745F(\u521B40:23); \u4E5D\u4E2A\u8499\u6069\u7684\u5927\u9EBB\u98CE\u75C5\u4EBA\u5FD8\u8BB0\u4E3B\u8036\u7A23\u7684\u6069\u60C5(\u8DEF17:17).</li><li>\u5728\u7EA6\u745F\u7684\u4E8B\u4E0A, \u4E0E\u7EA6\u745F\u540C\u56DA\u7684\u90A3\u4E2A\u72AF\u4EBA(\u9152\u653F)\u627F\u8BA4\u81EA\u5DF1\u7684\u7F6A(\u521B41:9,12); \u5728\u4E3B\u8036\u7A23\u7684\u4E8B\u4E0A, \u90A3\u540C\u9489\u7684\u72AF\u4EBA\u627F\u8BA4\u81EA\u5DF1\u7684\u7F6A(\u8DEF23:41).</li><li>\u738B\u91CA\u653E\u7EA6\u745F, \u8131\u79BB\u76D1\u72F1\u7684\u76D1\u7981(\u8BD7105:20); \u795E\u91CA\u653E\u4E3B\u8036\u7A23, \u8131\u79BB\u6B7B\u4EA1\u7684\u62D8\u7981(\u5F922:24: \u201C\u5C06\u6B7B\u7684\u75DB\u82E6\u89E3\u91CA\u4E86, \u53EB\u7942\u590D\u6D3B, \u56E0\u4E3A\u7942\u539F\u4E0D\u80FD\u88AB\u6B7B\u62D8\u7981\u201D).</li><li>\u7EA6\u745F\u627F\u8BA4\u81EA\u5DF1\u6240\u80FD\u505A\u7684\u4E00\u5207\u5168\u5728\u4E4E\u795E, \u4E0D\u5728\u4E4E\u4ED6\u81EA\u5DF1(\u521B41:16); \u4E3B\u8036\u7A23\u627F\u8BA4\u4E0D\u80FD\u51ED\u81EA\u5DF1\u505A\u4EC0\u4E48, \u4E00\u5207\u4E43\u5728\u4E4E\u795E(\u7EA65:19).</li><li>\u7EA6\u745F\u6709\u795E\u7684\u7075\u5728\u4ED6\u91CC\u9762(\u521B41:25); \u4E3B\u8036\u7A23\u6709\u5723\u7075\u7684\u6069\u818F, \u6709\u5723\u7075\u5728\u7942\u8EAB\u4E0A(\u5F9210:38; \u8DEF4:18). \u4E3B\u8036\u7A23\u6709\u795E\u7684\u4E03\u7075(\u542F3:1; \u7F16\u8005\u6CE8: \u201C\u4E03\u201D\u8C61\u5F81\u5B8C\u7F8E\u3001\u5B8C\u6574\u3001\u5B8C\u6EE1\u3001\u4E30\u76DB\u7B49, \u6545\u201C\u6709\u795E\u7684\u4E03\u7075\u201D\u8C61\u5F81\u4E3B\u8036\u7A23\u6709\u5B8C\u7F8E\u5723\u7075\u7684\u4E30\u76DB\u5B8C\u6EE1).</li><li>\u795E\u5DF2\u5C06\u8FD9\u4E00\u5207\u4E8B\u90FD\u6307\u793A\u7EA6\u745F(\u521B41:39); \u7236\u795E\u5DF2\u5C06\u81EA\u5DF1\u6240\u505A\u7684\u4E00\u5207\u4E8B\u90FD\u6307\u793A\u4E3B\u8036\u7A23(\u7EA65:20).</li><li>\u6CA1\u6709\u4EBA\u50CF\u7EA6\u745F\u90A3\u6837\u6709\u806A\u660E\u548C\u667A\u6167(\u521B41:39); \u4E00\u5207\u7684\u667A\u6167\u548C\u77E5\u8BC6\u90FD\u85CF\u5728\u4E3B\u8036\u7A23\u91CC\u9762(\u897F2:3).</li><li>\u7EA6\u745F\u638C\u7BA1(\u6CBB\u7406)\u6CD5\u8001\u7684\u5BB6(\u521B41:40); \u4E3B\u8036\u7A23\u6CBB\u7406\u795E\u7684\u5BB6(\u67653:6).</li><li>\u7EA6\u745F\u6210\u4E3A\u57C3\u53CA\u7684\u5BB0\u76F8, \u5728\u4E00\u4EBA\u4E4B\u4E0B, \u4E07\u4EBA\u4E4B\u4E0A(\u521B41:40); \u4E3B\u8036\u7A23\u5F97\u7740\u8D85\u4E4E\u4E07\u540D\u4E4B\u4E0A\u7684\u540D(\u81532:9); \u9664\u4E86\u7236\u795E\u4EE5\u5916(\u6797\u524D15:27-28),[<a href="http://malaccagospelhall.org.my/2016/03/8399/#_ftn8" target="_blank" rel="noopener noreferrer">8]</a> \u4E07\u7269\u90FD\u670D\u5728\u7942\u7684\u811A\u4E0B(\u67652:8-10).</li><li>\u6CD5\u8001\u6D3E\u7EA6\u745F\u6CBB\u7406\u57C3\u53CA\u5168\u5730(\u521B41:41); \u7236\u795E\u6D3E\u4E3B\u8036\u7A23\u4F5C\u738B, \u6CBB\u7406\u4E07\u7269(\u6797\u524D15:25-27; \u5F171:22).</li><li>\u57C3\u53CA\u5168\u5730\u7684\u767E\u59D3\u5411\u7EA6\u745F\u8DEA\u4E0B(\u521B41:43); \u672A\u6765\u4E07\u7269(\u4E07\u819D)\u5C06\u5411\u4E3B\u8036\u7A23\u8DEA\u62DC(\u81532:10).</li><li>\u7EA6\u745F\u79EF\u84C4\u7684\u4E94\u8C37\u751A\u591A, \u4E0D\u53EF\u80DC\u6570, \u5982\u540C\u6D77\u8FB9\u7684\u6C99, \u65E0\u6CD5\u8BA1\u7B97(\u521B41:49); \u4E3B\u8036\u7A23\u6709\u6D4B\u4E0D\u900F(\u6570\u4E0D\u5C3D)\u7684\u4E30\u5BCC(\u5F173:8).</li><li>\u7EA6\u745F\u5F97\u4E86\u4E24\u4E2A\u513F\u5B50, \u957F\u5B50\u540D\u53EB\u739B\u62FF\u897F(\u5C31\u662F\u4F7F\u4E4B\u5FD8\u4E86[forgetting]\u7684\u610F\u601D), \u56E0\u4E3A\u4ED6\u8BF4: \u201C\u795E\u4F7F\u6211\u5FD8\u4E86\u4E00\u5207\u7684\u56F0\u82E6\u201D(\u521B41:51); \u4E3B\u8036\u7A23\u4F3C\u4E4E\u4E5F\u5FD8\u4E86\u4E00\u5207\u82E6\u695A, \u56E0\u201C\u7942\u5FC5\u770B\u89C1\u81EA\u5DF1\u52B3\u82E6\u7684\u529F\u6548, \u4FBF\u5FC3\u6EE1\u610F\u8DB3\u201D(\u8D5B53:11).</li><li>\u7EA6\u745F\u6B21\u5B50\u540D\u53EB\u4EE5\u6CD5\u83B2(\u5C31\u662F\u4F7F\u4E4B\u660C\u76DB[fruitful, \u591A\u7ED3\u679C\u5B50]\u7684\u610F\u601D), \u56E0\u4E3A\u4ED6\u8BF4: \u201C\u795E\u4F7F\u6211\u5728\u53D7\u82E6\u7684\u5730\u65B9\u660C\u76DB\u201D(\u521B41:52); \u4E3B\u8036\u7A23\u4E5F\u660C\u76DB, \u591A\u7ED3\u679C\u5B50, \u56E0\u7942\u4EE3\u6B7B\u540E\u201C\u5C31\u7ED3\u51FA\u8BB8\u591A\u5B50\u7C92\u6765\u201D(\u7EA612:24).</li><li>\u6CD5\u8001\u8981\u6240\u6709\u6C42\u52A9\u7684\u4F17\u6C11\u201C\u5F80\u7EA6\u745F\u90A3\u91CC\u53BB\u201D(\u521B41:55); \u5F7C\u5F97\u5BF9\u4E3B\u8036\u7A23\u8BF4: \u201C\u4E3B\u554A, \u7962\u6709\u6C38\u751F\u4E4B\u9053, \u6211\u4EEC\u8FD8\u5F52\u4ECE\u8C01\u5462?\u201D(\u7EA66:68; \u6362\u8A00\u4E4B, \u4ED6\u4EEC\u4E0D\u5F80\u522B\u4EBA\u90A3\u91CC\u53BB, \u53EA\u5F52\u5411\u4E3B).</li><li>\u6CD5\u8001\u5BF9\u4F17\u6C11\u8BF4: \u201C\u51E1\u4ED6(\u7EA6\u745F)\u6240\u8BF4\u7684, \u4F60\u4EEC\u90FD\u8981\u505A\u201D(\u521B41:55); \u9A6C\u5229\u4E9A\u5BF9\u7528\u4EBA\u8BF4: \u201C\u4ED6(\u4E3B\u8036\u7A23)\u544A\u8BC9\u4F60\u4EEC\u4EC0\u4E48, \u4F60\u4EEC\u5C31\u505A\u4EC0\u4E48\u201D(\u7EA62:5); \u7236\u795E\u4E5F\u8BF4: \u201C\u8FD9\u662F\u6211\u7684\u7231\u5B50, \u6211\u6240\u559C\u60A6\u7684. \u4F60\u4EEC\u8981\u542C\u7942\u201D(\u592A17:5).</li><li>\u7EA6\u745F\u201C\u5F00\u4E86\u5404\u5904\u7684\u4ED3\u201D, \u4F9B\u5E94\u7CAE\u98DF\u7ED9\u4EBA(\u521B41:56); \u4E3B\u8036\u7A23\u201C\u6253\u5F00\u5723\u7ECF\u5C5E\u7075\u7684\u4ED3\u5E93\u201D \u2014 \u8BB2\u89E3\u5723\u7ECF \u2014 \u4F9B\u5E94\u7075\u7CAE\u7ED9\u4EBA(\u8DEF24:27,32).</li><li>\u5404\u5730\u7684\u4EBA\u90FD\u5411\u7EA6\u745F\u5356\u7CAE, \u5F97\u7740\u62EF\u6551(\u521B41:57); \u4E3B\u8036\u7A23\u65BD\u884C\u795E\u7684\u6551\u6069, \u76F4\u5230\u5730\u6781(\u5373\u5168\u5730\u7684\u4EBA\u90FD\u501F\u7740\u7942\u5F97\u5230\u62EF\u6551, \u8D5B49:6).</li><li>\u7EA6\u745F\u8BA4\u8BC6\u7942\u7684\u5144\u5F1F\u4EEC(\u521B42:7-8); \u4E3B\u8036\u7A23\u8BA4\u8BC6\u6240\u6709\u4EBA(\u7EA62:24-25: \u201C\u56E0\u4E3A\u7942\u77E5\u9053\u4E07\u4EBA\u2026 \u56E0\u7942\u77E5\u9053\u4EBA\u5FC3\u91CC\u6240\u5B58\u7684\u201D).</li><li>\u7EA6\u745F\u7684\u4F17\u5144\u5F1F\u4E0D\u8BA4\u5F97\u7EA6\u745F(\u521B42:8); \u4E16\u4EBA\u4E0D\u8BA4\u8BC6\u4E3B\u8036\u7A23; \u7942\u5230\u81EA\u5DF1\u7684\u5730\u65B9\u6765, \u81EA\u5DF1\u7684\u4EBA\u5012\u4E0D\u63A5\u5F85\u7942(\u7EA61:10-11).</li><li>\u7EA6\u745F\u5411\u5144\u5F1F\u4EEC\u8BF4\u4E25\u5389\u7684\u8BDD, \u628A\u4ED6\u4EEC\u4E0B\u5728\u76D1\u91CC\u4E09\u5929(\u521B42:7,17); \u4E3B\u8036\u7A23\u5C06\u501F\u7740\u4E03\u5E74\u707E\u96BE\u6765\u4E25\u5389\u5BF9\u5F85\u4EE5\u8272\u5217\u4EBA, \u4F7F\u4ED6\u4EEC\u53D7\u82E6\u6094\u6539(\u803630:7).</li><li>\u6D41\u7EA6\u745F\u7684\u8840\u4E4B\u7F6A\u5411\u4ED6\u7684\u5144\u5F1F\u4EEC\u8FFD\u8BA8(\u521B42:22); \u6D41\u4E3B\u8036\u7A23\u4E4B\u8840\u7684\u7F6A\u5411\u6740\u5BB3\u7942\u7684\u4EE5\u8272\u5217\u4EBA(\u72B9\u592A\u4EBA)\u8FFD\u8BA8(\u592A 27:25).</li><li>\u7EA6\u745F\u79BB\u5F00\u4ED6\u4EEC, \u7136\u540E\u54ED\u6CE3(\u521B42:24); \u4E3B\u8036\u7A23\u770B\u89C1\u8036\u8DEF\u6492\u51B7\u5C31\u4E3A\u6B64\u57CE\u54C0\u54ED(\u56E0\u7942\u60F3\u5230\u6B64\u57CE\u7684\u4EBA\u5C06\u9762\u4E34\u60B2\u60E8\u906D\u9047, \u8DEF19:41).</li><li>\u7EA6\u745F\u547D\u4EE4\u8981\u628A\u4ED6\u4EEC\u7684\u888B\u5B50\u7ED9\u88C5\u6EE1(\u521B42:25); \u6211\u4EEC\u4ECE\u4E3B\u8036\u7A23\u90A3\u91CC\u9886\u53D7\u4E30\u6EE1\u7684\u6069\u5178(\u7EA61:16; \u4E5F\u53C2 \u8BD7147:14: \u201C\u7528\u4E0A\u597D\u7684\u9EA6\u5B50\u4F7F\u4F60\u6EE1\u8DB3\u201D).</li><li>\u7EA6\u745F\u628A\u6240\u6709\u94B1\u8FD8\u7ED9\u4ED6\u4EEC(\u521B42:25); \u4EBA\u4EEC\u201C\u4E0D\u7528\u94F6\u94B1\u201D\u4FBF\u53EF\u5F97\u5230\u4E3B\u8036\u7A23\u7684\u7F8E\u597D\u4F9B\u5E94(\u8D5B55:1).</li><li>\u7EA6\u745F\u7ED9\u4ED6\u4EEC\u8DEF\u9014\u4E0A\u7684\u4F9B\u5E94(\u521B42:25); \u5728\u57FA\u7763\u8036\u7A23\u91CC, \u6211\u4EEC\u4E00\u5207\u6240\u9700\u7528\u7684\u90FD\u5F97\u4EE5\u5145\u8DB3(\u81534:19).</li><li>\u7EA6\u745F\u628A\u8FD9\u4E9B\u4EBA\u5E26\u56DE\u5BB6(\u521B43:16); \u4E3B\u8036\u7A23\u8BF7\u4EBA\u8FDB\u6765, \u5750\u6EE1\u7942\u7684\u5C4B\u5B50(\u8DEF14:23).</li><li>\u7EA6\u745F\u9884\u5907\u597D, \u8BF7\u4ED6\u4EEC\u7528\u9910(\u521B43:16); \u4E3B\u8036\u7A23\u5DF2\u628A\u5404\u6837\u9700\u7528\u90FD\u9884\u5907\u597D\u4E86, \u8BF7\u4EBA\u6765\u8D74\u5E2D(\u592A22:4).</li><li>\u4ED6\u4EEC\u63A5\u8FD1\u7BA1\u5BB6, \u4E0E\u7BA1\u5BB6\u4EA4\u8C08(\u521B43:19); \u4E3B\u8036\u7A23\u501F\u7740\u5723\u7075\u6559\u5BFC\u4FE1\u5F92\u5404\u6837\u771F\u7406(\u7EA614:26).</li><li>\u4ED6\u4EEC\u5E26\u4E86\u81EA\u5DF1\u7684\u94B1\u6765(\u521B43:22); \u72B9\u592A\u4EBA\u60F3\u8981\u4EE5\u672C\u8EAB\u7684\u5584\u884C\u6765\u7ACB\u81EA\u5DF1\u7684\u4E49(\u7F5710:3).</li><li>\u7EA6\u745F\u5411\u4ED6\u4EEC\u95EE\u5B89, \u53EB\u4ED6\u4EEC\u4E0D\u8981\u5BB3\u6015(\u521B43:23); \u4E3B\u8036\u7A23\u544A\u8BC9\u4EBA\u4E0D\u8981\u5BB3\u6015(\u592A14:27); \u56E0\u4FE1\u4E3B\u8036\u7A23, \u4FE1\u5F92\u7684\u5FC3\u5145\u6EE1\u8BF8\u822C\u559C\u4E50\u548C\u5E73\u5B89(\u7F5715:13).</li><li>\u4ED6\u4EEC\u548C\u7EA6\u745F\u4E00\u540C\u5BB4\u4E50(\u521B43:34); \u8EAB\u4E3A\u597D\u7267\u4EBA, \u4E3B\u8036\u7A23\u56E0\u5DF2\u627E\u56DE\u5931\u843D\u7684\u7F8A(\u6307\u5931\u4E27\u8005)\u800C\u4E0E\u4F17\u4EBA(\u53EF\u6307\u4F17\u5929\u4F7F)\u4E00\u540C\u6B22\u559C\u5FEB\u4E50(\u8DEF15:6,9,24).</li><li>\u72B9\u5927\u627F\u8BA4\u795E\u5DF2\u67E5\u51FA\u4ED6\u4EEC(\u5144\u5F1F\u4EEC)\u7684\u7F6A\u6765(\u521B44:16); \u4E3B\u8036\u7A23\u501F\u7740\u5723\u7075\u4F7F\u4EBA\u77E5\u7F6A(\u7EA616:8-9).</li><li>\u7EA6\u745F\u548C\u5144\u5F1F\u4EEC\u76F8\u8BA4(\u521B45:1); \u4E3B\u8036\u7A23\u4F7F\u7942\u7684\u95E8\u5F92\u773C\u775B\u660E\u4EAE, \u8BA4\u51FA\u4E3B\u8036\u7A23(\u8DEF24:31).</li><li>\u7EA6\u745F\u8981\u5144\u5F1F\u4EEC\u8FD1\u524D\u6765(\u521B45:4); \u6211\u4EEC\u9760\u7740\u4E3B\u8036\u7A23\u7684\u8840\u5F97\u4EE5\u4EB2\u8FD1\u795E(\u5F172:13).</li><li>\u7EA6\u745F\u8BF4\u4ED6\u662F\u5144\u5F1F\u4EEC\u6240\u5356(\u5230\u57C3\u53CA)\u7684\u7EA6\u745F(\u521B45:4); \u4E3B\u8036\u7A23\u544A\u8BC9\u4FDD\u7F57(\u626B\u7F57)\u7942\u5C31\u662F\u4FDD\u7F57\u6240\u903C\u8FEB\u7684\u8036\u7A23(\u5F929:5).</li><li>\u7EA6\u745F\u8BF4\u662F\u795E\u5DEE\u9063\u4ED6\u6765\u5230\u57C3\u53CA(\u521B45:5,7); \u4E3B\u8036\u7A23\u4E5F\u662F\u8499\u795E\u5DEE\u9063, \u6765\u5230\u4E16\u95F4(\u7EA6\u58F94:9).</li><li>\u795E\u5927\u65BD\u62EF\u6551(\u521B45:7); \u4E3B\u8036\u7A23\u62EF\u6551\u4FE1\u5F92\u8131\u79BB\u90A3\u6781\u5927\u7684\u6B7B\u4EA1(\u6797\u540E1:10).</li><li>\u7EA6\u745F\u88AB\u5DEE\u9063\u5230\u57C3\u53CA\u6765, \u4E0D\u662F\u51FA\u4E8E\u4ED6\u7684\u5144\u5F1F\u4EEC, \u4E43\u662F\u51FA\u4E8E\u795E(\u521B45:8); \u4E3B\u8036\u7A23\u88AB\u5DEE\u9063\u5230\u4E16\u4E0A\u6765, \u4E43\u662F\u6309\u7740\u795E\u7684\u5B9A\u65E8\u548C\u5148\u89C1(\u5F922:23).</li><li>\u7EA6\u745F\u5728\u521B\u4E16\u8BB045\u7AE0\u67095\u6B21\u63D0\u53CA\u7236\u4EB2\u96C5\u5404(\u521B45:3,9,13,18,19), \u8DB3\u89C1\u7EA6\u745F\u6709\u7231\u7236\u4E4B\u5FC3, \u65F6\u5E38\u8BB0\u5FF5\u7236; \u4E3B\u8036\u7A23\u4E5F\u6DF1\u7231\u7236(\u6307\u7236\u795E), \u65F6\u5E38\u8BB0\u5FF5\u7236, \u4E14\u9075\u884C\u7236\u7684\u65E8\u610F(\u8DEF2:49; 22:42; 23:46).</li><li>\u7EA6\u745F\u8BF7\u7236\u4EB2\u524D\u6765, \u4E0D\u8981\u803D\u5EF6(\u521B45:9); \u4E3B\u8036\u7A23\u9080\u8BF7\u4EBA\u4FE1\u9760\u7942, \u4E0D\u8981\u803D\u5EF6(\u53C2 \u6797\u540E6:2: \u201C\u73B0\u5728\u6B63\u662F\u60A6\u7EB3\u7684\u65F6\u5019; \u73B0\u5728\u6B63\u662F\u62EF\u6551\u7684\u65E5\u5B50\u201D).</li><li>\u7EA6\u745F\u8981\u5949\u517B\u4ED6\u7684\u5BB6\u4EBA(\u521B45:11); \u4E3B\u8036\u7A23\u8BF4, \u51E1\u5230\u7942\u90A3\u91CC\u53BB\u7684, \u7942\u603B\u4E0D\u4E22\u5F03(\u7EA66:37).</li><li>\u4F17\u5144\u5F1F\u7684\u773C\u90FD\u770B\u89C1\u662F\u7EA6\u745F\u4EB2\u53E3\u5BF9\u4ED6\u4EEC\u8BF4\u8BDD(\u5373\u77E5\u9053\u6B64\u4EBA\u5B9E\u5728\u662F\u7EA6\u745F; \u521B45:12); \u4E3B\u8036\u7A23\u8BF4: \u201C\u4F60\u4EEC\u770B\u6211\u7684\u624B, \u6211\u7684\u811A, \u5C31\u77E5\u9053\u5B9E\u5728\u662F\u6211\u4E86\u201D(\u8DEF24:39).</li><li>\u7EA6\u745F\u5429\u5490\u5144\u5F1F\u4EEC, \u628A\u4ED6\u5728\u57C3\u53CA\u4E00\u5207\u7684\u8363\u8000\u90FD\u544A\u8BC9\u4ED6\u7236\u4EB2(\u521B45:13); \u4E3B\u8036\u7A23\u6709\u8363\u8000\u7684\u798F\u97F3(\u6797\u540E4:4), \u4E5F\u8981\u4EBA\u5BA3\u626C\u7942\u7684\u8363\u8000\u7F8E\u5FB7(\u5F7C\u524D2:9).</li><li>\u7EA6\u745F\u8981\u5144\u5F1F\u4EEC\u5168\u5BB6\u90FD\u642C\u5230\u4ED6\u8FD9\u91CC\u6765(\u521B45:18-19); \u4E3B\u8036\u7A23\u8BF4: \u201C\u51E1\u52B3\u82E6\u62C5\u91CD\u62C5\u7684\u4EBA, \u53EF\u4EE5\u5230\u6211\u8FD9\u91CC\u6765, \u6211\u5C31\u4F7F\u4F60\u4EEC\u5F97\u5B89\u606F\u201D(\u592A11:28).</li><li>\u7EA6\u745F\u63D0\u9192\u5144\u5F1F\u4EEC\u4E0D\u8981\u7231\u60DC\u4ED6\u4EEC\u7684\u5BB6\u5177, \u56E0\u4E3A\u57C3\u53CA\u5168\u5730\u7684\u7F8E\u7269\u90FD\u662F\u4ED6\u4EEC\u7684(\u521B45:20); \u4E3B\u8036\u7A23\u501F\u7740\u4FDD\u7F57\u63D0\u9192\u6211\u4EEC\u8981\u5FD8\u8BB0\u80CC\u5F8C, \u653E\u4E0B\u8FC7\u53BB\u7684\u4E00\u5207(\u81533:13-14).</li><li>\u96C5\u5404\u4E24\u6B21\u8BF4\u7EA6\u745F\u8FD8\u5728(\u8FD8\u6D3B\u7740; \u521B45:26,28); \u4FDD\u7F57\u5BA3\u79F0\u4E3B\u8036\u7A23\u662F\u6D3B\u7740\u7684(\u5F9225:19).</li><li>\u7EA6\u745F\u5411\u6CD5\u8001\u63D0\u53CA\u201C\u6211\u7684\u5F1F\u5144\u201D(\u521B46:31; 47:1); \u4E3B\u8036\u7A23\u79F0\u4FE1\u7942\u7684\u4EBA\u4E3A\u201C\u5F1F\u5144\u201D(\u67652:11).</li><li>\u57C3\u53CA\u4EBA\u6C42\u7EA6\u745F\u5E2E\u52A9, \u4EE5\u4FBF\u5B58\u6D3B, \u514D\u5F97\u6B7B\u5728\u4ED6\u9762\u524D(\u521B47:15,19); \u6C42\u52A9\u4E8E\u4E3B\u8036\u7A23\u7684\u4EBA\u4FBF\u5F97\u7740\u751F\u547D, \u56E0\u4E3B\u8BF4: \u201C\u590D\u6D3B\u5728\u6211, \u751F\u547D\u4E5F\u5728\u6211\u201D(\u7EA611:25).</li><li>\u6765\u5230\u7EA6\u745F\u9762\u524D\u7684\u4EBA\u628A\u94B1\u90FD\u82B1\u5C3D\u4E86(\u521B47:18); \u8BB8\u591A\u6765\u5230\u4E3B\u8036\u7A23\u9762\u524D\u7684\u4EBA\u4E5F\u662F\u5982\u6B64, \u5982\u6D6A\u5B50\u8017\u5C3D\u4E86\u4E00\u5207\u6240\u6709\u7684(\u8DEF15:14).</li><li>\u4ED6\u4EEC\u9664\u4E86\u8EAB\u4F53\u548C\u7530\u5730\u4E4B\u5916, \u4E00\u65E0\u6240\u5269(\u521B47:18); \u8BB8\u591A\u5230\u4E3B\u9762\u524D\u7684\u4EBA\u4E5F\u662F\u5982\u6B64, \u4E00\u65E0\u6240\u6709, \u5982\u4EBA\u65E0\u529B\u507F\u8FD8\u503A\u52A1(\u8DEF7:42)</li><li>\u7EA6\u745F\u4E70\u4E86\u4ED6\u4EEC\u548C\u4ED6\u4EEC\u7684\u5730(\u521B47:23); \u4E3B\u8036\u7A23\u7528\u91CD\u4EF7\u4E70\u4E86\u6211\u4EEC(\u6797\u524D6:20).</li><li>\u5F53\u7EA6\u745F\u7684\u5144\u5F1F\u4EEC\u89C1\u7236\u4EB2\u6B7B\u4E86, \u4FBF\u5DEE\u4EBA\u544A\u8BC9\u7EA6\u745F\u8BF4: \u201C\u6C42\u4F60\u9976\u6055\u201D, \u8FD9\u663E\u660E\u4ED6\u4EEC\u8FD8\u4E0D\u6DF1\u5165\u8BA4\u8BC6\u7EA6\u745F\u7684\u4E3A\u4EBA(\u521B50:17); \u4E3B\u8036\u7A23\u66FE\u5BF9\u7942\u7684\u95E8\u5F92\u8BF4: \u201C\u6211\u4E0E\u4F60\u4EEC\u540C\u5728\u8FD9\u6837\u957F\u4E45, \u4F60\u8FD8\u4E0D\u8BA4\u8BC6\u6211\u5417?\u201D(\u7EA614:9).</li><li>\u795E\u5B89\u6392\u7EA6\u745F\u88AB\u5356\u5230\u57C3\u53CA, \u7942\u7684\u610F\u601D\u539F\u662F\u597D\u7684, \u4E3A\u8981\u4FDD\u5168\u8BB8\u591A\u4EBA\u7684\u6027\u547D(\u521B50:20); \u795E\u5DEE\u9063\u4E3B\u8036\u7A23\u6765\u5230\u4E16\u754C, \u4E3A\u6211\u4EEC\u53D7\u82E6, \u8FD9\u539F\u662F\u597D\u7684, \u4E3A\u8981\u201C\u9886\u8BB8\u591A\u7684\u513F\u5B50\u8FDB\u8363\u8000\u91CC\u53BB\u201D(\u67652:10).</li><li>\u7EA6\u745F\u53EB\u4ED6\u4EEC\u4E0D\u8981\u5BB3\u6015, \u5E76\u7528\u4EB2\u7231\u7684\u8BDD\u5B89\u6170\u4ED6\u4EEC(\u521B50:21); \u4E3B\u8036\u7A23\u4E5F\u5B89\u6170\u95E8\u5F92, \u53EB\u4ED6\u4EEC\u5FC3\u91CC\u4E0D\u8981\u5FE7\u6101(\u7EA614:1).</li><li>\u7EA6\u745F\u662F\u591A\u7ED3\u679C\u5B50\u7684\u6811\u679D, \u4ED6\u7684\u679D\u6761\u63A2\u51FA\u5899\u5916(\u521B49:22); \u4E3B\u8036\u7A23\u662F\u8461\u8404\u6811, \u7942\u7684\u95E8\u5F92\u662F\u679D\u5B50(\u7EA615:5), \u95E8\u5F92\u628A\u798F\u97F3\u4F20\u5F00, \u8D85\u8D8A\u72B9\u592A\u6C11\u65CF\u7684\u5899(\u85E9\u7BF1), \u8FDB\u5230\u5916\u90A6\u4EBA\u5F53\u4E2D(\u5F9210:1,44-48).</li><li>\u7EA6\u745F\u662F\u6CC9\u65C1\u591A\u7ED3\u679C\u7684\u679D\u5B50(\u521B49:22); \u4E3B\u8036\u7A23\u6765\u5230\u96C5\u5404\u4E95\u65C1, \u6210\u4E3A\u201C\u591A\u7ED3\u679C\u5B50\u201D\u7684\u679D\u5B50(\u7942\u4F20\u798F\u97F3\u7ED3\u51FA\u6551\u6069\u7684\u679C\u5B50, \u56E0\u6492\u739B\u5229\u4E9A\u5987\u4EBA\u548C\u4E0D\u5C11\u6751\u6C11\u4FE1\u9760\u4E86\u7942, \u7EA64:6,14,39-42).</li><li>\u5F13\u7BAD\u624B\u5C06\u7EA6\u745F\u201C\u82E6\u5BB3, \u5411\u4ED6\u5C04\u7BAD, \u903C\u8FEB\u4ED6\u201D(\u521B49:23); \u4E3B\u8036\u7A23\u201C\u88AB\u85D0\u89C6, \u88AB\u4EBA\u538C\u5F03; \u591A\u53D7\u75DB\u82E6, \u5E38\u7ECF\u5FE7\u60A3\u201D(\u8D5B53:3).</li><li>\u4F46\u7EA6\u745F\u7684\u5F13\u4ECD\u65E7\u575A\u786C, \u4ED6\u7684\u624B\u5065\u58EE\u654F\u6377. \u8FD9\u662F\u56E0\u2026 \u96C5\u5404\u7684\u5927\u80FD\u8005(\u521B49:24); \u4E3B\u8036\u7A23\u56E0\u795E\u6240\u8D50\u4E0B\u7684\u5E2E\u52A9\u800C\u5F97\u4EE5\u575A\u56FA, \u4E0D\u81F3\u9000\u7F29, \u66F4\u4E0D\u653E\u4E0B\u4E3A\u4EBA\u4EE3\u6B7B\u7684\u6551\u8D4E\u5DE5\u4F5C(\u8DEF9:53; 22:42).[<a href="http://malaccagospelhall.org.my/2016/03/8399/#_ftn9" target="_blank" rel="noopener noreferrer">9]</a></li><li>\u8FD9\u662F\u56E0\u4EE5\u8272\u5217\u7684\u7267\u8005(\u521B49:24); \u4E3B\u8036\u7A23\u662F\u201C\u7FA4\u7F8A\u7684\u5927\u7267\u4EBA\u201D(\u5373\u4FE1\u5F92\u7684\u4F1F\u5927\u7267\u8005, \u676513:20)</li><li>\u8FD9\u662F\u56E0\u2026\u4EE5\u8272\u5217\u7684\u78D0\u77F3(\u521B49:25); \u4E3B\u8036\u7A23\u5C31\u662F\u575A\u7A33\u53EF\u9760\u7684\u201C\u78D0\u77F3\u201D(\u592A16:18; \u6797\u524D10:4).</li><li>\u8FD9\u4E9B\u798F\u5FC5\u964D\u5728\u7EA6\u745F\u7684\u5934\u4E0A(\u521B49:26); \u795E\u7528\u559C\u4E50\u6CB9\u818F\u4E3B\u8036\u7A23, \u80DC\u8FC7\u818F\u7942\u7684\u540C\u4F34(\u67651:9).</li><li>\u7EA6\u745F\u662F\u4E0E\u5F1F\u5144\u8FE5\u522B\u4E4B\u4EBA(\u6216\u8BD1\u201C\u4E0E\u4F17\u5F1F\u5144\u5206\u522B\u51FA\u6765\u7684\u4EBA\u201D, \u521B49:26); \u4E3B\u8036\u7A23\u4E5F\u4E0E\u4F17\u4E0D\u540C, \u201C\u8FDC\u79BB\u7F6A\u4EBA\u201D(\u6216\u8BD1\u201C\u4E0E\u7F6A\u4EBA\u5206\u522B\u51FA\u6765\u201D, \u67657:26).</li><li>\u957F\u5B50\u7684\u540D\u5206\u5F52\u7EA6\u745F(\u4EE3\u4E0A5:2); \u795E\u201C\u4F7F\u7942\u513F\u5B50(\u4E3B\u8036\u7A23)\u5728\u8BB8\u591A\u5F1F\u5144\u4E2D\u4F5C\u957F\u5B50\u201D(\u7F578:29).</li><li>\u4EBA\u4E0D\u4E3A\u7EA6\u745F\u7684\u82E6\u96BE\u62C5\u5FE7(\u64696:6); \u201C\u4F60\u4EEC\u4E00\u5207\u8FC7\u8DEF\u7684\u4EBA\u54EA, \u8FD9\u4E8B(\u53EF\u9884\u8868\u4E3B\u8036\u7A23\u53D7\u82E6)\u4F60\u4EEC\u4E0D\u4ECB\u610F\u5417? \u4F60\u4EEC\u8981\u89C2\u770B: \u6709\u50CF\u8FD9\u4E34\u5230\u6211\u7684\u75DB\u82E6\u6CA1\u6709, \u5C31\u662F\u8036\u548C\u534E\u5728\u7942\u53D1\u70C8\u6012\u7684\u65E5\u5B50\u4F7F\u6211\u6240\u53D7\u7684\u82E6\u201D(\u54C01:12).</li><li>\u6709\u4E0D\u8BA4\u8BC6\u7EA6\u745F\u7684\u65B0\u738B\u8D77\u6765, \u6CBB\u7406\u57C3\u53CA(\u51FA1:8); \u201C\u8FD9\u667A\u6167, \u4E16\u4E0A\u6709\u6743\u6709\u4F4D\u7684\u4EBA\u6CA1\u6709\u4E00\u4E2A\u77E5\u9053\u7684, \u4ED6\u4EEC\u82E5\u77E5\u9053, \u5C31\u4E0D\u628A\u8363\u8000\u7684\u4E3B\u9489\u5728\u5341\u5B57\u67B6\u4E0A\u4E86\u201D(\u6797\u524D2:8).</li><li>\u7EA6\u745F\u7684\u5B50\u5B59\u5BF9\u7EA6\u4E66\u4E9A\u8BF4: \u201C\u8036\u548C\u534E\u5230\u5982\u4ECA\u65E2\u7136\u8D50\u798F\u4E0E\u6211\u4EEC, \u6211\u4EEC\u4E5F\u65CF\u5927\u4EBA\u591A\u201D(\u4E6617:14); \u5C5E\u4E3B\u8036\u7A23\u7684\u4EBA\u56E0\u4E3B\u5F97\u798F, \u201C\u7942(\u7236\u795E)\u5728\u57FA\u7763\u91CC\u66FE\u8D50\u7ED9\u6211\u4EEC\u5929\u4E0A\u5404\u6837\u5C5E\u7075\u7684\u798F\u6C14\u201D(\u5F171:3).</li><li>\u7EA6\u4E66\u4E9A\u627F\u8BA4\u7EA6\u745F\u5BB6\u201C\u662F\u65CF\u5927\u4EBA\u591A, \u5E76\u4E14\u5F3A\u76DB\u201D(\u4E6617:17); \u5C5E\u4E3B\u8036\u7A23\u7684\u4EBA\u56E0\u4E3B\u5F97\u4EE5\u5F3A\u76DB, \u201C\u6211\u9760\u7740\u90A3\u52A0\u7ED9\u6211\u529B\u91CF\u7684(\u57FA\u7763), \u51E1\u4E8B\u90FD\u80FD\u505A\u201D(\u81534:13).</li><li>\u7EA6\u745F\u5BB6\u80DC\u4E86\u4E9A\u6469\u5229\u4EBA(\u58EB1:35); \u5C5E\u4E3B\u8036\u7A23\u7684\u4EBA\u9760\u4E3B\u5938\u80DC, \u201C\u9760\u7740\u7231\u6211\u4EEC\u7684\u4E3B, \u5728\u8FD9\u4E00\u5207\u7684\u4E8B\u4E0A\u5DF2\u7ECF\u5F97\u80DC\u6709\u4F59\u4E86\u201D(\u7F578:37).</li></ol>',5),o=[t];function n(p,_,s,c,f,h){return e(),i("div",null,o)}var y=l(a,[["render",n]]);export{m as __pageData,y as default};
