import{_ as e,c as a,o as t,a as r}from"./app.80c5deab.js";const f='{"title":"\u4FE1\u4E09\u4F4D\u4E00\u4F53\u7684\u795E","description":"","frontmatter":{},"headers":[{"level":2,"title":"\u4FE1\u4E09\u4F4D\u4E00\u4F53\u7684\u795E","slug":"\u4FE1\u4E09\u4F4D\u4E00\u4F53\u7684\u795E"},{"level":2,"title":"\u4E9A\u5F53","slug":"\u4E9A\u5F53"},{"level":2,"title":"\u4EBA\u7684\u7F6A","slug":"\u4EBA\u7684\u7F6A"},{"level":2,"title":"\u7F6A\u4EBA\u7684\u5904\u5883","slug":"\u7F6A\u4EBA\u7684\u5904\u5883"},{"level":2,"title":"\u795E\u7684\u62EF\u6551","slug":"\u795E\u7684\u62EF\u6551"},{"level":2,"title":"\u8036\u7A23\u7684\u798F\u97F3","slug":"\u8036\u7A23\u7684\u798F\u97F3"},{"level":2,"title":"\u5723\u7075\u7684\u5DE5\u4F5C","slug":"\u5723\u7075\u7684\u5DE5\u4F5C"},{"level":2,"title":"\u6211\u7684\u4FE1","slug":"\u6211\u7684\u4FE1"}],"relativePath":"gospel/\u798F\u97F3\u8981\u4E49-\u7CBE\u534E.md"}',i={},l=r('<h2 id="\u4FE1\u4E09\u4F4D\u4E00\u4F53\u7684\u795E" tabindex="-1">\u4FE1\u4E09\u4F4D\u4E00\u4F53\u7684\u795E <a class="header-anchor" href="#\u4FE1\u4E09\u4F4D\u4E00\u4F53\u7684\u795E" aria-hidden="true">#</a></h2><p>\u521B\u9020\u5E76\u4E14\u638C\u7BA1\u5B87\u5B99\u4E07\u6709\uFF0C\u5305\u62EC\u4E00\u5207\u7684\u79E9\u5E8F\uFF0C\u89C4\u5F8B\uFF0C\u5929\u4F7F\uFF0C\u4EBA\u7C7B\u3002\u5168\u77E5\u5168\u80FD\u3002\u5723\u6D01\uFF0C\u516C\u4E49\uFF0C\u6148\u7231\u3002\u4E09\u4F4D\u4E00\u4F53\u7684\u795E\u540C\u4EAB\u795E\u4E00\u5207\u7684\u5C5E\u6027\uFF0C\u5728\u6C38\u6052\u4E2D\u540C\u5C0A\u540C\u8363\uFF0C\u4EE5\u5B8C\u5168\u7684\u7231\u5F7C\u6B64\u76F8\u5C5E\uFF0C\u6240\u4EE5\u795E\u5C31\u662F\u7231\u7684\u672C\u4F53\u3002\u800C\u5723\u7075\u987A\u670D\u57FA\u7763\uFF0C\u57FA\u7763\u987A\u670D\u5929\u7236\uFF0C\u53C8\u6784\u6210\u4F4D\u683C\u4E2D\u7684\u5B8C\u5168\u987A\u670D\u7684\u5173\u7CFB\u3002\u5F53\u7136\u6211\u4EEC\u5BF9\u795E\u7684\u7406\u89E3\u90FD\u6765\u81EA\u5723\u7ECF\u7684\u542F\u793A\uFF0C\u795E\u53EA\u662F\u5411\u6211\u4EEC\u542F\u793A\u4E86\u6211\u4EEC\u6240\u80FD\u77E5\u7684\u90E8\u5206\u3002</p><h2 id="\u4E9A\u5F53" tabindex="-1">\u4E9A\u5F53 <a class="header-anchor" href="#\u4E9A\u5F53" aria-hidden="true">#</a></h2><p>\u4EBA\u8D77\u521D\u6709\u4ECE\u795E\u6765\u7F8E\u597D\u7684\u5F62\u8C61\u6837\u5F0F\uFF0C\u6709\u4ECE\u795E\u800C\u6765\u7684\u601D\u60F3\uFF0C\u7406\u6027\uFF0C\u60C5\u611F\uFF0C\u667A\u6167\uFF0C\u521B\u9020\u6027\uFF0C\u81EA\u7531\u610F\u5FD7\u3002 \u4E5F\u6709\u5723\u6D01\uFF0C\u516C\u4E49\uFF0C\u826F\u5584\uFF0C\u7231\u7684\u9053\u5FB7\u5C5E\u6027\u3002\u4E0E\u795E\u540C\u4F4F\u5728\u4F0A\u7538\u56ED\uFF0C\u88AB\u8D50\u6743\u67C4\u7BA1\u7406\u4E16\u754C\u3002\u4E3A\u8981\u663E\u660E\u795E\u7684\u5F62\u8C61\u6837\u5F0F\u7684\u8363\u7F8E\uFF0C\u4EE5\u6B64\u8363\u8000\u795E\u3002\u8D77\u521D\uFF0C\u65E0\u7F6A\u800C\u7F8E\u597D\u7684\u4EBA\u548C\u5723\u6D01\u7684\u795E\u548C\u7766\u76F8\u4EA4\u3002</p><h2 id="\u4EBA\u7684\u7F6A" tabindex="-1">\u4EBA\u7684\u7F6A <a class="header-anchor" href="#\u4EBA\u7684\u7F6A" aria-hidden="true">#</a></h2><p>\u56E0\u4E9A\u5F53\u72AF\u7F6A\uFF0C\u4E0E\u795E\u9694\u7EDD\uFF0C\u5728\u5C5E\u7075\u4E0A\u6B7B\u4EA1\uFF0C\u4ECE\u4E9A\u5F53\u800C\u51FA\u7684\u540E\u55E3\uFF0C\u4E0E\u795E\u7684\u5173\u7CFB\u65AD\u7EDD\uFF0C\u4ECE\u6B64\u7F6A\uFF0C\u6B7B\u4EA1\uFF0C\u9B54\u9B3C\u7684\u6743\u52BF\u7EDF\u6CBB\u4E86\u4EBA\u7C7B\u3002 \u4EBA\u548C\u795E\u5173\u7CFB\u7834\u88C2\uFF0C\u4EBA\u5728\u6069\u5178\u4E2D\u5815\u843D\uFF0C\u4EBA\u548C\u4EBA\uFF0C\u4EBA\u548C\u81EA\u6211\uFF0C\u4EBA\u548C\u81EA\u7136\uFF0C\u4EBA\u548C\u7075\u754C\u7684\u5173\u7CFB\u90FD\u8D25\u574F\u4E86\u3002 \u4ECE\u4E9A\u5F53\u800C\u51FA\u7684\u4EBA\u90FD\u5931\u53BB\u4E86\u548C\u795E\u6B63\u5F53\u7684\u5173\u7CFB\uFF0C\u88AB\u795E\u62D2\u7EDD\u3002 \u4E5F\u81EA\u7136\u7684\u4E0D\u8BA4\u8BC6\u795E\uFF0C\u62D2\u7EDD\u795E\uFF0C\u601D\u60F3\uFF0C\u5F62\u5F0F\u4E0E\u795E\u53CD\u5BF9\u3002\u6839\u672C\u65E0\u6CD5\u884C\u795E\u8981\u6C42\u7684\u4E49\uFF0C\u6B62\u4E0D\u4F4F\u72AF\u7F6A\u5F97\u7F6A\u795E\uFF0C\u4E8F\u7F3A\u4E86\u795E\u7684\u8363\u8000\u3002 \u4EBA\u5F00\u59CB\u6D3B\u5728\u6EE1\u8DB3\u6B32\u671B\uFF0C\u5EFA\u7ACB\u9A84\u50B2\u7684\u52A8\u529B\u4E2D\u3002 \u65E0\u89C6\u795E\u672C\u8EAB\uFF0C\u4E5F\u5305\u62EC\u795E\u7684\u8363\u8000\uFF0C\u6743\u67C4\uFF0C\u5C0A\u8D35\uFF0C\u5723\u6D01\uFF0C\u826F\u5584\u3002\u795E\u6240\u5F53\u914D\u5F97\u627F\u8BA4\uFF0C\u5C0A\u5D07\u7684\u8363\u8000\uFF0C\u88AB\u4EBA\u65E0\u89C6\u3002 \u4EBA\u5728\u8C0B\u53D6\u81EA\u5DF1\u7684\u8363\u8000\uFF0C\u6EE1\u8DB3\u3002 \u79EF\u6781\u7684\u81EA\u6211\u6210\u5168\u3002 \u4EBA\u65E0\u6CD5\u81EA\u53D1\u7684\u4ECE\u7F6A\u6076\u4E2D\u8131\u79BB\uFF0C\u6B62\u606F\u4E0D\u4F4F\u72AF\u7F6A\u7684\u51B2\u52A8\uFF0C\u65E0\u8BBA\u600E\u6837\u52A0\u5DE5\uFF0C\u7F8E\u5316\uFF0C\u8C03\u6574\u3002\u90FD\u4E0D\u8FC7\u662F\u5728\u884C\u72AF\u7F6A\u4E4B\u5B9E\u3002 \u4EBA\u7684\u4E49\u90FD\u662F\u6C61\u79FD\u7684\u8863\u670D\u3002</p><h2 id="\u7F6A\u4EBA\u7684\u5904\u5883" tabindex="-1">\u7F6A\u4EBA\u7684\u5904\u5883 <a class="header-anchor" href="#\u7F6A\u4EBA\u7684\u5904\u5883" aria-hidden="true">#</a></h2><p>\u5723\u6D01\u516C\u4E49\u7684\u795E\uFF0C\u9274\u67E5\u7F6A\u6076\uFF0C\u4EE5\u7F6A\u6076\u4E3A\u7F6A\u6076\uFF0C\u5FC5\u8981\u5BA1\u5224\u7F6A\u6076\uFF0C\u5BF9\u72AF\u7F6A\u7684\u4EBA\uFF0C\u6BEB\u4E0D\u59D1\u606F\u3002 \u795E\u7EDD\u4E0D\u4F1A\u5FD8\u8BB0\uFF0C\u7565\u8FC7\u7F6A\u4EBA\u7684\u7F6A\uFF0C\u6240\u4EE5\u5C06\u8981\u4E34\u5230\u4EBA\u7684\u662F\u795E\u6C38\u4E0D\u6B62\u606F\uFF0C\u548C\u7EAF\u4E00\u4E0D\u6742\u7684\u5FFF\u6012\u3002 \u4EBA\u53EA\u4E0D\u8FC7\u6D3B\u5728\u4E00\u4E2A\u795E\u6682\u65F6\u7684\u5BBD\u5BB9\uFF0C\u5FCD\u8010\u4E2D\u3002 \u79EF\u7D2F\u7F6A\u7684\u5DE5\u4EF7\uFF0C\u79EF\u7D2F\u795E\u7684\u6124\u6012\uFF0C\u76F4\u81F3\u672B\u540E\u7684\u5BA1\u5224\u3002</p><p>\u7F6A\u4EBA\u9003\u4E0D\u51FA\u5168\u80FD\u5168\u77E5\u795E\u7684\u76D1\u5BDF\uFF0C\u795E\u5FC5\u8981\u4EE5\u5723\u6D01\uFF0C\u4EE5\u516C\u4E49\u5BA1\u5224\u4EBA\uFF0C\u795E\u4EE5\u5C06\u6765\u6C38\u4E0D\u6B62\u606F\u7684\u6124\u6012\u5411\u7F6A\u4EBA\u503E\u5012\uFF0C\u4EE5\u5730\u72F1\u7684\u6C38\u706B\u5211\u7F5A\u4E0D\u80FD\u6094\u6539\u5F52\u4FE1\u7684\u7F6A\u4EBA\u3002</p><h2 id="\u795E\u7684\u62EF\u6551" tabindex="-1">\u795E\u7684\u62EF\u6551 <a class="header-anchor" href="#\u795E\u7684\u62EF\u6551" aria-hidden="true">#</a></h2><p>\u4F46\u795E\u6709\u601C\u60AF\u7684\u5FC3\u80A0\uFF0C\u7EDD\u4E0D\u613F\u610F\u4EBA\u843D\u5165\u5730\u72F1\u4E2D\u3002 \u5E76\u4E14\u795E\u672C\u610F\u8981\u521B\u9020\u7684\u4EBA\uFF0C\u5C31\u662F\u6309\u7167\u4ED6\u7684\u5F62\u8C61\u548C\u6837\u5F0F\u3002 \u5E76\u4E14\u4E07\u6709\u7684\u662F\u5728\u57FA\u7763\u91CC\u9020\u7684\uFF0C\u901A\u8FC7\u57FA\u7763\u9020\u7684\u3002 \u795E\u8981\u901A\u8FC7\u57FA\u7763\uFF0C\u5728\u57FA\u7763\u91CC\uFF0C\u8FDB\u884C\u65B0\u7684\u521B\u9020\u3002 \u771F\u6B63\u5B8C\u6210\u795E\u9020\u4EBA\u7684\u76EE\u7684\u3002 \u795E\u7684\u65E8\u610F\u5FC5\u4E0D\u843D\u7A7A\uFF0C\u795E\u7684\u6148\u7231\u548C\u826F\u5584\uFF0C\u5728\u9020\u4EBA\u4E4B\u521D\u5C31\u5411\u7740\u4EBA\u7C7B\u5C55\u5F00\u3002 \u795E\u4E50\u610F\u5C06\u4ED6\u7684\u4E30\u76DB\uFF0C\u8363\u7F8E\uFF0C\u5C0A\u8D35\u4E0E\u4EBA\u5206\u4EAB\u3002 \u613F\u610F\u4E0E\u4EBA\u4EA4\u5F80\uFF0C\u4F7F\u4EBA\u9886\u53D7\u4ED6\u7684\u4E30\u5BCC\uFF0C\u5927\u80FD\uFF0C\u667A\u6167\u3002 \u4EE5\u81F3\u4E8E\u4EBA\u628A\u795E\u8363\u8000\u7684\u672C\u8D28\uFF0C\u770B\u4E3A\u6700\u672C\u8D28\uFF0C\u6700\u9AD8\u7684\u8363\u8000\u3002 \u4F7F\u4EBA\u8363\u8000\u4ED6\u3002 \u4F46\u7F6A\u6076\u4E2D\u7684\u4EBA\uFF0C\u6839\u672C\u4E0D\u53EF\u80FD\u8FBE\u6210\u795E\u7684\u76EE\u7684\u3002 \u4ED6\u53E3\u53D1\u4E0D\u51FA\u8D5E\u7F8E\uFF0C\u611F\u8C22\uFF0C\u53CD\u800C\u662F\u4EB5\u6E0E\uFF0C\u8FB1\u9A82\u3002 \u4ED6\u7684\u5FC3\u53D1\u4E0D\u51FA\u5C0A\u5D07\uFF0C\u656C\u62DC\uFF0C\u53EA\u6709\u65E0\u89C6\uFF0C\u62D2\u7EDD\u3002</p><p>\u6240\u4EE5\u795E\u8FD8\u6709\u884C\u52A8\uFF0C\u5C31\u662F\u5728\u521B\u7ACB\u4E16\u754C\u4E4B\u5148\uFF0C\u5C31\u9884\u5907\u6551\u8D4E\u3002\u5728\u4EBA\u7C7B\u7684\u5386\u53F2\u4E2D\u884C\u52A8\uFF0C \u4E0E\u4E9A\u4F2F\u62C9\u7F55\u7ACB\u7EA6\uFF0C\u9884\u8A00\uFF0C\u9884\u8868\u7684\u5F62\u5F0F\u8868\u660E\u6551\u8D4E\u4E8B\u4EF6\u3002 \u5230\u4E86\u5B9A\u7684\u65E5\u5B50\uFF0C\u57FA\u7763\u7531\u5723\u7075\u611F\u5B55\u7AE5\u5973\u9A6C\u5229\u4E9A\uFF0C\u795E\u964D\u751F\u4E3A\u4EBA\u3002 \u7A7F\u4E0A\u4EBA\u7684\u8F6F\u5F31\uFF0C\u5C40\u9650\uFF0C\u8EAB\u4F53\u6027\u7684\u9700\u8981\uFF0C\u751F\u6D3B\u79CD\u79CD\u8BD5\u63A2\u3002 \u53EA\u662F\u8036\u7A23\u6CA1\u6709\u72AF\u7F6A\uFF0C\u5B8C\u5168\u4EE5\u5929\u7236\u7684\u65E8\u610F\u4E3A\u4F7F\u547D\u3002\u4F20\u5929\u56FD\u7684\u798F\u97F3\uFF0C\u5F81\u670D\u9ED1\u6697\uFF0C\u5149\u7167\u4EBA\u5FC3\uFF0C\u62A2\u593A\u7075\u9B42\uFF0C\u5EFA\u7ACB\u6559\u4F1A\uFF0C\u8BAD\u7EC3\u95E8\u5F92\uFF0C\u6258\u4ED8\u4F7F\u547D\u3002 \u800C\u8036\u7A23\u660E\u660E\u77E5\u9053\u5FC5\u7136\u7684\uFF0C\u6781\u81F4\u75DB\u82E6\u7684\u6B7B\u4EA1\uFF0C\u5374\u987A\u670D\u5230\u5E95\uFF0C\u6B23\u7136\u8D74\u6B7B\u3002\u65E0\u7F6A\u7684\u8036\u7A23\u5374\u6210\u4E3A\u66FF\u7F6A\u7684\u7F94\u7F8A\uFF0C\u4EE5\u4EBA\u7684\u751F\u547D\uFF0C\u5728\u8089\u8EAB\u4E2D\u5B9A\u4E86\u7F6A\u6848\u3002 \u7ECF\u5386\u4E86\u795E\u7684\u79BB\u5F03\uFF0C\u5BA1\u5224\uFF0C\u51FB\u6253\uFF0C\u6B7B\u4EA1\u3002 \u539F\u6765\u8036\u7A23\u6240\u53D7\u7684\u82E6\uFF0C\u627F\u8F7D\u7684\u5374\u662F\u7F6A\u4EBA\u7684\u7F6A\u3002\u795E\u5BF9\u7F6A\u4EBA\u7684\u6124\u6012\uFF0C\u52A0\u5728\u4E86\u8036\u7A23\u8EAB\u4E0A\u3002 \u540E\u53C8\u4ECE\u6B7B\u91CC\u590D\u6D3B\uFF0C\u771F\u6B63\u80DC\u8FC7\u4E86\u7F6A\uFF0C\u6B7B\u4EA1\uFF0C\u9B54\u9B3C\uFF0C \u4E5F\u6210\u4E3A\u95E8\u5F92\u53EF\u4FE1\u7684\u51ED\u636E\u3002 \u540C\u4E8B\u57FA\u7763\u4E5F\u4EE5\u6211\u4EEC\u770B\u4E0D\u89C1\u7684\u5F62\u5F0F\uFF0C\u4F5C\u4E3A\u5927\u796D\u53F8\uFF0C\u5C06\u81EA\u5DF1\u4E00\u6B21\u732E\u4E0A\u5929\u4E0A\u7684\u81F3\u5723\u6240\uFF0C\u771F\u6B63\u5728\u7236\u795E\u9762\u524D\u6210\u5C31\u4E86\u6551\u8D4E\u5DE5\u4F5C\uFF0C\u4ECE\u6B64\u6210\u4E3A\u6211\u4EEC\u7684\u4E2D\u4FDD\uFF0C\u4E3A\u6211\u4EEC\u5728\u7236\u795E\u53F3\u8FB9\u4EE3\u6C42\u3002\u53C8\u4E3A\u6211\u4EEC\u5DEE\u9063\u4FDD\u6075\u5E08\u5723\u7075\uFF0C\u786E\u4FDD\u6551\u6069\u7684\u679C\u6548\u3002 \u5982\u4ECA\u57FA\u7763\u505A\u738B\u5347\u5165\u5929\u5802\uFF0C\u4E3A\u6211\u4EEC\u9884\u5907\u5730\u65B9\u3002\u5C06\u6765\u5FC5\u5E26\u7740\u5723\u57CE\u65B0\u8036\u8DEF\u6492\u51B7\u964D\u4E34\uFF0C\u5C06\u5168\u5730\u66F4\u65B0\u3002 \u884C\u5584\u7684\u590D\u6D3B\u7684\u751F\uFF0C\u4F5C\u6076\u7684\u590D\u6D3B\u5B9A\u7F6A\u3002</p><h2 id="\u8036\u7A23\u7684\u798F\u97F3" tabindex="-1">\u8036\u7A23\u7684\u798F\u97F3 <a class="header-anchor" href="#\u8036\u7A23\u7684\u798F\u97F3" aria-hidden="true">#</a></h2><p>\u65E0\u7F6A\u7684\u8036\u7A23\u9489\u5728\u5341\u5B57\u67B6\u4E0A\uFF0C\u62C5\u5F53\u4E86\u4FE1\u4ED6\u4E4B\u4EBA\u7684\u7F6A\uFF0C\u627F\u53D7\u4E86\u795E\u7684\u6124\u6012\uFF0C\u505A\u6210\u4E86\u8D4E\u7F6A\u796D\u633D\u56DE\u796D\u3002\u6700\u540E\u6B7B\u91CC\u590D\u6D3B\u663E\u660E\u662F\u795E\u7684\u513F\u5B50\uFF0C\u65E0\u7F6A\u800C\u66FF\u7F6A\u7684\u7F94\u7F8A\u3002\u8036\u7A23\u4E5F\u8FDB\u5165\u5929\u4E0A\u7684\u81F3\u5723\u6240\uFF0C\u4E00\u6B21\u732E\u796D\uFF0C\u5C31\u5B8C\u6210\u6C38\u8FDC\u8D4E\u7F6A\u7684\u4E8B\uFF0C\u53C8\u4E3A\u6211\u4EEC\u505A\u6709\u6548\u7684\u4EE3\u6C42\u3002 \u4F7F\u6211\u4EEC\u4E5F\u80FD\u8FDB\u5165\u5E54\u5185\uFF0C\u4F7F\u6211\u4EEC\u80FD\u88AB\u5929\u7236\u63A5\u7EB3\uFF0C\u5728\u81F3\u5723\u6240\u4E0E\u795E\u4EB2\u5BC6\u76F8\u4EA4\u3002 \u57FA\u7763\u53C8\u5DEE\u9063\u5723\u7075\u505A\u6211\u4EEC\u7684\u4FDD\u60E0\u5E08\uFF0C\u5F15\u5BFC\u4EBA\u8D70\u6C38\u751F\u7684\u8DEF\u3002 \u57FA\u7763\u4E5F\u85C9\u7740\u6559\u4F1A\u7EDF\u6CBB\u7BA1\u7406\u4ED6\u7684\u767E\u59D3\uFF0C\u4E3A\u8981\u6210\u5168\u5F25\u8D5B\u4E9A\u56FD\u5EA6\u3002</p><h2 id="\u5723\u7075\u7684\u5DE5\u4F5C" tabindex="-1">\u5723\u7075\u7684\u5DE5\u4F5C <a class="header-anchor" href="#\u5723\u7075\u7684\u5DE5\u4F5C" aria-hidden="true">#</a></h2><p>\u5723\u7075\u5F00\u542F\uFF0C\u662F\u795E\u4E3B\u52A8\u5411\u4EBA\u884C\u52A8\uFF0C\u4F7F\u4EBA\u660E\u767D\u57FA\u7763\u7684\u5341\u5B57\u67B6\u66FF\u4EE3\u6027\u6551\u8D4E\u7684\u5927\u7231\uFF0C\u4E3A\u7F6A\u4E3A\u4E49\u4E3A\u5BA1\u5224\uFF0C\u81EA\u5DF1\u8D23\u5907\u81EA\u5DF1\u3002\u5728\u5BF9\u7F6A\u65E0\u53EF\u6307\u671B\u7684\u7EDD\u5883\u4E2D\u6295\u9760\u552F\u4E00\u7684\u6551\u8D4E\u8036\u7A23\u57FA\u7763\uFF0C\u5F97\u4EE5\u4F7F\u4EBA\u4EE5\u4FE1\u5FC3\u56DE\u5E94\u5723\u7075\u800C\u91CD\u751F\uFF0C\u4F4F\u5728\u4EBA\u5FC3\u91CC\uFF0C\u5F15\u5BFC\u4EBA\u660E\u767D\u5723\u7ECF\uFF0C\u771F\u7406\uFF0C\u4FDD\u5B88\u6240\u4FE1\u4E4B\u4EBA\u4E0D\u518D\u5931\u4E27\u3002\u4EBA\u4E5F\u662F\u5728\u5723\u7075\u7684\u5F00\u542F\u4E2D\uFF0C \u9886\u53D7\u795E\u7684\u5C0A\u8D35\uFF0C\u8363\u8000\uFF0C\u80FD\u529B\uFF0C\u7231\u3002 \u4EE5\u81F3\u4E8E\u4EBA\u80FD\u5411\u7740\u795E\u611F\u6069\uFF0C\u7231\u6155\uFF0C\u987A\u670D\uFF0C\u8DDF\u968F\uFF0C\u820D\u5DF1\uFF0C\u4F8D\u5949\u3002\u6211\u4EEC\u4E5F\u662F\u85C9\u7740\u5723\u7075\u4E0E\u795E\u76F8\u4EA4\uFF0C\u7977\u544A\uFF0C\u5EFA\u7ACB\u6D3B\u6CFC\u7684\u5173\u7CFB\uFF0C\u5728\u4EBA\u795E\u5173\u7CFB\u7684\u57FA\u7840\u4E0A\u53BB\u6784\u5EFA\u5408\u795E\u5FC3\u610F\u7684\u4EBA\u4E0E\u4EBA\uFF0C\u4EBA\u4E0E\u7269\uFF0C\u4EBA\u4E0E\u90AA\u7075\u7684\u5173\u7CFB\u3002\u7231\u795E\uFF0C\u7231\u4EBA\uFF0C\u62B5\u6321\u9B54\u9B3C\u3002 \u540C\u65F6\u5723\u7075\u4E5F\u7ED9\u6211\u4EEC\u8BF8\u822C\u7684\u6069\u8D50\uFF0C\u6069\u5178\uFF0C\u4F7F\u6211\u4EEC\u7528\u6765\u9020\u5C31\u751F\u547D\uFF0C\u6CBB\u7406\u795E\u7684\u5BB6\u3002</p><h2 id="\u6211\u7684\u4FE1" tabindex="-1">\u6211\u7684\u4FE1 <a class="header-anchor" href="#\u6211\u7684\u4FE1" aria-hidden="true">#</a></h2><p>\u660E\u767D\u81EA\u5DF1\u7684\u7F6A\u548C\u795E\u4E3A\u7F6A\u4EBA\u8131\u79BB\u6B7B\u548C\u7F6A\u7684\u798F\u97F3\uFF0C\u4E3A\u7F6A\u6094\u6539\uFF0C\u613F\u610F\u4FE1\u9760\u57FA\u7763\uFF0C\u6C42\u5F97\u8D66\u514D\u548C\u4FDD\u62A4\uFF0C\u5B9E\u9645\u7684\u7F6A\u88AB\u6E05\u7B97\uFF0C\u4E5F\u7ECF\u5386\u5723\u7075\u7684\u66F4\u65B0\uFF0C\u8D66\u7F6A\u4E4B\u6069\uFF0C\u6551\u6069\u4E4B\u4E50\u3002 \u4E0D\u4EC5\u77E5\u9053\u795E\u6211\u4EEC\u7684\u521B\u9020\u4E3B\uFF0C\u4E5F\u662F\u6211\u4EEC\u7684\u6551\u4E3B\uFF0C\u4E5F\u662F\u638C\u7BA1\u6211\u751F\u547D\u7684\u4E3B\u3002\u901A\u8FC7\u5723\u7075\uFF0C\u4E0E\u57FA\u7763\u8054\u5408\uFF0C\u53D1\u751F\u4E86\u6211\u751F\u547D\u7684\u6539\u53D8\uFF0C\u57FA\u7763\u7684\u6027\u60C5\u5728\u6211\u91CC\u9762\uFF0C\u7406\u6240\u5F53\u7136 \u8981\u52AA\u529B\u8131\u79BB\u7F6A\uFF0C\u987A\u670D\u57FA\u7763\uFF0C\u820D\u5DF1\u8DDF\u4ECE\u3002\u52AA\u529B\u8FC7\u4E0E\u798F\u97F3\u76F8\u79F0\u7684\u751F\u6D3B\uFF0C\u8B66\u9192\u7B49\u5019\u4E3B\u7684\u518D\u6765\u3002</p><p>\u5B66\u4E60\u5723\u7ECF\uFF1A\u5723\u7ECF\u662F\u795E\u7684\u9ED8\u793A\uFF0C\u795E\u5411\u4EBA\u5168\u5907\u7684\u542F\u793A\u4E5F\u552F\u72EC\u5723\u7ECF\uFF0C\u662F\u4FE1\u5F92\u548C\u6559\u4F1A\u6700\u9AD8\u7684\u6807\u51C6\uFF0C\u57FA\u7763\u5F92\u8981\u5728\u6B63\u610F\u7406\u89E3\u5723\u7ECF\u7684\u57FA\u7840\u4E0A\u6210\u957F\uFF0C\u5B66\u4E60\u5F97\u6551\u6210\u5723\u7684\u667A\u6167\u3002\u5F53\u7136\u552F\u72EC\u795E\u85C9\u7740\u5723\u7075\u7684\u5149\u7167\u80FD\u4F7F\u6211\u4EEC\u660E\u767D\u5723\u7ECF\u7684\u7CBE\u610F\u3002\u5723\u7075\u85C9\u7740\u5723\u7ECF\u5BF9\u6BCF\u4E00\u4E2A\u4EBA\u8BF4\u8BDD\uFF0C\u4E5F\u900F\u8FC7\u6BCF\u4E00\u4E2A\u4EBA\u8BF4\u8BDD\uFF0C\u5E2E\u52A9\u6BCF\u4E00\u4E2A\u4EBA\u7701\u5BDF\u3002\u6211\u4EEC\u5728\u8BFB\u7ECF\u7684\u8FC7\u7A0B\u4E2D\uFF0C\u88AB\u63ED\u53D1\uFF0C\u88AB\u6559\u8BAD\uFF0C\u88AB\u8BF4\u670D\uFF0C\u88AB\u7763\u8D23\uFF0C\u88AB\u7167\u4EAE\uFF0C\u88AB\u5F15\u5BFC\uFF0C\u88AB\u5F52\u6B63\uFF0C\u6FC0\u53D1\u7231\u5FC3\uFF0C\u52C9\u52B1\u884C\u5584\u3002</p><ol><li>\u6211\u4EEC\u5C0A\u91CD\u5148\u8F88\u4EEC\u5BF9\u5723\u7ECF\u7684\u9886\u53D7\uFF0C\u795E\u7ED9\u5386\u4EE3\u5723\u5F92\u7684\u542F\u793A\uFF0C\u53C2\u8003\u91CA\u7ECF\u6750\u6599\u548C\u795E\u5B66\u77E5\u8BC6\u3002</li><li>\u6211\u4EEC\u6E34\u671B\u5723\u7075\u6D3B\u6CFC\u7684\u5DE5\u4F5C\uFF0C\u4E0D\u61C8\u8FFD\u5BFB\uFF0C\u5C3D\u5FC3\u9ED8\u60F3\uFF0C\u5F7C\u6B64\u4EA4\u901A\u3002\u4F7F\u6211\u4EEC\u85C9\u7740\u5723\u7ECF\u795E\u7684\u8BDD\u8BED\u4F4F\u5728\u57FA\u7763\u91CC\u3002</li><li>\u6211\u4EEC\u575A\u6301\u4EE5\u7ECF\u89E3\u7ECF\uFF0C\u4EE5\u6574\u672C\u5723\u7ECF\u89E3\u91CA\u6574\u672C\u5723\u7ECF\u3002\u56E0\u4E3A\u5723\u7ECF\u65E0\u4E00\u7F3A\u5C11\uFF0C\u65E0\u4E00\u6CA1\u6709\u4F34\u5076\u3002</li></ol><p>\u610F\u5473\u7740\u7528\u6574\u672C\u5723\u7ECF\u89E3\u91CA\u5C40\u90E8\u7ECF\u6587 = \u5177\u4F53\u7684\u7ECF\u6587\u5B57\u53E5\u9700\u8981\u6574\u672C\u5723\u7ECF\u5168\u5907\u7684\u771F\u7406\u53BB\u9610\u91CA\u3002 \u56E0\u4E3A\u795E\u7684\u7075\u5C06\u4ED6\u4EEC\u805A\u96C6\u3002</p>',21),h=[l];function d(s,n,p,c,_,o){return t(),a("div",null,h)}var v=e(i,[["render",d]]);export{f as __pageData,v as default};
