import{_ as e,c as n,o as a,a as p}from"./app.a6e9f6c9.js";const u='{"title":"\u521B\u4E16\u7EAA 38\u7AE0  \u72B9\u5927\u4E71\u4F26","description":"","frontmatter":{},"headers":[{"level":2,"title":"\u4E00\u3001\u805A\u7126\u72B9\u592A","slug":"\u4E00\u3001\u805A\u7126\u72B9\u592A"},{"level":2,"title":"\u4E8C\u3001\u72B9\u5927\u4E4B\u5B50","slug":"\u4E8C\u3001\u72B9\u5927\u4E4B\u5B50"},{"level":2,"title":"\u4E09\u3001\u72B9\u5927\u4E71\u4F26","slug":"\u4E09\u3001\u72B9\u5927\u4E71\u4F26"},{"level":2,"title":"\u56DB\u3001\u53CC\u751F\u5B50","slug":"\u56DB\u3001\u53CC\u751F\u5B50"}],"relativePath":"index.md"}',r={},t=p(`<h1 id="\u521B\u4E16\u7EAA-38\u7AE0-\u72B9\u5927\u4E71\u4F26" tabindex="-1">\u521B\u4E16\u7EAA 38\u7AE0 \u72B9\u5927\u4E71\u4F26 <a class="header-anchor" href="#\u521B\u4E16\u7EAA-38\u7AE0-\u72B9\u5927\u4E71\u4F26" aria-hidden="true">#</a></h1><h2 id="\u4E00\u3001\u805A\u7126\u72B9\u592A" tabindex="-1">\u4E00\u3001\u805A\u7126\u72B9\u592A <a class="header-anchor" href="#\u4E00\u3001\u805A\u7126\u72B9\u592A" aria-hidden="true">#</a></h2><p>\u524D\u4E00\u7AE0\u63D0\u5230 \uFF0C<code>\u3010\u521B37:2\u3011\u96C5\u5404\u7684\u8BB0\u7565\u5982\u4E0B</code>\uFF1A\u96C5\u5404\u7684\u513F\u5B50\u4EEC\u7684\u4E8B\u60C5\u90FD\u5F52\u7B97\u4E3A\u96C5\u5404\u7684\u8BB0\u7565\u3002 \u5723\u7ECF\u8BDA\u5B9E\u7684\u8BB0\u5F55\u96C5\u5404\u5BB6\u5404\u6837\u7684\u7F6A\u6076\uFF0C\u4EE5\u53CA\u795E\u5728\u5176\u4E2D\u7684\u638C\u6743\u3002 \u4E49\u4EBA\u7684\u751F\u673A\u5728\u795E\u624B\u91CC\uFF0C\u7F6A\u4EBA\u7684\u6B7B\u4EA1\u4E5F\u5728\u795E\u4E00\u5FF5\u4E4B\u95F4\u3002 \u72B9\u5927\u51FA\u5356\u7EA6\u745F\u4E4B\u540E\uFF0C\u7EE7\u7EED\u8D25\u574F\uFF0C\u795E\u7684\u60E9\u7F5A\u91CD\u91CD\u7684\u4E34\u5230\u8FD9\u4E00\u5BB6\u3002 \u5723\u7ECF\u901A\u8FC7\u72B9\u5927\uFF0C\u8BDA\u5B9E\u7684\u8BB0\u8F7D\u7740\u9009\u6C11\u5BB6\u65CF\u7684\u7F6A\uFF0C\u518D\u901A\u8FC7\u7EA6\u745F\u53BB\u53CD\u5E94\u5916\u90A6\u4EBA\u7684\u7F6A\u3002\u6DEB\u4E71\uFF0C\u8150\u8D25\uFF0C\u6740\u5BB3\uFF0C\u51FA\u5356\uFF0C\u7EA6\u745F\u4E5F\u7ECF\u5386\u7740\u5404\u79CD\u5404\u6837\u7684\u7F6A\u4EBA\uFF0C\u4F46\u5728\u795E\u7684\u4FDD\u5B88\u770B\u987E\u4E2D\uFF0C\u751F\u53D1\u7684\u4E0D\u662F\u6B8B\u5FCD\u7684\u5FC3\uFF0C\u800C\u662F\u9976\u6055\u3002</p><p>\u5723\u7ECF\u5C06\u955C\u5934\u805A\u7126\u5728\u72B9\u5927\u8EAB\u4E0A\u3002 \u4E00\u65B9\u9762\u6D41\u4FBF\u7684\u6DEB\u4E71\u5BFC\u81F4\u4ED6\u5931\u53BB\u4E86\u957F\u5B50\u7684\u6743\u67C4\uFF0C\u751A\u81F3\u662F\u5F1F\u5144\u4EEC\u7684\u5C0A\u91CD\u3002\u4ED6\u88AB\u5B64\u7ACB\u548C\u6B3A\u9A97\u3002 \u4E00\u65B9\u9762\u72B9\u5927\u662F\u51FA\u5356\u7EA6\u745F\u7684\u4E3B\u8C0B\uFF0C\u4ED6\u4F5C\u6076\u83B7\u5F97\u4E86\u4E00\u5B9A\u7684\u6743\u67C4\u3002\u7F6A\u4EBA\u4F1A\u5728\u540C\u5FC3\u5408\u610F\u7684\u4F5C\u6076\u7684\u4E8B\u60C5\u4E0A\uFF0C\u83B7\u5F97\u6743\u5229\u548C\u5C0A\u91CD\u3002 \u795E\u8981\u5BF9\u72B9\u5927\u5C55\u5F00\u7BA1\u6559\uFF0C\u5265\u593A\uFF0C\u5BA1\u5224\uFF0C\u91CD\u5EFA\u72B9\u5927\uFF0C\u91CD\u5EFA\u4EE5\u8272\u5217\u5BB6\u3002</p><p>\u5A36\u8FE6\u5357\u5973\u5B50\uFF0C\u627E\u5993\u5973\u7684\u72B9\u5927\u548C\u4E0B\u9762\u7684\u7EA6\u745F\u5F62\u6210\u663E\u660E\u7684\u5BF9\u6BD4\u3002 \u6211\u4EEC\u4E5F\u770B\u5230\u72B9\u5927\u5982\u4F55\u626E\u6F14\u4E00\u4E2A\u7F6A\u4EBA\uFF0C\u53C8\u662F\u5982\u4F55\u5931\u53BB\u957F\u5B50\u540D\u5206\u7684\u3002 \u4ED6\u867D\u7136\u62E5\u6709\u957F\u5B50\u7684\u6743\u67C4\u3002\u4F46\u8FD9\u6743\u67C4\u6307\u5411\u7684\u662F\u57FA\u7763\u3002</p><p>\u6709\u4E9B\u4EBA\u50CF\u7EA6\u745F\uFF0C\u4E9A\u4F2F\u62C9\u7F55\uFF0C\u575A\u5F3A\u7684\u6D3B\u5728\u795E\u7684\u5FC3\u610F\u91CC\uFF0C\u4E0E\u795E\u540C\u884C\u3002 \u4ED6\u4EEC\u85C9\u7740\u4E00\u7CFB\u5217\u7684\u82E6\u96BE\uFF0C\u5851\u9020\u6210\u5408\u795E\u5FC3\u610F\u7684\u5668\u76BF\u3002</p><p>\u6709\u4E9B\u4EBA\u50CF\u72B9\u5927\uFF0C\u7F57\u5F97\uFF0C\u575A\u5F3A\u7684\u6D3B\u5728\u81EA\u5DF1\u7684\u8089\u4F53\uFF0C\u79C1\u6B32\u4E2D\u3002\u4EFB\u610F\u5984\u4E3A\uFF0C\u4E0D\u77E5\u6094\u6539\uFF0C\u4E0D\u77E5\u8B66\u9192\u3002 \u795E\u4F7F\u7528\u4E00\u7CFB\u5217\u7684\u82E6\u96BE\uFF0C\u51FB\u6253\uFF0C\u53EA\u662F\u4E3A\u4E86\u7BA1\u6559\u4ED6\u4EEC\uFF0C\u4F7F\u4ED6\u4EEC\u80FD\u56DE\u8F6C\u5F52\u5411\u795E\u3002 \u795E\u5BF9\u4ED6\u4EEC\u7684\u4FDD\u5B88\uFF0C\u4E0D\u8FC7\u662F\u4FDD\u5B88\u4ED6\u4EEC\u7684\u6551\u6069\u4E0D\u81F3\u4E8E\u5931\u843D\u3002<code>\u3010\u72B91:23\u3011\u6709\u4E9B\u4EBA\u4F60\u4EEC\u8981\u4ECE\u706B\u4E2D\u62A2\u51FA\u6765\uFF0C\u642D\u6551\u4ED6\u4EEC\uFF1B\u6709\u4E9B\u4EBA\u4F60\u4EEC\u8981\u5B58\u60E7\u6015\u7684\u5FC3\u601C\u60AF\u4ED6\u4EEC\uFF0C\u8FDE\u90A3\u88AB\u60C5\u6B32\u6CBE\u67D3\u7684\u8863\u670D\u4E5F\u5F53\u538C\u6076\u3002</code></p><p>\u795E\u7684\u9009\u6C11\uFF0C\u7EDD\u5BF9\u4E0D\u610F\u5473\u7740\u4ED6\u4EEC\u6BD4\u5916\u90A6\u4EBA\u66F4\u4E49\u3002\u72B9\u592A\u4EBA\uFF0C\u5916\u90A6\u4EBA\u90FD\u5728\u5F8B\u6CD5\u4E4B\u4E0B\u3002 \u4ED6\u4EEC\u90FD\u9700\u8981\u88AB\u5F8B\u6CD5\u7BA1\u7406\u3002 \u53EA\u662F\u4ED6\u4EEC\u8499\u4E86\u601C\u60AF\uFF0C\u5982\u679C\u6CA1\u6709\u795E\uFF0C\u4ED6\u4EEC\u53EF\u4EE5\u66F4\u52A0\u8D25\u574F\u3002</p><h2 id="\u4E8C\u3001\u72B9\u5927\u4E4B\u5B50" tabindex="-1">\u4E8C\u3001\u72B9\u5927\u4E4B\u5B50 <a class="header-anchor" href="#\u4E8C\u3001\u72B9\u5927\u4E4B\u5B50" aria-hidden="true">#</a></h2><div class="language-"><pre><code>\u3010\u521B38:1\u3011\u90A3\u65F6\uFF0C\u72B9\u5927\u79BB\u5F00\u4ED6\u5F1F\u5144\u4E0B\u53BB\uFF0C\u5230\u4E00\u4E2A\u4E9A\u675C\u5170\u4EBA\u540D\u53EB\u5E0C\u62C9\u7684\u5BB6\u91CC\u53BB\u3002
\u3010\u521B38:2\u3011\u72B9\u5927\u5728\u90A3\u91CC\u770B\u89C1\u4E00\u4E2A\u8FE6\u5357\u4EBA\u540D\u53EB\u4E66\u4E9A\u7684\u5973\u513F\uFF0C\u5C31\u5A36\u5979\u4E3A\u59BB\uFF0C\u4E0E\u5979\u540C\u623F\uFF0C
\u3010\u521B38:3\u3011\u5979\u5C31\u6000\u5B55\u751F\u4E86\u513F\u5B50\uFF0C\u72B9\u5927\u7ED9\u4ED6\u8D77\u540D\u53EB\u73E5\u3002
\u3010\u521B38:4\u3011\u5979\u53C8\u6000\u5B55\u751F\u4E86\u513F\u5B50\uFF0C\u6BCD\u4EB2\u7ED9\u4ED6\u8D77\u540D\u53EB\u4FC4\u5357\u3002
\u3010\u521B38:5\u3011\u5979\u590D\u53C8\u751F\u4E86\u513F\u5B50\uFF0C\u7ED9\u4ED6\u8D77\u540D\u53EB\u793A\u62C9\u3002\u5979\u751F\u793A\u62C9\u7684\u65F6\u5019\uFF0C\u72B9\u5927\u6B63\u5728\u57FA\u6089\u3002
\u3010\u521B38:6\u3011\u72B9\u5927\u4E3A\u957F\u5B50\u73E5\u5A36\u59BB\uFF0C\u540D\u53EB\u4ED6\u739B\u3002
\u3010\u521B38:7\u3011\u72B9\u5927\u7684\u957F\u5B50\u73E5\u5728\u8036\u548C\u534E\u773C\u4E2D\u770B\u4E3A\u6076\uFF0C\u8036\u548C\u534E\u5C31\u53EB\u4ED6\u6B7B\u4E86\u3002
\u3010\u521B38:8\u3011\u72B9\u5927\u5BF9\u4FC4\u5357\u8BF4\uFF1A\u201C\u4F60\u5F53\u4E0E\u4F60\u54E5\u54E5\u7684\u59BB\u5B50\u540C\u623F\uFF0C\u5411\u5979\u5C3D\u4F60\u4E3A\u5F1F\u7684\u672C\u5206\uFF0C\u4E3A\u4F60\u54E5\u54E5\u751F\u5B50\u7ACB\u540E\u3002\u201D
\u3010\u521B38:9\u3011\u4FC4\u5357\u77E5\u9053\u751F\u5B50\u4E0D\u5F52\u81EA\u5DF1\uFF0C\u6240\u4EE5\u540C\u623F\u7684\u65F6\u5019\uFF0C\u4FBF\u9057\u5728\u5730\uFF0C\u514D\u5F97\u7ED9\u4ED6\u54E5\u54E5\u7559\u540E\u3002
\u3010\u521B38:10\u3011\u4FC4\u5357\u6240\u4F5C\u7684\u5728\u8036\u548C\u534E\u773C\u4E2D\u770B\u4E3A\u6076\uFF0C\u8036\u548C\u534E\u4E5F\u5C31\u53EB\u4ED6\u6B7B\u4E86\u3002
\u3010\u521B38:11\u3011\u72B9\u5927\u5FC3\u91CC\u8BF4\uFF1A\u201C\u6050\u6015\u793A\u62C9\u4E5F\u6B7B\uFF0C\u50CF\u4ED6\u4E24\u4E2A\u54E5\u54E5\u4E00\u6837\u201D\uFF0C\u5C31\u5BF9\u4ED6\u513F\u5987\u4ED6\u739B\u8BF4\uFF1A\u201C\u4F60\u53BB\uFF0C\u5728\u4F60\u7236\u4EB2\u5BB6\u91CC\u5B88\u5BE1\uFF0C\u7B49\u6211\u513F\u5B50\u793A\u62C9\u957F\u5927\u3002\u201D\u4ED6\u739B\u5C31\u56DE\u53BB\u4F4F\u5728\u5979\u7236\u4EB2\u5BB6\u91CC\u3002
</code></pre></div><p><strong>\u72B9\u5927\u79BB\u5F00\u4ED6\u5F1F\u5144</strong> \u5356\u4E86\u7EA6\u745F\uFF0C\u52A0\u4E0A\u7236\u4EB2\u7684\u773C\u6CEA\u3002\u53EF\u80FD\u662F\u72B9\u5927\u5BF9\u81EA\u5DF1\u751F\u6D3B\u5728\u8FD9\u4E2A\u5144\u5F1F\u548C\u7236\u4EB2\u4E2D\u95F4\u90FD\u6839\u6839\u4E8E\u6000\uFF0C\u4E5F\u6709\u53EF\u80FD\u72B9\u5927\u7684\u5144\u5F1F\u4EEC\u5BF9\u4ED6\u6709\u6240\u975E\u8BAE\uFF0C\u8FC1\u6012\u3002 \u4E8E\u662F\u4ED6\u79BB\u5F00\u4F17\u5F1F\u5144\uFF0C\u5B81\u613F\u4E0E\u5916\u90A6\u4EBA\u540C\u5C45\u3002</p><p>\u8FD9\u79CD\u60C5\u51B5\u7C7B\u4F3C\u4E00\u4E9B\u201C\u57FA\u7763\u5F92\u201D\u56E0\u4E3A\u5728\u6559\u4F1A\u53D7\u5230\u4F24\u5BB3\u2014\u2014\u5B9E\u9645\u4E0A\u4ED6\u4EEC\u81EA\u5DF1\u5C31\u662F\u65BD\u5BB3\u4EBA\u4E4B\u4E00\u2014\u2014\u4E4B\u540E\uFF0C\u5C31\u79BB\u5F00\u6559\u4F1A\uFF0C\u8FD4\u56DE\u4E16\u754C\u3002</p><p><strong>\u4E9A\u675C\u5170\u4EBA \u540D\u53EB\u5E0C\u62C9</strong> \u72B9\u5927\u79BB\u5F00\u4E86\u4ED6\u7684\u5144\u5F1F\uFF0C\u9047\u5230\u4E86\u4E00\u4E2A\u8D35\u4EBA\u3002\u901A\u8FC7\u4ED6\u627E\u5230\u4E86\u59BB\u5B50\uFF0C\u59BB\u5B50\u6B7B\u540E\uFF0C\u4E00\u8D77\u53BB\u62FF\u4EAD\uFF0C\u5AD6\u4E86\u513F\u5AB3\uFF0C\u8FD9\u4E2A\u4EBA\u6210\u4E86\u72B9\u5927\u4FE1\u4EFB\u7684\u670B\u53CB\uFF0C\u5E2E\u52A9\u72B9\u5927\u53BB\u627E\u5993\u5973\u53D6\u56DE\u5F53\u5934\u3002</p><p><strong>\u8FE6\u5357\u4EBA \u540D\u53EB\u4E66\u4E9A</strong> \u72B9\u5927\u5A36\u4E86\u8FE6\u5357\u5973\u5B50\uFF0C\u8FDD\u80CC\u4E9A\u4F2F\u62C9\u7F55\u6240\u8D77\u7684\u8A93\uFF0C\u8FDD\u80CC\u795E\u7684\u65E8\u610F \u4E24\u4E2A\u513F\u5B50\u7684\u6B7B\u57CB\u4E0B\u4F0F\u7B14\uFF0C\u72B9\u5927\u4E24\u4E2A\u513F\u5B50\u88AB\u795E\u770B\u4E3A\u6076\uFF0C\u5C31\u6B7B\u4E86\u3002\u4E16\u4E0A\u8BE5\u6B7B\u7684\u6076\u4EBA\u5F88\u591A\u5F88\u591A\uFF0C\u4E3A\u4EC0\u4E48\u662F\u4ED6\u4EEC\u4E24\u4E2A\uFF1F \u56E0\u4E3A\u4ED6\u4EEC\u662F\u8FE6\u5357\u5973\u5B50\u7684\u540E\u88D4\uFF0C\u800C\u4E14\u4ED6\u4EEC\u8868\u73B0\u51FA\u6765\u7684\u6076\u786E\u5B9E\u8BE5\u6B7B\uFF0C\u6240\u4EE5\u4ED6\u4EEC\u7684\u65CF\u7CFB\u65AD\u4EE3\u4E86\u3002</p><p>\u8FD9\u4E2A\u72B9\u5927\u7684\u59BB\u5B50\u81F3\u6B7B\uFF0C\u6211\u4EEC\u4E5F\u4E0D\u77E5\u9053\u4ED6\u7684\u540D\u5B57\u3002\u5723\u7ECF\u5E76\u4E0D\u7EAA\u5FF5\u5979\u3002</p><p>\u4FC4\u5357\u4E0D\u4E3A\u54E5\u54E5\u7ACB\u540E\uFF0C\u8036\u548C\u534E\u770B\u4ED6\u4F5C\u7684\u662F\u6076\u4E8B\u3002 \u56E0\u4E3A\u7ED9\u54E5\u54E5\u751F\u7684\u513F\u5B50\uFF0C\u62E5\u6709\u957F\u5B50\u7684\u540D\u5206\u3002 \u4E00\u65B9\u9762\u4ED6\u5360\u6709\u4ED6\u739B\uFF0C\u4E00\u65B9\u9762\u4ED6\u6B3A\u9A97\u7236\u4EB2\u3002</p><p>\u72B9\u5927\u6050\u60E7\u4E09\u513F\u5B50\u793A\u62C9\u7684\u6B7B\uFF0C\u6B3A\u9A97\u513F\u5987\u3002 \u4ED6\u4E5F\u6CA1\u6709\u67E5\u9A8C\u513F\u5B50\u7684\u6B7B\u662F\u51FA\u4E8E\u4EC0\u4E48\u539F\u56E0\u3002 \u5E76\u4E0D\u6C42\u544A\u8036\u548C\u534E\u3002 \u795E\u4E25\u5389\u7BA1\u6559\u4ED6\uFF0C\u4ED6\u4ECD\u7136\u4E0D\u56DE\u8F6C\u3002 \u5FC3\u8499\u8102\u6CB9\uFF0C\u7075\u6027\u4E0A\u963B\u9694\u3002 \u6240\u4EE5\u5B9E\u9645\u4E0A\u4ED6\u628A\u513F\u5B50\u7684\u6B7B\u5F52\u56E0\u5230\u4E86\u513F\u5987\u8EAB\u4E0A\u3002\u8BA9\u5979\u627F\u62C5\u4E00\u5207\u540E\u679C\u3002</p><h2 id="\u4E09\u3001\u72B9\u5927\u4E71\u4F26" tabindex="-1">\u4E09\u3001\u72B9\u5927\u4E71\u4F26 <a class="header-anchor" href="#\u4E09\u3001\u72B9\u5927\u4E71\u4F26" aria-hidden="true">#</a></h2><div class="language-"><pre><code>\u3010\u521B38:12\u3011\u8FC7\u4E86\u8BB8\u4E45\uFF0C\u72B9\u5927\u7684\u59BB\u5B50\u4E66\u4E9A\u7684\u5973\u513F\u6B7B\u4E86\uFF0C\u72B9\u5927\u5F97\u4E86\u5B89\u6170\uFF0C\u5C31\u548C\u4ED6\u670B\u53CB\u4E9A\u675C\u5170\u4EBA\u5E0C\u62C9\u4E0A\u4EAD\u62FF\u53BB\uFF0C\u5230\u4ED6\u526A\u7F8A\u6BDB\u7684\u4EBA\u90A3\u91CC\u3002
\u3010\u521B38:13\u3011\u6709\u4EBA\u544A\u8BC9\u4ED6\u739B\u8BF4\uFF1A\u201C\u4F60\u7684\u516C\u516C\u4E0A\u4EAD\u62FF\u526A\u7F8A\u6BDB\u53BB\u4E86\u3002\u201D
\u3010\u521B38:14\u3011\u4ED6\u739B\u89C1\u793A\u62C9\u5DF2\u7ECF\u957F\u5927\uFF0C\u8FD8\u6CA1\u6709\u5A36\u5979\u4E3A\u59BB\uFF0C\u5C31\u8131\u4E86\u5979\u4F5C\u5BE1\u5987\u7684\u8863\u88F3\uFF0C\u7528\u5E15\u5B50\u8499\u7740\u8138\uFF0C\u53C8\u906E\u4F4F\u8EAB\u4F53\uFF0C\u5750\u5728\u4EAD\u62FF\u8DEF\u4E0A\u7684\u4F0A\u62FF\u5370\u57CE\u95E8\u53E3\u3002
\u3010\u521B38:15\u3011\u72B9\u5927\u770B\u89C1\u5979\uFF0C\u4EE5\u4E3A\u662F\u5993\u5973\uFF0C\u56E0\u4E3A\u5979\u8499\u7740\u8138\u3002
\u3010\u521B38:16\u3011\u72B9\u5927\u5C31\u8F6C\u5230\u5979\u90A3\u91CC\u53BB\uFF0C\u8BF4\uFF1A\u201C\u6765\u5427\uFF01\u8BA9\u6211\u4E0E\u4F60\u540C\u5BDD\u3002\u201D\u4ED6\u539F\u4E0D\u77E5\u9053\u662F\u4ED6\u7684\u513F\u5987\u3002\u4ED6\u739B\u8BF4\uFF1A\u201C\u4F60\u8981\u4E0E\u6211\u540C\u5BDD\uFF0C\u628A\u4EC0\u4E48\u7ED9\u6211\u5462\uFF1F\u201D
\u3010\u521B38:17\u3011\u72B9\u5927\u8BF4\uFF1A\u201C\u6211\u4ECE\u7F8A\u7FA4\u91CC\u53D6\u4E00\u53EA\u5C71\u7F8A\u7F94\uFF0C\u6253\u53D1\u4EBA\u9001\u6765\u7ED9\u4F60\u3002\u201D\u4ED6\u739B\u8BF4\uFF1A\u201C\u5728\u672A\u9001\u4EE5\u5148\uFF0C\u4F60\u613F\u610F\u7ED9\u6211\u4E00\u4E2A\u5F53\u5934\u5417\uFF1F\u201D
\u3010\u521B38:18\u3011\u4ED6\u8BF4\uFF1A\u201C\u6211\u7ED9\u4F60\u4EC0\u4E48\u5F53\u5934\u5462\uFF1F\u201D\u4ED6\u739B\u8BF4\uFF1A\u201C\u4F60\u7684\u5370\uFF0C\u4F60\u7684\u5E26\u5B50\u548C\u4F60\u624B\u91CC\u7684\u6756\u3002\u201D\u72B9\u5927\u5C31\u7ED9\u4E86\u5979\uFF0C\u4E0E\u5979\u540C\u5BDD\uFF0C\u5979\u5C31\u4ECE\u72B9\u5927\u6000\u4E86\u5B55\u3002
\u3010\u521B38:19\u3011\u4ED6\u739B\u8D77\u6765\u8D70\u4E86\uFF0C\u9664\u53BB\u5E15\u5B50\uFF0C\u4ECD\u65E7\u7A7F\u4E0A\u4F5C\u5BE1\u5987\u7684\u8863\u88F3\u3002
</code></pre></div><p>\u8FC7\u4E86\u8BB8\u4E45\uFF0C\u72B9\u5927\u7684\u59BB\u5B50\u4E66\u4E9A\u7684\u5973\u513F\u6B7B\u4E86\uFF0C\u72B9\u5927\u5F97\u4E86\u5B89\u6170\uFF1F</p><p>\u5979\u751F\u793A\u62C9\u7684\u65F6\u5019\uFF0C\u72B9\u5927\u6B63\u5728\u57FA\u6089\u3002\u5927\u7EA6\u80FD\u6697\u793A\u4ED6\u4EEC\u592B\u59BB\u5173\u7CFB\u51FA\u73B0\u4E86\u95EE\u9898\uFF0C\u8FD9\u8FB9\u8868\u660E\uFF0C\u4ED6\u59BB\u5B50\u6B7B\u4E86\uFF0C\u4ED6\u975E\u4F46\u6CA1\u6709\u60E7\u6015\uFF0C\u6094\u6539\uFF0C\u53CD\u800C\u5F97\u4E86\u5B89\u6170\u3002 \u4ED6\u611F\u53D7\u4E0D\u5230\u795E\u7684\u7BA1\u6559\u548C\u547C\u53EC\u3002 \u4E00\u65B9\u9762\u4ED6\u786E\u5B9E\u5728\u4E0D\u5E78\u7684\u5A5A\u59FB\u91CC\u5F97\u5230\u89E3\u8131\u3002</p><p>\u4E9A\u675C\u5170\u4EBA\u5E0C\u62C9\u5347\u7EA7\u6210\u4E3A\u72B9\u5927\u7684\u670B\u53CB\uFF0C\u901A\u8FC7\u8FD9\u4E2A\u4EBA\uFF0C\u72B9\u5927\u83B7\u5F97\u66F4\u591A\u6DEB\u4E71\u4E0A\u7684\u6E20\u9053\u3002 \u5230\u4ED6\u526A\u7F8A\u6BDB\u7684\u4EBA\u90A3\u91CC\uFF0C\u5F88\u53EF\u80FD\u662F\u53BB\u83B7\u5F97\u5AD6\u8D44\u3002 \u800C\u4E14\u4ED6\u6025\u4E0D\u53EF\u8010\uFF0C\u5728\u6CA1\u6709\u5AD6\u8D44\u7684\u65F6\u5019\u5C31\u8D4A\u5AD6\u3002</p><p>\u72B9\u5927\u53BB\u62FF\u4EAD\u62DB\u5993\u660E\u663E\u662F\u6709\u89C4\u5F8B\u7684\uFF0C\u8FD9\u4E2A\u89C4\u5F8B\u88AB\u4ED6\u739B\u628A\u63E1\u4F4F\u4E86\u3002 \u4ED6\u739B\u901A\u8FC7\u4EBA\u6709\u53EF\u9760\u7684\u60C5\u62A5\u6765\u6E90\uFF0C\u5979\u5F97\u786E\u8BA4\u72B9\u5927\u80FD\u5AD6\u5993\uFF0C\u5E76\u4E14\u624B\u5934\u6CA1\u6709\u73B0\u91D1\u6D41\uFF0C\u65E0\u6CD5\u652F\u4ED8\u5AD6\u8D44\uFF0C\u6700\u540E\u83B7\u5F97\u4E86\u5F53\u5934\u3002 \u4ED6\u739B\u4E5F\u5F97\u628A\u81EA\u5DF1\u6253\u626E\u8D77\u6765\uFF0C\u4E0D\u88AB\u72B9\u5927\u8BA4\u8BC6\u3002 \u4EE5\u524D\u7684\u5993\u5973\u628A\u81EA\u5DF1\u7684\u8EAB\u4EFD\u9690\u85CF\u8D77\u6765\u4E5F\u5F88\u666E\u904D\uFF0C\u56E0\u4E3A\u5979\u5F88\u53EF\u80FD\u660E\u9762\u4E0A\u8FD8\u662F\u4E00\u4E2A\u7537\u4EBA\u7684\u59BB\u5B50\u3002</p><div class="language-"><pre><code>\u3010\u521B38:20\u3011\u72B9\u5927\u6258\u4ED6\u670B\u53CB\u4E9A\u675C\u5170\u4EBA\u9001\u4E00\u53EA\u5C71\u7F8A\u7F94\u53BB\uFF0C\u8981\u4ECE\u90A3\u5973\u4EBA\u624B\u91CC\u53D6\u56DE\u5F53\u5934\u6765\uFF0C\u5374\u627E\u4E0D\u7740\u5979\uFF0C
\u3010\u521B38:21\u3011\u5C31\u95EE\u90A3\u5730\u65B9\u7684\u4EBA\u8BF4\uFF1A\u201C\u4F0A\u62FF\u5370\u8DEF\u65C1\u7684\u5993\u5973\u5728\u54EA\u91CC\uFF1F\u201D\u4ED6\u4EEC\u8BF4\uFF1A\u201C\u8FD9\u91CC\u5E76\u6CA1\u6709\u5993\u5973\u3002\u201D
\u3010\u521B38:22\u3011\u4ED6\u56DE\u53BB\u89C1\u72B9\u5927\u8BF4\uFF1A\u201C\u6211\u6CA1\u6709\u627E\u7740\u5979\uFF0C\u5E76\u4E14\u90A3\u5730\u65B9\u7684\u4EBA\u8BF4\uFF1A\u2018\u8FD9\u91CC\u6CA1\u6709\u5993\u5973\u3002\u2019\u201D
\u3010\u521B38:23\u3011\u72B9\u5927\u8BF4\uFF1A\u201C\u6211\u628A\u8FD9\u5C71\u7F8A\u7F94\u9001\u53BB\u4E86\uFF0C\u4F60\u7ADF\u627E\u4E0D\u7740\u5979\uFF0C\u4EFB\u51ED\u5979\u62FF\u53BB\u5427\uFF0C\u514D\u5F97\u6211\u4EEC\u88AB\u7F9E\u8FB1\u3002\u201D
</code></pre></div><p>\u72B9\u5927\u62D6\u670B\u53CB\u4E9A\u675C\u5170\u4EBA\u5E0C\u62C9\u53BB\u9001\u5AD6\u8D44\uFF0C\u4ED6\u4EEC\u5904\u7406\u8FD9\u4E8B\u60C5\u6CA1\u6709\u906E\u906E\u63A9\u63A9\uFF0C\u5E0C\u62C9\u975E\u5E38\u53EF\u9760\uFF0C\u914D\u5408\u3002 \u516C\u5F00\u53BB\u627E\u5993\u5973\uFF0C\u8FD9\u4E2A\u975E\u5E38\u7F9E\u8FB1\u7684\u4E8B\u60C5\u3002</p><p>\u4ED6\u4EEC\u8BF4\uFF1A\u201C\u8FD9\u91CC\u5E76\u6CA1\u6709\u5993\u5973\u3002\u201D \u5F53\u5730\u4EBA\u5426\u5B9A\u4E86\u8FD9\u91CC\u6709\u5993\u5973\uFF0C\u5426\u5B9A\u4E86\u8FD9\u91CC\u6709\u5356\u6DEB\u7684\u884C\u4E3A\uFF0C\u5F53\u7136\u4E5F\u662F\u63D0\u9192\u72B9\u5927\uFF0C\u4F60\u6DEB\u4E71\u7684\u5BF9\u8C61\u4E0D\u662F\u4E00\u822C\u5993\u5973\u3002 \u53EF\u4EE5\u8BF4\u8FD9\u89E3\u9664\u4E86\u4ED6\u739B\u4F5C\u4E3A\u5993\u5973\u7684\u7F6A\u540D\u3002 \u4ED6\u739B\u5982\u679C\u662F\u4E00\u822C\u6027\u7684\u5993\u5973\u4E5F\u6839\u672C\u4E0D\u53EF\u80FD\u627E\u516C\u516C\u6DEB\u4E71\u3002\u4E0D\u8C0B\u94B1\u8D22\u53CD\u800C\u662F\u72B9\u5927\u7684\u5370\uFF0C\u8170\u5E26\uFF0C\u6756\u3002\u4ED6\u660E\u663E\u662F\u6709\u4E00\u79CD\u4F01\u56FE\u5FC3\uFF0C\u4E3A\u8981\u7ED9\u72B9\u5927\u5BB6\u7ACB\u540E\u3002 \u540C\u65F6\u53C8\u8981\u88AB\u72B9\u5927\u627F\u8BA4\uFF0C\u513F\u5B50\u80FD\u5F52\u5165\u72B9\u5927\u7684\u540D\u4E0B\u3002 \u5370\u4EE3\u8868\u8D22\u5BCC\uFF0C\u4EA7\u4E1A\u7684\u652F\u914D\u6743\uFF0C\u6756\u4EE3\u8868\u6743\u67C4\uFF0C\u5E26\u5B50\u4EE3\u8868\u7740\u72B9\u5927\u7684\u8EAB\u4EFD\uFF0C\u8840\u7EDF\u3002\u7CBE\u660E\u7684\u72B9\u5927\u5DF2\u7ECF\u88AB\u7CBE\u5FC3\u8C0B\u9A97\u7684\u513F\u5987\u9A97\u53BB\u4E86\u4E00\u5207\u3002 \u5F53\u7136\u4E5F\u663E\u793A\u72B9\u5927\u5BF9\u8FD9\u4E9B\u5E76\u4E0D\u5728\u4E4E\uFF0C\u6216\u8005\u6839\u672C\u6CA1\u6709\u610F\u8BC6\u5230\u540D\u5206\u6743\u67C4\u7684\u91CD\u8981\u6027\u3002 \u8FD9\u70B9\u8FDC\u8FDC\u4E0D\u5982\u96C5\u5404\u3002</p><h2 id="\u56DB\u3001\u53CC\u751F\u5B50" tabindex="-1">\u56DB\u3001\u53CC\u751F\u5B50 <a class="header-anchor" href="#\u56DB\u3001\u53CC\u751F\u5B50" aria-hidden="true">#</a></h2><div class="language-"><pre><code>\u3010\u521B38:24\u3011\u7EA6\u8FC7\u4E86\u4E09\u4E2A\u6708\uFF0C\u6709\u4EBA\u544A\u8BC9\u72B9\u5927\u8BF4\uFF1A\u201C\u4F60\u7684\u513F\u5987\u4ED6\u739B\u4F5C\u4E86\u5993\u5973\uFF0C\u4E14\u56E0\u884C\u6DEB\u6709\u4E86\u8EAB\u5B55\u3002\u201D\u72B9\u5927\u8BF4\uFF1A\u201C\u62C9\u51FA\u5979\u6765\uFF0C\u628A\u5979\u70E7\u4E86\uFF01\u201D
\u3010\u521B38:25\u3011\u4ED6\u739B\u88AB\u62C9\u51FA\u6765\u7684\u65F6\u5019\uFF0C\u4FBF\u6253\u53D1\u4EBA\u53BB\u89C1\u5979\u516C\u516C\uFF0C\u5BF9\u4ED6\u8BF4\uFF1A\u201C\u8FD9\u4E9B\u4E1C\u897F\u662F\u8C01\u7684\uFF0C\u6211\u5C31\u662F\u4ECE\u8C01\u6000\u7684\u5B55\u3002\u8BF7\u4F60\u8BA4\u4E00\u8BA4\uFF0C\u8FD9\u5370\u548C\u5E26\u5B50\u5E76\u6756\u90FD\u662F\u8C01\u7684\uFF1F\u201D
\u3010\u521B38:26\u3011\u72B9\u5927\u627F\u8BA4\u8BF4\uFF1A\u201C\u5979\u6BD4\u6211\u66F4\u6709\u4E49\uFF0C\u56E0\u4E3A\u6211\u6CA1\u6709\u5C06\u5979\u7ED9\u6211\u7684\u513F\u5B50\u793A\u62C9\u3002\u201D\u4ECE\u6B64\u72B9\u5927\u4E0D\u518D\u4E0E\u5979\u540C\u5BDD\u4E86\u3002

\u3010\u521B38:27\u3011\u4ED6\u739B\u5C06\u8981\u751F\u4EA7\uFF0C\u4E0D\u6599\u5979\u8179\u91CC\u662F\u4E00\u5BF9\u53CC\u751F\u3002
\u3010\u521B38:28\u3011\u5230\u751F\u4EA7\u7684\u65F6\u5019\uFF0C\u4E00\u4E2A\u5B69\u5B50\u4F38\u51FA\u4E00\u53EA\u624B\u6765\uFF0C\u6536\u751F\u5A46\u62FF\u7EA2\u7EBF\u62F4\u5728\u4ED6\u624B\u4E0A\uFF0C\u8BF4\uFF1A\u201C\u8FD9\u662F\u5934\u751F\u7684\u3002\u201D
\u3010\u521B38:29\u3011\u968F\u540E\u8FD9\u5B69\u5B50\u628A\u624B\u6536\u56DE\u53BB\uFF0C\u4ED6\u54E5\u54E5\u751F\u51FA\u6765\u4E86\u3002\u6536\u751F\u5A46\u8BF4\uFF1A\u201C\u4F60\u4E3A\u4EC0\u4E48\u62A2\u7740\u6765\u5462\uFF1F\u201D\u56E0\u6B64\u7ED9\u4ED6\u8D77\u540D\u53EB\u6CD5\u52D2\u65AF\u3002
\u3010\u521B38:30\u3011\u540E\u6765\uFF0C\u4ED6\u5144\u5F1F\u90A3\u624B\u4E0A\u6709\u7EA2\u7EBF\u7684\u4E5F\u751F\u51FA\u6765\uFF0C\u5C31\u7ED9\u4ED6\u8D77\u540D\u53EB\u8C22\u62C9\u3002
</code></pre></div><p><strong>\u62C9\u51FA\u5979\u6765\uFF0C\u628A\u5979\u70E7\u4E86</strong> \u5AD6\u5BA2\u7684\u6B8B\u5FCD\u548C\u5047\u5192\u4F2A\u5584\u3002 \u72B9\u5927\u5728\u634D\u536B\u5F8B\u6CD5\uFF0C\u9053\u4E49\uFF0C\u4F46\u8DF3\u8FC7\u4E86\u81EA\u5DF1\u3002\u4E25\u4E8E\u5F8B\u4EBA\uFF0C\u5BBD\u4E8E\u5F8B\u5DF1\u3002 \u72B9\u5927\u5BF9\u81EA\u5DF1\u6DEB\u4E71\u7684\u7F6A\u8BB3\u83AB\u5982\u6DF1\uFF0C\u5BF9\u5979\u739B\u7684\u6DEB\u4E71\u7684\u7F6A\u5374\u6BEB\u4E0D\u7559\u60C5\u3002</p><p><strong>\u5979\u6BD4\u6211\u66F4\u6709\u4E49</strong> \u72B9\u5927\u4E0D\u662F\u6307\u8D23\u5BF9\u65B9 \uFF0C \u4ED6\u627F\u8BA4\u81EA\u5DF1\u7684\u4E0D\u4E49\uFF0C\u6CA1\u6709\u628A\u5979\u7ED9\u513F\u5B50\u793A\u62C9\u3002\u5728\u7ED9\u513F\u5B50\u7ACB\u540E\u8FD9\u4EF6\u4E8B\u4E0A\u72B9\u5927\u4E0D\u4E0A\u5FC3\uFF0C\u540E\u4EE3\u5173\u7CFB\u5230\u795E\u7684\u5E94\u8BB8\u3002</p><p>\u5F53\u7136\u4ED6\u739B\u540C\u6837\u6709\u7F6A\uFF0C\u5979\u53EF\u80FD\u6839\u672C\u4E0D\u61C2\u8BEB\u547D\u3002\u5728\u5979\u7684\u5224\u65AD\u4E2D\uFF0C\u54EA\u6015\u4E71\u4F26\uFF0C\u72AF\u7F6A\u5F97\u5230\u513F\u5B50\u90FD\u662F\u503C\u5F97\u7684\u3002 \u8FD9\u4E00\u5BB6\u5B8C\u5168\u4E16\u4FD7\u5316\uFF0C\u548C\u8FE6\u5357\u4EBA\u5E76\u65E0\u5206\u522B\u3002 \u8FD9\u4E5F\u7A81\u51FA\u4E86\u4EE5\u8272\u5217\u5BB6\u65CF\u9700\u8981\u5F8B\u6CD5\u7BA1\u7406\u4ED6\u4EEC\u3002</p><blockquote><p>\u540E\u6765\u5728\u6469\u897F\u65F6\u671F\uFF0C\u7533\u547D\u8BB0\u6709\u6761\u4F8B\u89C4\u5B9A\uFF1A\u6B7B\u8005\u7684\u5144\u5F1F\u53EF\u4EE5\u62D2\u7EDD\u5144\u4EA1\u5F1F\u7EE7\u7684\u4E49\u52A1\uFF0C\u4E0D\u8FC7\u4ED6\u82E5\u62D2\u7EDD\u5C31\u8981\u88AB\u770B\u4F5C\u662F\u53EF\u803B\u7684\u3002\u53D4\u5AC2\u4FE9\u8981\u5F53\u7740\u672C\u5730\u957F\u8001\u7684\u9762\uFF0C\u5AC2\u5B50\u8981\u8131\u4E0B\u5C0F\u53D4\u7684\u978B\uFF08[\u5F974:7-8]\uFF09\uFF0C\u5E76\u5410\u553E\u6CAB\u5728\u4ED6\u8138\u4E0A\uFF08[\u753325:5-10]\uFF09\u3002 \u5728\u4EE5\u540E\u76F8\u5F53\u957F\u7684\u65F6\u671F\u5185\uFF0C\u4EE5\u8272\u5217\u5BF9\u5F85\u8FD9\u7C7B\u95EE\u9898\uFF0C\u90FD\u662F\u5BB9\u8BB8\u5E74\u8F7B\u5BE1\u5987\u5728\u670D\u4E27\u671F\u540E\u5C3D\u5FEB\u518D\u5A5A\u3002\u5979\u4EEC\u6709\u7279\u522B\u7684\u8863\u7740\uFF0C\u6765\u8868\u660E\u5176\u5BE1\u5987\u8EAB\u5206\u3002\u7531\u4E8E\u5BE1\u5987\u6CA1\u6709\u627F\u53D7\u9057\u4EA7\u7684\u6743\u5229\uFF0C\u5F8B\u6CD5\u7279\u51C6\u5979\u4EEC\u5728\u522B\u4EBA\u6536\u5272\u540E\u7684\u7530\u91CC\u62FE\u9EA6\u7A57\uFF08[\u5F972:2]\uFF09\uFF0C\u53C8\u4FDD\u62A4\u5979\u4EEC\u4E0D\u53D7\u6B3A\u8D1F(\u753310:18\uFF0C14:29)\u3002\u60DF\u6709\u796D\u53F8\u7684\u5973\u513F\u6709\u6743\u5229\u5149\u8363\u56DE\u5F52\u7236\u5BB6\uFF08[\u522922:13]\uFF09\u3002</p><p>\u795E\u901A\u8FC7\u5F8B\u6CD5\u7684\u89C4\u5B9A\u4FDD\u62A4\u90A3\u4E9B\u5973\u5B50\u3002</p></blockquote><p><strong>\u4ECE\u6B64\u72B9\u5927\u4E0D\u518D\u4E0E\u5979\u540C\u5BDD\u4E86</strong> \u72B9\u5927\u6CA1\u6709\u4E0E\u8FD9\u4E2A\u540D\u4E49\u4E0A\u81EA\u5DF1\u513F\u5B50\u7684\u6BCD\u4EB2\u540C\u5C45\uFF0C\u4ED6\u5F00\u59CB\u7BA1\u5236\u81EA\u5DF1\u7684\u60C5\u6B32\u3002</p><p><strong>\u4E00\u5BF9\u53CC\u751F</strong> \u53CC\u80DE\u80CE\u662F\u795E\u5BF9\u72B9\u5927\u5BB6\u7684\u601C\u60AF\uFF0C\u56E0\u4E3A\u72B9\u5927\u6B7B\u4E86\u4E24\u4E2A\u513F\u5B50\u3002 \u4E5F\u662F\u5BF9\u4ED6\u739B\u52A0\u500D\u7684\u6069\u5178\u3002 \u53CC\u80DE\u80CE\u7684\u795E\u5B66\u60C5\u666F\u5267\uFF0C\u53C8\u662F\u4E00\u4E2A\u957F\u5B50\u53D8\u6210\u6B21\u5B50\u5267\u60C5\u81F4\u656C\u3002 \u4EE5\u5B9E\u739B\u5229\u548C\u7EA6\u745F\uFF0C\u96C5\u5404\u548C\u4EE5\u626B\uFF0C\u4EE5\u6CD5\u83B2\u548C\u739B\u62FF\u897F\u3002\u4E00\u822C\u5B69\u5B50\u51FA\u751F\u540E\uFF0C\u4EBA\u7684\u773C\u5149\uFF0C\u671F\u5F85\uFF0C\u4F7F\u7528\u66F4\u591A\u7684\u4F1A\u504F\u5411\u957F\u5B50\u3002 \u957F\u5B50\u66F4\u9700\u8981\u62C5\u5F53\uFF0C\u9886\u8896\u7684\u54C1\u683C\u3002 \u800C\u8FD9\u4E9B\u901A\u5E38\u662F\u4ECE\u4E16\u754C\u6765\u7684\uFF0C\u4E5F\u662F\u4E16\u754C\u8D4B\u4E88\u7684\u3002 \u795E\u5374\u62E3\u9009\u8F6F\u5F31\u7684\uFF0C\u5E7C\u5C0F\u7684\uFF0C\u5351\u5FAE\u7684\u3002 \u5305\u62EC\u6469\u897F\uFF0C\u6709\u54E5\u54E5\u548C\u59D0\u59D0\uFF0C\u4ED6\u7B97\u6700\u5C0F\u3002\u5305\u62EC\u5927\u536B\u4E5F\u662F\u6700\u5C0F\u7684\uFF0C\u88AB\u7236\u4EB2\u8FB9\u7F18\u5316\uFF0C\u88AB\u54E5\u54E5\u4EEC\u6392\u6324\u3002 \u4ED6\u4EEC\u51ED\u7740\u51FA\u751F\u548C\u5916\u5728\uFF0C\u6CA1\u6709\u4EC0\u4E48\u53EF\u5938\u7684\u3002</p><p>\u72B9\u5927\u4EE5\u53CA\u8FD9\u4E2A\u4EE5\u8272\u5217\u5BB6\u65CF\u4E0D\u53EF\u80FD\u4E0D\u77E5\u9053\u5229\u767E\u52A0\u751F\u4E24\u4E2A\u513F\u5B50\uFF0C\u795E\u5982\u4F55\u5E94\u8BB8\u7684\u4E8B\u60C5\u3002 \u4ED6\u4EEC\u4E5F\u4E00\u5B9A\u77E5\u9053\u795E\u5982\u4F55\u5E26\u9886\u96C5\u5404\u8FDB\u5165\u547D\u5B9A\u3002 \u795E\u85C9\u7740\u4ED6\u6240\u8981\u62E3\u9009\u7684\u5A74\u5B69\uFF0C\u85C9\u7740\u4ED6\u7684\u6210\u957F\uFF0C\u6405\u52A8\u5BB6\u65CF\u3002 \u4E00\u4E2A\u5BB6\u65CF\u4E2D\uFF0C\u51FA\u4E86\u4E00\u4E2A\u88AB\u795E\u4F7F\u7528\u7684\u4EBA\uFF0C\u5BF9\u5168\u5BB6\u90FD\u662F\u795D\u798F\u3002 \u73B0\u5728\u6559\u4F1A\u4E2D\u6709\u5F88\u591A\u4FE1\u4E8C\u4EE3\uFF0C\u4ED6\u4EEC\u4ECE\u7236\u6BCD\u90A3\u91CC\u7EE7\u627F\u4E86\u5E76\u4E0D\u5168\u5907\u7684\u4FE1\u4EF0\uFF0C\u6240\u4EE5\u4ED6\u4EEC\u81EA\u5DF1\u8981\u5728\u6559\u4F1A\u4E2D\u6210\u957F\uFF0C\u6709\u4E00\u5929\u80FD\u56DE\u53BB\u5F71\u54CD\u7236\u6BCD\u7684\u4FE1\u4EF0\u3002</p><p>\u795E\u5BF9\u4EE5\u8272\u5217\u7684\u4FDD\u5B88\uFF0C\u5C31\u662F\u4E0D\u65AD\u6709\u4EBA\u5174\u8D77\uFF0C\u652F\u6491\u8D77\u8FD9\u4E2A\u6C11\u65CF\u7684\u4FE1\u4EF0\u3002 \u7279\u522B\u662F\u5927\u536B\u5BB6\u7684\u4FDD\u5B88\uFF0C\u4E0D\u65AD\u5927\u536B\u7684\u540E\u4EE3\u63A5\u7EED\u4ED6\u505A\u738B\u3002</p>`,36),d=[t];function o(s,c,i,l,h,g){return a(),n("div",null,d)}var v=e(r,[["render",o]]);export{u as __pageData,v as default};
