import{_ as p,c as _,o as t,a as e}from"./app.00e6071a.js";const h='{"title":"","description":"","frontmatter":{},"headers":[],"relativePath":"poem/\u7ED9\u820D\u5DF1\u7684\u72EC\u767D.md"}',a={},o=e("<p>\u4E3B\u554A\uFF0C\u5B9E\u5728\u65E0\u6CD5\u660E\u767D\u662F\u600E\u6837\u7684\u7231\uFF0C\u600E\u6837\u7684\u820D\u5DF1\u4E4B\u5FC3\uFF0C\u600E\u6837\u7684\u987A\u670D\uFF0C\u8BA9\u4F60\u6210\u5C31\u8FD9\u6837\u7684\u5341\u67B6\u6551\u8D4E\u3002\u96BE\u9053\u6CA1\u6709\u8F7B\u7701\u7684\u6551\u8D4E\u65B9\u6848\u5417\uFF1F\u6211\u76F8\u4FE1\u4F60\u4E00\u5B9A\u80FD\u8BBE\u8BA1\u51FA\u6765\uFF0C\u4F60\u53EF\u4EE5\u4E0D\u7528\u6765\u8FD9\u4E0D\u914D\u8BA9\u4F60\u6765\u7684\u4E16\u754C\u3002 \u6211\u4EEC\u8FD9\u6837\u7F6A\u6076\uFF0C\u65E0\u77E5\uFF0C\u6E3A\u5C0F\u7684\u751F\u547D\uFF0C\u4E0D\u503C\u5F97\u4F60\u6765\u505A\u90A3\u4E48\u591A\u3002\u4F46\u662F\u4F60\u6765\u4E86\uFF0C \u4E3A\u7740\u6709\u7F6A\u6709\u75C5\u6709\u9650\u7684\u4EBA\u3002\u6CA1\u6709\u4EBA\u770B\u52A8\u7269\u98DE\u9E1F\u6606\u866B\u7684\u9AD8\u9AD8\u5728\u4E0A\uFF0C\u6CA1\u6709\u7528\u4EBA\u4EE5\u8F96\u5236\uFF0C\u5974\u5F79\u3002\u9009\u62E9\u7684\u5374\u662F\u5351\u5FAE\uFF0C\u8D2B\u4E4F\uFF0C\u8BEF\u89E3\uFF0C\u654C\u610F\uFF0C\u80CC\u53DB\uFF0C\u5492\u8BC5\u2026\u2026\u7684\u751F\u5B58\u548C\u6B7B\u4EA1\u3002\u5728\u4EBA\u4EEC\u7F16\u7EC7\u7684\u68A6\u60F3\u91CC\u9762\uFF0C\u5145\u6EE1\u7740\u5BCC\u8DB3\u548C\u5584\u610F\u3002\u5E76\u4E3A\u4E4B\u52AA\u529B\uFF0C\u4E3A\u4E4B\u594B\u6597\u3002\u4ED6\u4EEC\u7684\u820D\u5F03\u548C\u80CC\u8D1F\uFF0C\u90FD\u4E0D\u8FC7\u4E3A\u4E86\u6210\u5168\u81EA\u5DF1\uFF0C\u6EE1\u8DB3\u4ECA\u751F\u3002\u6211\u4E5F\u7528\u65E0\u6570\u5E7B\u60F3\u53BB\u9003\u907F\u7684\u82E6\u96BE\u3002\u7528\u52AA\u529B\uFF0C\u4E5F\u7528\u7948\u6C42\uFF0C\u8C0B\u6C42\u81EA\u5DF1\u7684\u6E34\u671B\u3002\u9A71\u52A8\u6211\u7684\u4F9D\u7136\u662F\u5FC3\u4E2D\u6240\u559C\u597D\u7684\uFF0C \u4F9D\u7136\u540C\u5982\u4E16\u4EBA\u4E00\u6837\u53D6\u820D\uFF0C\u548C\u80CC\u8D1F\u3002</p><p>\u6211\u80FD\u505A\u7684\u820D\u5DF1\uFF0C\u5145\u6EE1\u7740\u8BA1\u8F83\uFF0C\u76D8\u7B97\u3002\u901A\u8FC7\u820D\u5F03\u4E00\u4E9B\u6B21\u8981\uFF0C\u5FAE\u5C0F\u7684\u5229\u76CA\uFF0C\u53BB\u6362\u53D6\u66F4\u9700\u8981\uFF0C\u66F4\u591A\u7684\u5229\u76CA\u3002 \u4E5F\u901A\u8FC7\u820D\u5F03\u5229\u76CA\u53BB\u6362\u5F97\u60C5\u4E49\uFF0C\u88AB\u4EBA\u79F0\u4E49\uFF0C\u88AB\u81EA\u5DF1\u79F0\u4E49\u3002</p><p>\u4EBA\u610F\uFF0C\u60C5\u6B32\u7684\u820D\u5DF1\uFF0C\u90FD\u88AB\u4F60\u770B\u4E3A\u6C61\u79FD\uFF0C\u5E76\u4E14\u6B32\u671B\u5E38\u5E38\u80DC\u8FC7\u6211\u4EEC\u7684\u5229\u4ED6\u4E4B\u5FC3\u3002 \u4EBA\u7684\u8D2A\u6C42\uFF0C\u5E38\u5E38\u5BFC\u81F4\u727A\u7272\u522B\u4EBA\uFF0C\u8F96\u5236\u522B\u4EBA\u6765\u6210\u5168\u81EA\u5DF1\u3002 \u6211\u4E5F\u5E38\u5E38\u5982\u6B64\uFF0C\u5E38\u5E38\u4F24\u5BB3\u4E86\u4EBA\uFF0C\u4E8F\u6B20\u4E86\u4EBA\uFF0C\u4E5F\u6DF1\u6DF1\u5F97\u7F6A\u4E86\u4F60\u3002</p><p>\u4E3B\u554A\uFF0C\u8D2A\u5A6A\u4E16\u754C\uFF0C\u5728\u610F\u4ECA\u751F\u7684\u5FC3\u4ECD\u7136\u5728\u6211\u91CC\u9762\uFF0C\u6211\u600E\u4E48\u80FD\u53BB\u820D\u5DF1\u5462\uFF1F\u56E0\u4E3A\u6211\u7684\u751F\u547D\u4ECD\u7136\u5728\u63A8\u5D07\u5730\u4E0A\u7684\u667A\u6167\uFF0C\u5229\u76CA\uFF0C\u5C0A\u4E25\u3002\u6211\u65E0\u4EE5\u81EA\u53D1\u7684\u6539\u6362\u751F\u547D\uFF0C\u552F\u72EC\u8FDB\u5165\u5230\u4F60\u7684\u751F\u547D\u91CC\u9762\u3002\u53BB\u4F53\u4F1A\u4F60\u7684\u820D\u5DF1\u5927\u7231\uFF0C \u4F53\u4F1A\u4F60\u9009\u62E9\u82E6\u96BE\uFF0C\u5351\u5FAE\uFF0C\u5C48\u8FB1\uFF0C\u8EAB\u8D25\u540D\u88C2\u7684\u6551\u8D4E\u65B9\u5F0F\u3002</p><p>\u4F60\u4F7F\u6211\u4EEC\u6210\u4E3A\u4E00\u4E2A\u65B0\u9020\u7684\u4EBA\uFF0C\u6709\u4F60\u751F\u547D\u6027\u60C5\u7684\u4EBA\uFF0C\u5982\u4ECA\u867D\u7136\u505A\u4E0D\u5230\uFF0C\u5374\u660E\u767D\u4F60\u7684\u820D\u5DF1\u732E\u4E0A\uFF0C\u4E5F\u6E34\u6155\u987A\u670D\uFF0C\u5FE0\u5FC3\uFF0C\u59D4\u8EAB\uFF0C\u6295\u9760\u7684\u54C1\u8D28\uFF0C\u652F\u914D\u6211\u7684\u751F\u547D\u3002 \u4E3B\u554A\uFF0C\u66F4\u6DF1\u7684\u5F97\u7740\u6211\uFF0C\u8BA9\u6211\u770B\u91CD\u4F60\u7684\u65E8\u610F\uFF0C\u660E\u767D\u4F60\u547C\u53EC\u6211\u4EEC\u5F52\u5411\u4F60\u7684\u5FC3\u610F\u3002</p><p>\u6C42\u4F60\u575A\u5B9A\u6211\u91CC\u9762\u820D\u5DF1\u7684\u5FC3\u5FD7\u3002\u4E3A\u4F60\u7684\u56FD\u4F60\u7684\u65E8\u610F\u52AA\u529B\uFF0C\u4F60\u4E5F\u5FC5\u7136\u770B\u987E\u7267\u517B\u7684\u6211\u7684\u4E00\u751F\u3002\u6211\u53EF\u4EE5\u4E0D\u7528\u5FE7\u8651\u4ECA\u751F\uFF0C\u56E0\u4F60\u505A\u6211\u968F\u65F6\u7684\u501A\u9760\u3002</p><p>\u77E5\u9053\u8FD9\u4E16\u754C\u6CA1\u6709\u4EC0\u4E48\u503C\u5F97\u8FFD\u6C42\u7684\uFF0C\u4F60\u8981\u9884\u5907\u7684\u7F8E\u597D\u65E0\u4E0E\u4F26\u6BD4\u3002\u4F60\u4E0D\u5728\u610F\u7684\uFF0C\u6709\u4EC0\u4E48\u4E0D\u80FD\u653E\u4E0B\uFF0C\u56E0\u4E3A\u8F6C\u77AC\u5373\u901D\uFF0C\u4F60\u5728\u610F\u7684\uFF0C\u5374\u610F\u4E49\u6C38\u6052\u3002</p><p>\u66FE\u7ECF\u591A\u5C11\u6B21\u57CB\u6028\u4F60\uFF0C\u8BA9\u6211\u5EA6\u8FC7\u6CA1\u6709\u7236\u7231\u7684\u7AE5\u5E74\uFF0C\u6CA1\u6709\u592A\u597D\u6559\u80B2\u7684\u6210\u957F\u671F\uFF0C\u7ECF\u5386\u8FC7\u8D2B\u82E6\uFF0C\u8270\u8F9B\uFF0C\u4F24\u5BB3\u3002\u5145\u6EE1\u7740\u81EA\u5351\uFF0C\u63D0\u9632\uFF0C\u654F\u611F\uFF0C\u81EA\u5C0A\u5FC3\uFF0C\u53DB\u9006\uFF0C\u6124\u6124\u4E0D\u5E73\u7684\u6027\u60C5\u3002 \u5BF9\u4EBA\u4E5F\u5145\u6EE1\u7740\u9003\u907F\uFF0C\u9694\u9602\uFF0C\u51B7\u6F20\uFF0C\u4E0D\u61C2\u5206\u5BF8\u7684\u76F8\u4EA4\u969C\u788D\u3002 \u5728\u6211\u91CC\u9762\uFF0C\u597D\u50CF\u4E16\u754C\u6B20\u7740\u6211\u4EC0\u4E48\uFF0C\u597D\u50CF\u4E3B\u4F60\u4E8F\u5F85\u6211\u4E86\u3002</p><p>\u4E3B\u554A\uFF0C\u597D\u60F3\u5E38\u5E38\u88AB\u4F60\u89E6\u6478\uFF0C\u88AB\u4F60\u5B89\u6170\uFF0C\u6027\u60C5\u6DF1\u5904\u7684\u4F24\u75D5\u88AB\u4F60\u533B\u6CBB\u3002\u56E0\u4E3A\u54C1\u5C1D\u4F60\u7684\u7F8E\u597D\uFF0C\u66FE\u7ECF\u7684\u8FC7\u5F80\u90FD\u5FAE\u4E0D\u8DB3\u9053\u3002 \u77E5\u9053\u4F60\u5BF9\u6211\u8FD9\u6837\u7684\u751F\u547D\uFF0C\u6709\u4F55\u7B49\u7684\u9976\u6055\uFF0C\u5BF9\u6211\u4E00\u751F\u6709\u4F55\u7B49\u7684\u6276\u6301\u548C\u770B\u987E\u3002 \u6211\u5C31\u65E0\u6BD4\u6E34\u671B\u7684\u5411\u4EBA\u8BC9\u8FF0\uFF0C\u6211\u7684\u4E3B\u4F55\u7B49\u7F8E\u597D\u3002 \u751A\u81F3\u5168\u529B\u4EE5\u8D74\uFF0C\u5C3D\u5DF1\u6240\u80FD\u503E\u5012\u4F60\u7ED9\u6211\u7684\u795D\u798F\u3002</p><p>\u6211\u60F3\u90A3\u6837\uFF0C\u6211\u53EF\u4EE5\u6B23\u7136\u7684\u53BB\u820D\u5DF1\uFF0C\u56E0\u4E3A\u6709\u4F60\u6E90\u6E90\u4E0D\u65AD\u7684\u4F9B\u5E94\u548C\u5B89\u6170\u3002 \u6211\u53EF\u4EE5\u6B23\u7136\u80CC\u4E0A\u5341\u67B6\uFF0C\u56E0\u4E3A\u4F60\u7684\u62C5\u5B50\u662F\u8F7B\u7701\u7684\uFF0C\u6709\u5723\u7075\u7684\u5E2E\u52A9\uFF0C\u6709\u5BB6\u4EBA\u5F7C\u6B64\u540C\u5DE5\uFF0C\u6709\u4F60\u638C\u7BA1\u8FC7\u7A0B\u548C\u7ED3\u679C\u3002</p><p>\u597D\u60F3\u6D3B\u5728\u5145\u6EE1\u4F60\u795D\u798F\u7684\u8282\u594F\u91CC\uFF0C\u5E26\u52A8\u6211\u4E0D\u51B7\u4E0D\u70ED\u7684\u751F\u547D\u4E3A\u4F60\u8E0A\u8DC3\u3002 \u6211\u8981\u4E13\u6CE8\uFF0C\u5728\u4E4E\u4F60\u7684\u8BDD\u8BED\uFF0C\u6709\u6548\u7684\u79EF\u7D2F\u5C5E\u7075\u7684\u77E5\u8BC6\uFF0C\u9884\u5907\u81EA\u5DF1\u505A\u4F60\u7684\u5DE5\u4EBA\uFF0C\u8981\u66F4\u70ED\u5207\u7684\u670D\u4E8B\u6559\u4F1A\u548C\u4ED6\u4EBA\uFF0C\u4F7F\u6211\u6210\u4E3A\u4F60\u5411\u7740\u8FD9\u4E0D\u88AB\u795D\u798F\u7684\u4E16\u754C\uFF0C\u8F93\u51FA\u795D\u798F\u7684\u4EBA\u3002</p><p>\u8FD9\u4E2A\u4E0D\u786E\u5B9A\u7684\u4E16\u754C\uFF0C\u4F60\u662F\u6211\u4EEC\u7684\u552F\u4E00\u786E\u5B9A\u3002 \u6211\u4EEC\u4E5F\u4E00\u540C\u88AB\u4F60\u786E\u5B9A\u3002</p><hr><p>\u5355\u8BBA\u820D\u5DF1\uFF0C\u8FD9\u662F\u4EBA\u5F88\u7A00\u7F3A\uFF0C\u4F46\u4E5F\u5F88\u666E\u904D\u7684\u54C1\u8D28\u3002\u820D\u5DF1\u4E00\u65B9\u9762\u662F\u4E00\u79CD\u666E\u904D\u73B0\u8C61\uFF0C\u4EB2\u60C5\u53CB\u60C5\u7231\u60C5\u4EBA\u7C7B\u793E\u4F1A\u7684\u4F26\u7406\u5E38\u5E38\u9760\u9700\u8981\u591A\u4EBA\u7684\u820D\u5DF1\u53BB\u7EF4\u7CFB\uFF1B\u4EBA\u5E38\u5E38\u4E3A\u4E86\u66F4\u591A\u7684\u83B7\u53D6\uFF0C\u800C\u820D\u5F03\u4E00\u4E9B\u5FAE\u5C0F\u7684\uFF0C\u6B21\u8981\u7684\u5229\u76CA\uFF0C\u83B7\u53D6\u66F4\u591A\u7684\u5229\u76CA\u3002 \u6295\u5165\u548C\u56DE\u62A5\u7684\u535A\u5F08\uFF0C\u6784\u5EFA\u4E86\u5546\u4E1A\u793E\u4F1A\u3002\u4E5F\u6709\u4E00\u65B9\u9762\u5728\u4EBA\u6587\u5173\u6000\uFF0C\u5728\u6148\u7231\u9886\u57DF\uFF0C\u201C\u7A77\u5219\u72EC\u5584\u5176\u8EAB\uFF0C\u8FBE\u8005\u517C\u5584\u5929\u4E0B\u3002\u201D \u6309\u7167\u795E\u5F62\u8C61\u9020\u7684\u4EBA\uFF0C\u4ECD\u7136\u80FD\u663E\u51FA\u53EF\u8D35\u7684\u54C1\u8D28\u6765\u3002\u4F46\u8FD9\u4E9B\u90FD\u88AB\u795E\u8BC4\u4E3A\u6C61\u79FD\uFF0C\u770B\u4E3A\u53EF\u5492\u8BC5\u7684\u3002</p><p>\u4EBA\u7684\u820D\u5DF1\u53D1\u751F\u5728\u4EBA\u4E0E\u7269\u4E4B\u95F4\uFF0C\u4EBA\u4E0E\u4EBA\u4E4B\u95F4\uFF0C\u5374\u6CA1\u6709\u53D1\u751F\u5728\u4EBA\u548C\u795E\u4E4B\u95F4\u3002 \u4EE5\u4EBA\u81EA\u8EAB\u4E3A\u51FA\u53D1\u70B9\u7684\u820D\u5DF1\uFF0C\u5F53\u6E34\u671B\u5F97\u5230\u7684\u8D2A\u6C42\u8D85\u8FC7\u4E86\u4ED8\u51FA\u4E4B\u5FC3\u3002\u90A3\u4E48\u4EBA\u5FC3\u5C31\u4F1A\u5145\u65A5\u7F6A\u6076\u3002\u4EE5\u727A\u7272\u4ED6\u4EBA\uFF0C\u8F96\u5236\u4ED6\u4EBA\uFF0C\u7279\u522B\u662F\u4EE5\u5F97\u7F6A\u795E\u7684\u65B9\u5F0F\u6EE1\u8DB3\u81EA\u5DF1\u3002</p><p>\u4EBA\u81EA\u8EAB\u5C31\u662F\u6240\u80FD\u83B7\u53D6\u7684\u5C31\u6709\u9650\uFF0C\u80FD\u4ED8\u51FA\u7684\u66F4\u52A0\u6709\u9650\uFF0C\u4EBA\u6709\u9650\u5EA6\u7684\u751F\u547D\u5C31\u6CE8\u5B9A\u4E86\u820D\u5DF1\u7684\u7A0B\u5EA6\uFF0C\u6781\u5176\u6709\u9650\uFF0C\u5FAE\u4E0D\u8DB3\u9053\u3002</p><p>\u800C\u4EE5\u4EBA\u7684\u820D\u5DF1\u5145\u6EE1\u4E86\u65E0\u77E5\u548C\u865A\u8C0E\u3002\u820D\u5F03\u4E86\u751F\u547D\uFF0C\u6362\u53D6\u4E86\u77E5\u8BC6\uFF0C\u80FD\u529B\uFF0C\u94B1\u8D22\uFF0C\u6362\u6765\u7684\u8FD9\u4E9B\u865A\u7A7A\uFF0C\u53C8\u80FD\u7528\u4EC0\u4E48\u6362\u53D6\u751F\u547D\u5462\uFF1F</p><p>\u4F46\u57FA\u7763\u5374\u638C\u7BA1\u4E07\u6709\uFF0C\u627F\u8F7D\u4E00\u5207\u5C0A\u8D35\uFF0C\u5C0A\u8363\uFF0C\u5BCC\u6709\uFF0C\u5F3A\u5927\u3002\u4ED6\u5C06\u4E00\u5207\u90FD\u820D\u5F03\u4E86\uFF0C\u6210\u4E3A\u51FA\u4E8E\u5C18\u571F\uFF0C\u6781\u5176\u6709\u9650\uFF0C\u5FAE\u4E0D\u8DB3\u9053\u7684\u4EBA\u3002 \u5E76\u4E14\u4ED6\u5728\u6E3A\u5C0F\u7684\u4EBA\u7C7B\u4E2D\uFF0C\u9009\u62E9\u4E86\u6700\u5351\u5FAE\uFF0C\u8D2B\u7A77\u7684\u6D3B\u6CD5\uFF0C\u548C\u6700\u5C48\u8FB1\uFF0C\u60E8\u70C8\u7684\u6B7B\u6CD5\u3002\u57FA\u7763\u80CC\u8D1F\u7740\u8981\u9489\u6B7B\u81EA\u5DF1\u7684\u5341\u5B57\u67B6\uFF0C\u88AB\u5929\u4E0A\u7684\u79BB\u5F03\uFF0C\u88AB\u5730\u4E0A\u7684\u62D2\u7EDD\u3002</p><p>\u53EA\u6709\u57FA\u7763\u662F\u6700\u5927\u7684\u62E5\u6709\u548C\u5B58\u6709\uFF0C\u4ED6\u624D\u6709\u8D44\u683C\u820D\u5F03\uFF0C\u5E76\u4E14\u4ED6\u4E5F\u505A\u5230\u4E86\u3002</p><p>\u4ED6\u5728\u7528\u6781\u81F4\u7684\u820D\u5DF1\uFF0C\u5EFA\u7ACB\u4E00\u4E2A\u65B0\u7684\u65CF\u7FA4\uFF0C\u4E00\u4E2A\u65B0\u7684\u5BB6\u5EAD\uFF0C\u4E00\u4E2A\u56FD\u5EA6\uFF0C\u5C31\u662F\u4ED6\u7684\u6559\u4F1A\uFF0C\u4ED6\u7684\u65B0\u5987\u3002 \u800C\u4ED6\u4E00\u65E6\u7231\u4E86\uFF0C\u5C31\u7528\u6C38\u8FDC\u7684\u7231\uFF0C\u5305\u5BB9\uFF0C\u906E\u76D6\u53BB\u63A5\u7EB3\u3002\u5E76\u4E14\u628A\u6211\u4EEC\u5E26\u5230\u4ED6\u6C38\u8FDC\u8D50\u798F\u7684\u56FD\u5EA6\u91CC\u3002\u62E5\u6709\u4E00\u5207\u4E30\u76DB\u7684\u57FA\u7763\uFF0C\u505A\u6211\u4EEC\u751F\u547D\u7684\u4E3B\uFF0C\u505A\u6211\u4EEC\u7684\u4F9B\u5E94\u8005\uFF0C\u5E2E\u52A9\u8005\u3002\u6240\u4EE5\u6211\u4EEC\u53EF\u4EE5\u5728\u4FE1\u5FC3\u786E\u4FE1\u6709\u8FD9\u6837\u7684\u770B\u987E\uFF0C\u6709\u795E\u505A\u6211\u4EEC\u7684\u4FDD\u8BC1\u3002\u6211\u4EEC\u5C31\u80FD\u6210\u4E3A\u4E00\u4E2A\u820D\u5DF1\u8005\uFF0C\u56E0\u4E3A\u4E07\u6709\u90FD\u662F\u4ED6\u7684\u3002\u795E\u8981\u901A\u8FC7\u6211\u4EEC\u7684\u820D\u5DF1\uFF0C\u7ED9\u8FD9\u4E2A\u4E0D\u88AB\u795D\u798F\u7684\u4E16\u754C\u6D41\u51FA\u57FA\u7763\u91CC\u7684\u795D\u798F\u3002</p><p>\u4EBA\u672C\u6765\u4E0E\u795E\u9694\u7EDD\uFF0C\u6D3B\u5728\u795E\u7684\u5492\u8BC5\u4E4B\u4E0B\u3002\u795E\u4EFB\u51ED\u7F6A\uFF0C\u9B54\u9B3C\u8F96\u5236\u4EBA\uFF0C\u4EFB\u51ED\u4EBA\u987A\u4ECE\u81EA\u5DF1\u7684\u60C5\u6B32\u751F\u6D3B\uFF0C\u8FFD\u6C42\u81EA\u5DF1\u7684\u6EE1\u8DB3\u548C\u4E16\u754C\u91CC\u7684\u4E49\u3002\u5229\u76CA\uFF0C\u540D\u671B\uFF0C\u9AD8\u4F4D\uFF0C\u5C0A\u8D35\u88AB\u7F6A\u4EBA\u7684\u4EBA\u6027\u666E\u904D\u770B\u91CD\uFF0C\u8FD9\u6210\u4E3A\u4EBA\u5BF9\u81EA\u5DF1\u7684\u5F3A\u70C8\u7684\u8BC9\u6C42\uFF0C\u6C42\u4E4B\u4E0D\u5F97\u7684\u75DB\u82E6\uFF0C\u6709\u8FC7\u5931\u53BB\u7684\u66F4\u75DB\u82E6\u3002\u8FFD\u6C42\u8FD9\u4E9B\u7684\u77E5\u8BC6\u548C\u80FD\u529B\u88AB\u63A8\u5D07\uFF0C\u800C\u83B7\u5F97\u4E86\u8FD9\u4E9B\uFF0C\u5C31\u663E\u4E3A\u6210\u529F\u3002</p><p>\u4ECE\u4EBA\u5929\u7136\u7684\u751F\u547D\u6027\u60C5\uFF0C\u4EE5\u53CA\u4ECE\u4E16\u754C\u5B66\u4E60\u800C\u6765\u7684\u4EF7\u503C\u503E\u5411\uFF0C\u5C31\u662F\u5BF9\u5341\u67B6\u7684\u5426\u5B9A\uFF0C\u62D2\u7EDD\u3002\u56E0\u4E3A\u8FD9\u4EE3\u8868\u7740\u82E6\u96BE\uFF0C\u5351\u5FAE\uFF0C\u5C48\u8FB1\uFF0C\u53EF\u601C\uFF0C\u5F7B\u5E95\u7684\u8EAB\u8D25\u540D\u88C2\u3002 \u4ECE\u4EBA\u7684\u667A\u6167\u548C\u77E5\u8BC6\u65E0\u6CD5\u4ECE\u5341\u5B57\u67B6\u4E0A\u627E\u5230\u503C\u5F97\u4F9D\u9760\uFF0C\u503C\u5F97\u5B66\u4E60\uFF0C\u503C\u5F97\u6548\u6CD5\u7684\u5185\u5BB9\u3002\u66F4\u65E0\u4ECE\u8C08\u8D77\u80CC\u8D77\u5341\u5B57\u67B6\u3002</p><p>\u4F46\u57FA\u7763\u9080\u8BF7\u6211\u4EEC\u4E0E\u4ED6\u5EFA\u7ACB\u5173\u7CFB\u7684\u65B9\u5F0F\u5C31\u662F\u5341\u5B57\u67B6\u3002\u524D\u63D0\u5F53\u7136\u57FA\u7763\u81EA\u5DF1\u5728\u5341\u5B57\u67B6\u4E0A\u7684\u4EE3\u8D4E\uFF0C\u6E05\u7B97\u4E86\u4EBA\u7684\u7F6A\uFF1B\u4E5F\u85C9\u7740\u5723\u7075\u4F7F\u6211\u4EEC\u5F52\u4FE1\uFF0C\u8054\u5408\u4E8E\u57FA\u7763\u5341\u5B57\u67B6\u4E0A\u7684\u6B7B\u548C\u590D\u6D3B\u3002\u4F7F\u6211\u4EEC\u751F\u547D\u7684\u672C\u8D28\u53D1\u751F\u4E86\u6539\u53D8\u3002 \u8FD9\u4ECE\u57FA\u7763\u800C\u6765\u7684\u6027\u60C5\uFF0C\u80FD\u4F53\u8BA4\u57FA\u7763\u6240\u80CC\u8D1F\u7684\u5341\u5B57\u67B6\u3002\u6DF1\u77E5\u5FC5\u8981\uFF0C\u6DF1\u77E5\u6069\u91CD\uFF0C\u4E3A\u4E4B\u611F\u52A8\uFF0C\u4E3A\u4E4B\u6FC0\u52B1\u3002\u5E76\u5728\u5BF9\u5341\u5B57\u67B6\u4E8B\u4EF6\u7684\u8BA4\u8BC6\u4E2D\u4F7F\u6211\u4EEC\u53EF\u4EE5\u9762\u5BF9\u820D\u5DF1\u548C\u80CC\u8D1F\u81EA\u5DF1\u7684\u5341\u5B57\u67B6\u3002</p><p>\u820D\u5DF1\u548C\u5341\u5B57\u67B6\u53C8\u662F\u57FA\u7763\u6027\u60C5\u5E26\u7ED9\u4EBA\u7684\u5185\u5728\u8981\u6C42\u3002\u56E0\u4E3A\u5F15\u5BFC\u6211\u4EEC\u7684\u5723\u7075\uFF0C\u53C8\u85C9\u7740\u771F\u7406\uFF0C\u628A\u6211\u4EEC\u5E26\u5230\u5341\u67B6\u8DEF\u4E0A\u3002\u800C\u8FD9\u663E\u51FA\u4EBA\u7684\u4FE1\u5FC3\uFF0C\u663E\u51FA\u662F\u795E\u7684\u7528\u4EBA\uFF0C\u7528\u4ED6\u4E00\u751F\u6210\u4E3A\u57FA\u7763\u60A3\u96BE\u7684\u6CE8\u89E3\uFF0C\u662F\u57FA\u7763\u5341\u67B6\u4E4B\u7231\u7684\u5EF6\u7EED\u3002\u6211\u4EEC\u5BF9\u4E00\u5207\u7684\u8D2A\u7231\uFF0C\u5728\u9762\u5BF9\u57FA\u7763\u4E4B\u7231\u5438\u5F15\u7684\u65F6\u5019\uFF0C\u90FD\u90A3\u4E48\u7684\u9EEF\u7136\u5931\u8272\uFF0C\u5FAE\u4E0D\u4F4F\u9053\u3002 \u4E3B\u554A\uFF0C\u613F\u4F60\u7684\u7231\u6D47\u704C\u6211\u4EEC\uFF0C\u6D8C\u6D41\u4E0D\u5C3D\uFF0C\u6ED4\u6ED4\u4E0D\u7EDD\u3002</p><p>\u200B</p>",25),r=[o];function s(c,n,i,d,l,f){return t(),_("div",null,r)}var T=p(a,[["render",s]]);export{h as __pageData,T as default};
